INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (256, 2, 'RealFlex RealWin SCADA On_FC_CTAGLIST_FCS_ADDTAGMS 缓冲区溢出', '2004-01-01', 1, 1, 'CVE-2011-1563', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问RealFlex RealWin。', 'DATAC RealFlex RealWin 2.1（Build 6.1.10.10）及之前版本中的HMI应用程序中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向910端口发送的多个包中的超长用户名，执行任意代码。这些包有：（1）On_FC_CONNECT_FCS_LOGIN包，特制（2）On_FC_CTAGLIST_FCS_CADDTAG，（3）On_FC_CTAGLIST_FCS_CDELTAG，（4）On_FC_CTAGLIST_FCS_ADDTAGMS，（5）On_FC_RFUSER_FCS_LOGIN，（6）未明On_FC_BINFILE_FCS_*FILE，（7）On_FC_CGETTAG_FCS_GETTELEMETRY，（8）On_FC_CGETTAG_FCS_GETCHANNELTELEMETRY，（9）On_FC_CGETTAG_FCS_SETTELEMETRY，（10）On_FC_CGETTAG_FCS_SETCHANNELTELEMETRY，以及（11）On_FC_SCRIPT_FCS_STARTPROG包。', 'overflow', 1111641, 'RealFlex_RealWin_SCADA_On_FC_CTAGLIST_FCS_ADDTAGMS_Buffer_Overflow', 0, 'tcp any any -> any 910 (msg:\"ETPRO SCADA RealFlex RealWin SCADA On_FC_CTAGLIST_FCS_ADDTAGMS Buffer Overflow\"; flow:to_server,established; isdataat:768; content:\"|10 23 54 67|\"; offset:0; byte_test:4,>,768,0,relative,little; content:\"|05 00 05 00|\"; distance:6; within:4; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111641; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (512, 2, '检测到针对ABB MicroSCADA的远程代码执行攻击', '2003-01-01', 1, 1, 'CVE_2019_5620', NULL, 2, NULL, NULL, NULL, NULL, NULL, NULL, '远程代码执行', 10000019, '', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 12221 ( msg:\"检测到ABB MicroSCADA wserver.exe远程代码执行攻击\"; flow:to_server,established; content:\"EXECUTE|00|\"; metadata:policy max-detect-ips drop; reference:url,packetstorm.sigterm.no/1311-exploits/abb_wserver_exec.rb.txt; classtype:attempted-user; sid:10000019; rev:5; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1, 2, 'Microsoft SQL Server xp_sprintf 缓冲区溢出攻击尝试', '2005-01-01', 1, 1, 'CVE-2001-0542', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft SQL Server是Microsoft公司开发的Windows平台下的SQL服务器。<br>\nMicrosoft SQL Server存在多个溢出漏洞，可以使攻击者通过构造特殊的SQL请求在服务器上以服务器程序进程的权限执行代码或者使服务器程序崩溃。<br>\n在SQL Server 7.0/2000以及MSDE中提供的RAISERROR语句，发现存在一个缓冲溢出问题。<br>\nRAISERROR提供了返回用户定义错误及将@@error变量设置成用户定义错误号的能力。错误消息可以被动态地建立，或者基于错误号从sysmessages表中检索到。一旦出现了错误，错误就会以一种服务器错误消息的方式返回到客户机。RAISERROR的语法：<br>\nRAISERROR (msg_id | msg_str， severity， state<br>\n`， argument ``，…n``)<br>\n`WITH options`<br>\nMsg_id指明用户定义消息的id，该消息存储在sysmessages系统表中。<br>\nMsg_str用于动态创建消息的消息字符串。这与C语言中的printf非常相似。<br>\nSeverity定义用户赋值的错误消息严重程度。<br>\nState是从1到127的任意整数值，它表示错误的调用状态信息。负数的state值将缺省为1。<br>\nOPTIONS指明错误的定制选项。OPTIONS的有效值如下：<br>\n1) LOG。<br>\n将错误记录到服务器错误日志和NT事件日志中。该选项需要消息带有从19到25的严重程度。而只有系统管理员才能发出这种消息。<br>\n2) NOWAIT。<br>\n将消息立即发送到客户端服务器。<br>\n3) SETERROR。<br>\n不管其严重级别如何，将@@error的值设置为msg_id或5000。<br>\n当传递给RAISERROR一个相当长的参数时会触发一个溢出，可能导致入侵者获得local system权限。', 'overflow', 2100695, 'xp_sprintf_possible_buffer_overflow', 0, 'tcp $EXTERNAL_NET any -> $SQL_SERVERS 139 (msg:\"GPL EXPLOIT xp_sprintf possible buffer overflow\"; flow:to_server,established; content:\"x|00|p|00|_|00|s|00|p|00|r|00|i|00|n|00|t|00|f|00|\"; offset:32; nocase; reference:bugtraq,1204; reference:url,www.microsoft.com/technet/security/bulletin/MS01-060.mspx; classtype:attempted-user; sid:2100695; rev:10; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
