INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1026, 2, 'SpyEye木马传播 - 发现邮件附件名中含USPS的垃圾邮件', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'trojan', 2012388, 'USPS_SPAM_Inbound_possible_spyeye_trojan', 0, 'tcp $EXTERNAL_NET any -> $SMTP_SERVERS 25 (msg:\"ET TROJAN USPS SPAM Inbound possible spyeye trojan\"; flow:established,to_server; content:\"Content-Disposition|3A| attachment|3b|\"; nocase; content:\"filename=|22|USPS_\"; nocase; content:\".zip|22|\"; nocase; reference:url,www.virustotal.com/file-scan/report.html?id=ed1766eb13cc7f41243dd722baab9973560c999c1489763c0704debebe8f4cb1-1298551066; classtype:trojan-activity; sid:2012388; rev:2; metadata:created_at 2011_02_27, updated_at 2011_02_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (259, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) via HTTP', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019236, 'CVE-2014-6271_Attempt_in_HTTP_Version_Number', 0, 'tcp any any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"ET WEB_SERVER Possible CVE-2014-6271 Attempt in HTTP Version Number\"; flow:established,to_server; content:\"|20 28 29 20 7b|\"; fast_pattern:only; pcre:\"/^[^\\s]+\\s+[^\\s]+\\s+\\x28\\x29\\x20\\x7b[^\\r\\n]*?\\r?$/m\"; reference:url,blogs.akamai.com/2014/09/environment-bashing.html; classtype:attempted-admin; sid:2019236; rev:3; metadata:created_at 2014_09_25, updated_at 2019_10_07;)');
