INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (773, 2, 'CitectSCADA ODBC 缓冲区溢出尝试', '2000-01-01', 1, 1, 'CVE-2008-2639', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问CitectSCADA。', 'CitectSCADA是用于在数据采集与监控系统(SCADA)中提供监视和控制功能的软件。CitectSCADA和CitectFacilities中包含有ODBC服务器功能为关系数据库提供远程SQL访问。ODBC服务器组件默认在20222/tcp端口上监听来自网络的客户端请求，TCP上的应用层协议读取4个字节的初始报文指定下一个报文中数据的长度，然后从同一TCP套接字读取该长度的下一个报文，其中前5个字节为固定的头。将网络中的第二个报文读取到缓冲区后，就会将数据拷贝到栈上固定大小的内部缓冲区。由于对所读取的数据缺少正确地长度检查，使用栈上所分配的固定大小目标缓冲区的内存拷贝操作可能会出现溢出，允许未经认证的远程攻击者在有漏洞的系统上执行任意指令。', 'overflow', 2008542, 'CitectSCADA_ODBC_Overflowflow_Attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 20222 (msg:\"ET SCADA CitectSCADA ODBC Overflow Attempt\"; flow:established,to_server; dsize:4; byte_test:4,>,399,0; reference:cve,2008-2639; reference:url,www.digitalbond.com/index.php/2008/09/08/ids-signature-for-citect-vuln/; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; classtype:attempted-user; sid:2008542; rev:8; metadata:created_at 2010_07_30, updated_at 2016_06_07;)');
