INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (12, 2, 'Cogent unicode 缓冲区溢出尝试', '2002-01-01', 1, 1, 'CVE-2011-3493', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Cogent datahub。', 'Cogent DataHub是DataHub长线产品的最新版本，该产品为即时资料解决方案设定效能评定。Cogent DataHub 7.1.1.63及之前版本中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可借助超长的domain，report_domain，register_datahub或slave命令导致拒绝服务（崩溃）并可能执行任意代码。', 'overflow', 20207, 'Cogent_unicode_buffer_overflow_attempt_4', 0, 'http $EXTERNAL_NET any -> $HOME_NET 4502 ( msg:\"PROTOCOL-SCADA Cogent unicode buffer overflow attempt 4\"; flow:to_server,established; content:\"(domain \";nocase; content:\"|22|\";within:3; isdataat:100,relative; content:!\"|22|\";within:100; metadata:policy max-detect-ips drop; reference:cve,2011-3493; classtype:attempted-admin; sid:20207; rev:9; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (268, 2, 'SMB-DS IrotIsRunning andx 尝试', '2007-01-01', 1, 1, 'CVE-2002-1561', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows 2000/XP是微软公司开发的WINDOWS操作系统。  Microsoft Windows 2000/XP的RPC服务存在漏洞，远程攻击者可以利用这个漏洞进行拒绝服务攻击。  漏洞存在于Windows系统的DCE-RPC堆栈实现中，远程攻击者可以连接TCP 135端口，发送畸形数据，可导致关闭RPC服务，关闭RPC服务可以引起系统停止对新的RPC请求进行响应，产生拒绝服务。由于众多服务都依赖于RPC服务， 这可能使系统变得不稳定， 很多正常操作无法进行. 例如， Word中将无法使用拷贝/粘贴功能. 根据系统安装的补丁情况， 可能导致Windows XP系统重新起动.', 'dos', 2103268, 'NETBIOS_SMB-DS_IrotIsRunning_andx_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS IrotIsRunning andx attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.irot; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\x73|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,!&,128,6,relative; content:\"%\"; depth:1; offset:39; byte_jump:2,0,little,relative; content:\"&|00|\"; within:2; distance:29; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; isdataat:4,relative; content:\"|05|\"; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 02|\"; within:2; distance:19; byte_jump:4,8,relative,little,align; byte_test:4,>,1024,0,little; reference:bugtraq,6005; reference:cve,2002-1561; reference:url,www.microsoft.com/technet/security/bulletin/MS03-010.mspx; classtype:protocol-command-decode; sid:2103268; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (524, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) via HTTP', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019292, 'CVE-2014-6271_Attempt_In_HTTP_Headers_Line_Continuation_Evasion_CRLF', 0, 'http any any -> $HTTP_SERVERS any (msg:\"ET WEB_SERVER CVE-2014-6271 Attempt In HTTP Headers Line Continuation Evasion CRLF\"; flow:to_server,established; content:\"|28 29 0d 0a 20 7b|\"; fast_pattern:only; reference:url,www.invisiblethreat.ca/2014/09/cve-2014-6271/; classtype:attempted-admin; sid:2019292; rev:3; metadata:created_at 2014_09_28, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (780, 2, 'SMB Trans2 Query_Fs_Attribute_Info SrvSmbQueryFsInformation 池缓冲区溢出', '2000-01-01', 1, 1, 'CVE-2010-2550', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft 服务器消息块 (SMB) 协议软件处理特制 SMB 数据包的方式中存在一个未经身份验证的远程执行代码漏洞。 利用该漏洞的尝试不要求进行身份验证，从而使攻击者可以通过向运行 Server 服务的计算机发送特制网络消息来利用该漏洞。 成功利用此漏洞的攻击者可以完全控制系统。', 'overflow', 2012094, 'NETBIOS_SMB_Trans2_Query_Fs_Attribute_Info_SrvSmbQueryFsInformation_Pool_Buffer_Overflow', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"ET NETBIOS SMB Trans2 Query_Fs_Attribute_Info SrvSmbQueryFsInformation Pool Buffer Overflow\"; flow:to_server,established; content:\"|ff 53 4d 42 32|\"; offset:4; depth:5; content:\"|00 00 00 00|\"; distance:0; within:4; content:\"|00 00|\"; distance:30; within:2; content:\"|00 03 00|\"; distance:19; within:3; reference:url,www.exploit-db.com/exploits/14607/; reference:url,seclists.org/fulldisclosure/2010/Aug/122; reference:cve,2010-2550; reference:bid,42224; reference:url,www.microsoft.com/technet/security/Bulletin/MS10-054.mspx; classtype:attempted-user; sid:2012094; rev:2; metadata:created_at 2010_12_23, updated_at 2010_12_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1036, 2, 'BlackEnergy 恶意软件 SSL 证书检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'BlackEnergy是一款受欢迎的犯罪软件（即一种使犯罪活动自动化的恶意软件），流通在俄罗斯的地下网络，最早能够追溯到2007年。 刚开始，它被设计为一个在DDos攻击中创建僵尸网络的工具。随着时间的推移，这款恶意软件已经演变为支持各种插件，插件能够基于攻击的意图进行组合以提供必要的功能。鉴于该工具的特点，Blackenergy已被不同团伙用于不同的用途。有些团伙利用它发送垃圾邮件；另一些用来盗取银行凭证。最恶名昭彰的案例是在2008年俄格冲突期间，该工具被用来对格鲁吉亚实施网络攻击。', 'trojan', 2022327, 'BlackEnergy_SSL_Cert', 0, 'tls $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN BlackEnergy SSL Cert\"; flow:from_server,established; content:\"|09 00 e3 6e 25 fe 3f fa 53 80|\"; fast_pattern; content:\"|55 04 06|\"; distance:0; content:\"|02|XX\"; distance:1; within:3; content:\"|55 04 07|\"; distance:0; content:\"|0c|Default City\"; distance:1; within:13; reference:url,welivesecurity.com/2016/01/03/blackenergy-sshbeardoor-details-2015-attacks-ukrainian-news-media-electric-industry/; classtype:trojan-activity; sid:2022327; rev:2; metadata:attack_target Client_Endpoint, deployment Perimeter, tag SSL_Malicious_Cert, signature_severity Major, created_at 2016_01_04, updated_at 2016_07_01;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (13, 2, 'SpyEye 僵尸网络数据窃取行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'botnet', 2013529, 'Spyeye_Data_Exfiltration_8', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Spyeye Data Exfiltration 8\"; flow:established,to_server; content:\"|B4 B4 B4 B4 BC BF BF BF BF BD BD BD BD B3 B3 B3 B3|\"; offset:5; depth:17; classtype:trojan-activity; sid:2013529; rev:3; metadata:created_at 2011_09_01, updated_at 2011_09_01;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (269, 2, 'Iconics Genesis SCADA 未初始化的内存释放', '2004-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Iconics Genesis。', 'ICONICS是一家专门从事可视化和自动化软件开发的公司。GENESIS32GENESIS32是一个基于Web的HMI和SCADA应用，它完全可扩展OPC、SNMP和BACnet。GenBroker是一个监听38080端口的Windows服务。GENESIS32 9.2存在多个释放未初始化内存的漏洞。攻击者可以利用这些漏洞通过发送畸形报文强制释放任意位置的内存，导致拒绝服务。', 'dos', 1111656, 'Iconics_Genesis_SCADA_Freeing_of_Unitialized_Memory_Trigger_Option_1', 0, 'tcp any any -> any 38080 (msg:\"ETPRO SCADA Iconics Genesis SCADA Freeing of Unitialized Memory Trigger Option 1\"; flow:to_server,established; dsize:62;content:\"|b0 04 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 ff 0f 00 00 ff 0f 00 00|\"; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111656; rev:1;)');
