INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1045, 2, 'IGSS SCADA 系统目录遍历和上传覆盖', '2003-01-01', 1, 1, 'CVE-2011-1565', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在目录遍历漏洞。远程攻击者可以借助向TCP端口12401发送的..\\序列，（1）读取（操作码0x3），（2）创建或编写（操作码0x2）任意文件。', 'code_execution', 1111621, 'IGSS_SCADA_system_Directory_Traversal_Upload_and_Overwrite', 0, 'tcp any any -> any 12401 (msg:\"ETPRO SCADA IGSS SCADA system Directory Traversal Upload and Overwrite\"; flow:to_server,established; content:\"|01 00 34 12 0D|\"; offset:2; depth:5; content:\"|02|\"; distance:11; within:1; content:\"|2E 2E 5C 2E 2E 5C 2E 2E 5C 2E 2E 5C|\"; distance:0; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111621; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (278, 2, 'Microsoft Windows远程桌面协议代码执行漏洞', '2001-01-01', 1, 1, 'CVE-2012-0002', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft Windows XP SP2与SP3， Windows Server 2003 SP2， Windows Vista SP2， Windows Server 2008 SP2， R2及R2 SP1，与Windows 7 Gold与SP1版本中的远程桌面协议(RDP)实现中存在漏洞，该漏洞源于没有正确处理内存中的数据包。远程攻击者可通过发送特制RDP数据包触发访问（1）没有正确初始化或者（2）已被删除的对象，执行任意代码。也称&lsquo;Remote Desktop Protocol Vulnerability&rsquo;。  该漏洞已有修复补丁，如果未能及时安装补丁可采取以下临时修复措施：  方法一：禁用终端服务、远程桌面、远程协助和 Windows Small Business Server 2003 远程工作网站功能；  方法二：在企业周边防火墙中屏蔽TCP端口3389；  方法三：在运行 Windows Vista、Windows 7、Windows Server 2008 和 Windows Server 2008 R2 的受支持版本的系统上启用网络级别身份验证。', 'code_execution', 2014431, 'DOS_Microsoft_Remote_Desktop_Protocol_(RDP)_maxChannelIds_DoS_Attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 3389 (msg:\"ET DOS Microsoft Remote Desktop Protocol (RDP) maxChannelIds DoS Attempt\"; flow:to_server,established; content:\"|03 00|\"; depth:2; content:\"|e0|\"; distance:3; within:1; content:\"|03 00|\"; distance:0; content:\"|f0|\"; distance:3; within:1; content:\"|7f 65|\"; distance:1; within:2; content:\"|04 01 01 04 01 01 01 01 ff 30|\"; distance:3; within:10; content:\"|02|\"; distance:1; within:1; byte_test:1,<,0x80,0,relative,big; byte_jump:1,0,relative; byte_test:1,<,0x06,-1,relative,big; reference:url,www.msdn.microsoft.com/en-us/library/cc240836.aspx; reference:cve,2012-0002; reference:url,technet.microsoft.com/en-us/security/bulletin/ms12-020; reference:url,stratsec.blogspot.com.au/2012/03/ms12-020-vulnerability-for-breakfast.html; reference:url,aluigi.org/adv/termdd_1-adv.txt; reference:url,blog.binaryninjas.org/?p=58; reference:url,luca.ntop.org/Teaching/Appunti/asn1.html; classtype:attempted-dos; sid:2014431; rev:15; metadata:created_at 2012_03_20, updated_at 2012_03_20;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (534, 2, 'Windows Media ASF 文件下载- SET', '2000-01-01', 1, 1, 'CVE-2011-0042', NULL, 0, NULL, NULL, NULL, NULL, NULL, 'Windows Media Player，是微软公司出品的一款免费的播放器，是Microsoft Windows的一个组件，通常简称WMP。Windows Media Center多媒体娱乐中心，它是一种运行于Windows Vista操作系统上的多媒体应用程序。  Microsoft Windows Media Player和Windows Media Center在处理特制媒体内容时存在远程代码执行漏洞，远程攻击者可利用这些漏洞控制受影响系统。  1)DirectShow以不安全的方式加载某些库，通过诱使用户打开远程WebDAV或SMB共享上的.wtv、.drv-ms或.mpg文件，利用此漏洞加载任意库。  2)解析Microsoft Digital Video Recording (.dvr-ms)文件时，Stream Buffer Engine (SBE.dll) 中存在错误。', 'misc_activity', 2801466, 'Windows_Media_ASF_File_Download_SET', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT Windows Media ASF File Download SET\"; flow:established,from_server; flowbits:set,8miles.MS_ASF; flowbits:noalert; file_data; content:\"|30 26 B2 75 8E 66 CF 11 A6 D9 00 AA 00 62 CE 6C|\"; reference:cve,2011-0042; reference:url,www.microsoft.com/technet/security/bulletin/MS11-015.mspx; classtype:attempted-user; sid:2801466; rev:5;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1046, 2, 'SandWorm沙虫病毒 INF文件下载尝试', '2005-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Sandworm沙虫病毒几乎影响所有微软Windows vista以上装有Office软件的系统主机操作系统，通俗地说，这类攻击是通过发送附件文档的方式发起，受害者只要打开文档就会自动中招，也就是只要电脑中安装了覆盖面广泛的Office软件，都有可能受到该病毒的影响。<br>TrendMicro报告发现Sandworm发起后续攻击的恶意载荷包含了对工控企业的人机界面软件的攻击（主要是GE Cimplicity HMI），目的是进一步控制HMI，并且放置木马。该攻击利用了GE Cimplicity HMI软件的一个漏洞，打开攻击者恶意构造的.cim 或者.bcl文件允许在用户机器上执行任意代码，以及安装木马文件。上面提到的.cim 或者.bcl文件是Cimplicity的工程文件。', 'worm', 2019395, 'CURRENT_EVENTS_Possible_SandWorm_INF_Download', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET CURRENT_EVENTS Possible SandWorm INF Download\"; flow:to_client,established; file_data; content:\"Software|5c|Microsoft|5c|Windows|5c|CurrentVersion|5c|Run\"; nocase; content:\"7EBEFBC0-3200-11d2-B4C2-00A0C9697D17\"; nocase; content:\"ClassGuid\"; nocase; content:\"DefaultInstall\"; nocase; classtype:attempted-user; sid:2019395; rev:2; metadata:created_at 2014_10_14, updated_at 2014_10_14;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (535, 2, 'Conficker/KernelBot/MS08-067 相关木马探测流量', '2002-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Conficker，也被称作Downup，Downadup或Kido，Conficker蠕虫最早于2008年11月20日被发现的以微软的windows操作系统为攻击目标的计算机蠕虫病毒。迄今已出现了A、B、C、E四个版本，目前全球已有超过1500万台电脑受到感染。Conficker主要利用Windows操作系统MS08-067漏洞来传播，同时也能借助任何有USB接口的硬件设备来感染。', 'worm', 2008737, 'TROJAN_Conficker/KernelBot/MS08-067_related_Trojan_Checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Conficker/KernelBot/MS08-067 related Trojan Checkin\"; flow:established,to_server; content:\"GET\"; http_method; nocase; content:\"/kernel/zz.htm?\"; http_uri; content:\"Ver=\"; http_uri; metadata: former_category MALWARE; reference:url,doc.emergingthreats.net/bin/view/Main/2008737; classtype:trojan-activity; sid:2008737; rev:12; metadata:created_at 2010_07_30, updated_at 2010_07_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (791, 2, 'Microsoft Internet Explorer远程内存破坏漏洞(CVE-2015-0039)(MS15-009)', '2005-04-01', 1, 1, 'CVE-2015-0039', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Internet Explorer是微软公司推出的一款网页浏览器。Microsoft Internet Explorer 10-11版本存在远程内存破坏漏洞，远程攻击者通过构造的网站，利用此漏洞可执行任意代码或造成拒绝服务。', 'code_execution', 2809728, 'Possible_CVE-2015-0039_MS15-009_IE_UAF', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT Possible CVE-2015-0039 MS15-009 IE UAF\"; flow:established,to_client; file_data; content:\"msHyphens\"; nocase; fast_pattern:only; content:\"outlineStyle\"; nocase; content:\"inherit\"; nocase; pcre:\"/\\.style\\[\\s*[\\x22\\x27]\\s*msHyphens\\s*[\\x22\\x27]\\s*\\]\\s*=\\s*[\\x22\\x27]\\s*auto/i\"; pcre:\"/\\.style\\[\\s*[\\x22\\x27]\\s*outlineStyle\\s*[\\x22\\x27]\\s*\\]\\s*=\\s*[\\x22\\x27]\\s*ridge/i\"; pcre:\"/\\.replaceAdjacentText\\(\\s*[\\x22\\x27]\\s*afterBegin\\s*[\\x22\\x27]\\s*,\\s*[\\x22\\x27]\\s*inherit/i\"; classtype:attempted-user; sid:2809728; rev:3;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1047, 2, 'SMB-DS llsrconnect unicode 小端字节序溢出尝试', '2001-01-01', 1, 1, 'CVE-2005-0050', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'License Logging Service是一款设计用于帮助客户管理Microsoft服务产品的证书的工具。Microsoft License Logging Service在处理畸形请求时存在安全漏洞，远程攻击者可以利用这个漏洞以系统权限在系统上执行任意指令。License Logging Service在处理某个带有超长的参数畸形请求时存在缓冲区溢出漏洞，远程攻击者可能利用此漏洞在主机上执行任意指令，从而完全控制系统。', 'overflow', 2103125, 'NETBIOS_SMB-DS_llsrconnect_unicode_little_endian_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS llsrconnect unicode little endian overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.llsrpc; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; within:5; distance:3; byte_test:1,&,128,6,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00 00 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 00|\"; within:2; distance:19; byte_test:4,>,104,0,relative; reference:url,www.microsoft.com/technet/security/bulletin/ms05-010.mspx; classtype:attempted-admin; sid:2103125; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (24, 2, 'Masscan 扫描器扫描探测', '2003-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Masscan 是一个开源的无状态扫描器，支持应用层扫描，作者Robert David Graham号称这是世界上最快的扫描器，比大家耳熟能详的nmap等同类至少快十倍。详细信息见，Github：https://github.com/robertdavidgraham/masscan。', 'scan', 2017616, 'NETWORK_Incoming_Masscan_detected', 0, 'http any any -> $HOME_NET any (msg:\"ET SCAN NETWORK Incoming Masscan detected\"; flow:established,to_server; content:\"masscan/\"; depth:8; http_user_agent; reference:url,blog.erratasec.com/2013/10/that-dlink-bug-masscan.html; reference:url,blog.erratasec.com/2013/09/masscan-entire-internet-in-3-minutes.html; classtype:network-scan; sid:2017616; rev:4; metadata:created_at 2013_10_18, updated_at 2013_10_18;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (536, 2, 'RealFlex RealWin SCADA On_FC_CONNECT_FCS_LOGIN 缓冲区溢出', '2004-01-01', 1, 1, 'CVE-2011-1563', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问RealFlex RealWin。', 'DATAC RealFlex RealWin 2.1（Build 6.1.10.10）及之前版本中的HMI应用程序中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向910端口发送的多个包中的超长用户名，执行任意代码。这些包有：（1）On_FC_CONNECT_FCS_LOGIN包，特制（2）On_FC_CTAGLIST_FCS_CADDTAG，（3）On_FC_CTAGLIST_FCS_CDELTAG，（4）On_FC_CTAGLIST_FCS_ADDTAGMS，（5）On_FC_RFUSER_FCS_LOGIN，（6）未明On_FC_BINFILE_FCS_*FILE，（7）On_FC_CGETTAG_FCS_GETTELEMETRY，（8）On_FC_CGETTAG_FCS_GETCHANNELTELEMETRY，（9）On_FC_CGETTAG_FCS_SETTELEMETRY，（10）On_FC_CGETTAG_FCS_SETCHANNELTELEMETRY，以及（11）On_FC_SCRIPT_FCS_STARTPROG包。', 'overflow', 1111638, 'RealFlex_RealWin_SCADA_On_FC_CONNECT_FCS_LOGIN_Buffer_Overflow', 0, 'tcp any any -> any 910 (msg:\"ETPRO SCADA RealFlex RealWin SCADA On_FC_CONNECT_FCS_LOGIN Buffer Overflow\"; flow:to_server,established; isdataat:768; content:\"|10 23 54 67|\"; offset:0; byte_test:4,>,768,0,relative,little; content:\"|01 00 01 00|\"; distance:6; within:4; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111638; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (792, 2, 'Zeus 僵尸网络 User-Agent检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2016710, 'Zeus_User-Agent(z00sAgent)', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Zeus User-Agent(z00sAgent)\"; flow:to_server,established; content:\"z00sAgent\"; depth:9; http_user_agent; reference:md5,e94fb19f3a38f9b2a775b925e4c0abe3; classtype:trojan-activity; sid:2016710; rev:3; metadata:created_at 2013_04_02, updated_at 2013_04_02;)');
