INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (281, 2, 'SpyEye 僵尸网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'botnet', 2014044, 'SpyEye_Checkin_version_1.3.25_or_later_2', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN SpyEye Checkin version 1.3.25 or later 2\"; flow:established,to_server; content:\"POST\"; nocase; http_method; content:\"data=6Prm67\"; depth:11; http_client_body; metadata: former_category MALWARE; classtype:trojan-activity; sid:2014044; rev:5; metadata:created_at 2011_12_28, updated_at 2011_12_28;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (537, 2, 'Yokogawa CENTUM CS 3000 栈缓冲区溢出尝试', '2000-01-01', 1, 1, 'CVE-2014-0783', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Yokogawa CENTUM CS 3000。', 'Yokogawa CENTUM CS是日本横河电机（Yokogawa）公司的一套大型生产控制系统。该系统主要应用于多领域工厂中。Yokogawa CENTUM CS 3000 R3.09.50及之前版本中的BKHOdeq.exe文件存在基于栈的缓冲区溢出漏洞。远程攻击者可通过发送特制的TCP报文利用该漏洞执行任意代码。', 'overflow', 30562, 'Yokogawa_CENTUM_CS_3000_stack_buffer_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 20171 ( msg:\"PROTOCOL-SCADA Yokogawa CENTUM CS 3000 stack buffer overflow attempt\"; flow:to_server,established; content:\"|64 A1 18 00 00 00 83 C0 08 8B 20 81 C4 30 F8 FF FF|\";fast_pattern;nocase; metadata:policy balanced-ips drop,policy max-detect-ips drop,policy security-ips drop; reference:cve,2014-0783; reference:url,www.yokogawa.com/dcs/security/ysar/YSAR-14-0001E.pdf; classtype:attempted-admin; sid:30562; rev:3; )');
