INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (26, 2, 'Havex病毒 CnC 服务器响应检测', '2007-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Havex是被编写来感染SCADA和工控系统中使用的工业控制软件，这种木马可能有能力禁用水电大坝、使核电站过载、甚至可以做到按一下键盘就能关闭一个国家的电网。<br>Havex被命名为W32/Havex.A，它是一种通用的远程访问木马，近来被用于从事工业间谍活动，主要攻击对象是欧洲的许多使用和开发工业应用程序和机械设备的公司。Havex存在超过88个变种，期通信用的C&C服务器多达146个，并且有1500多个IP地址向C&C服务器通信。Havex利用OPC标准来收集网络和联网设备的信息。<br>', 'worm', 2018243, 'Havex_RAT_CnC_Server_Response', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN Havex RAT CnC Server Response\"; flow:established,from_server; file_data; content:\"|3c 21 2d 2d|havexhavex|2d 2d 3e|\"; metadata: former_category MALWARE; reference:md5,6557d6518c3f6bcb8b1b2de77165c962; classtype:trojan-activity; sid:2018243; rev:2; metadata:created_at 2014_03_11, updated_at 2014_03_11;)');
