INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (544, 2, 'CODESYS 网关服务器栈缓冲区溢出尝试', '2004-01-01', 1, 1, 'CVE-2012-4708', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问CoDeSys Gateway Server。', 'CoDeSys是Windows平台下，独立于硬件的IEC 61131-3开发系统，用于编程和创建控制器应用。3S CODESYS Gateway-Server 2.3.9.27之前版本中存在基于栈的缓冲区溢出漏洞。通过特制的数据包，远程攻击者利用该漏洞执行任意代码。', 'overflow', 26503, '3S_CoDeSys_Gateway_Server_stack_buffer_overflow_attempt_2', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET [1210,1211] ( msg:\"PROTOCOL-SCADA 3S CoDeSys Gateway Server stack buffer overflow attempt\"; flow:to_server,established; content:\"|DD DD|\";depth:2; content:\"|04 00 00 00|\";within:4;distance:16; isdataat:464,relative; content:!\"|00|\";within:464; metadata:policy max-detect-ips drop,policy security-ips drop; reference:cve,2012-4708; classtype:attempted-admin; sid:26503; rev:9; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (800, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) via Qmail', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019293, 'Qmail_CVE-2014-6271_Mail_From_attempt', 0, 'tcp any any -> $HOME_NET [25,587] (msg:\"ET EXPLOIT Possible Qmail CVE-2014-6271 Mail From attempt\"; flow:to_server,established; content:\"|28 29 20 7b|\"; fast_pattern:only; pcre:\"/^mail\\s*?from\\s*?\\x3a\\s*?[^\\r\\n]*?\\x28\\x29\\x20\\x7b/mi\"; reference:url,marc.info/?l=qmail&m=141183309314366&w=2; classtype:attempted-admin; sid:2019293; rev:2; metadata:created_at 2014_09_29, updated_at 2019_10_07;)');
