INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (546, 2, 'Microsoft SharePoint Server 2007 _layouts/help.aspx 跨站脚本漏洞', '2007-01-01', 1, 1, 'CVE-2010-0817', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'SharePoint Server是一个服务器功能集成套件，提供全面的内容管理和企业搜索，加速共享业务流程并简化跨界限信息共享。  Microsoft SharePoint Server 2007 12.0.0.6421的_layouts/help.aspx中存在跨站脚本攻击漏洞。远程攻击者可借助cid0参数注入任意web脚本或HTML。', 'xss', 2011073, 'WEB_SERVER_Microsoft_SharePoint_Server_2007__layouts/help.aspx_Cross_Site_Scripting_Attempt', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"ET WEB_SERVER Microsoft SharePoint Server 2007 _layouts/help.aspx Cross Site Scripting Attempt\"; flow:established,to_server; content:\"/_layouts/help.aspx\"; http_uri; nocase; content:\"cid0=\"; http_uri; nocase; pcre:\"/cid0\\x3d.+(script|alert|onmouse[a-z]+|onkey[a-z]+|onload|onunload|ondragdrop|onblur|onfocus|onclick|ondblclick|onsubmit|onreset|onselect|onchange)/Ui\"; reference:url,www.htbridge.ch/advisory/xss_in_microsoft_sharepoint_server_2007.html; reference:url,tools.cisco.com/security/center/viewAlert.x?alertId=20415; reference:url,www.microsoft.com/technet/security/Bulletin/MS10-039.mspx; reference:url,tools.cisco.com/security/center/viewAlert.x?alertId=20610; reference:cve,2010-0817; reference:url,doc.emergingthreats.net/2011073; classtype:web-application-attack; sid:2011073; rev:6; metadata:affected_product Web_Server_Applications, attack_target Web_Server, deployment Datacenter, tag XSS, tag Cross_Site_Scripting, signature_severity Major, created_at 2010_07_30, updated_at 2019_09_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (802, 2, 'Slammer 蠕虫外联传播尝试', '2002-01-01', 1, 1, 'CVE-2002-0649', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Slammer 是一款DDOS恶意程序, 透过一种全新的传染途径, 采取分布式阻断服务攻击感染服务器, 它利用SQL Server 弱点采取阻断服务攻击1434端口并在内存中感染SQL Server, 通过被感染的SQL Server 再大量的散播阻断服务攻击与感染, 造成SQL Server 无法正常作业或宕机, 使内部网络拥塞。和Code Red 一样, 它只是驻留在被攻击服务器的内存中. 大约在世界范围内造成了五十万台服务器宕机。', 'worm', 2102004, 'WORM_Slammer_Worm_propagation_attempt_OUTBOUND', 0, 'udp $HOME_NET any -> $EXTERNAL_NET 1434 (msg:\"GPL WORM Slammer Worm propagation attempt OUTBOUND\"; content:\"|04|\"; depth:1; content:\"|81 F1 03 01 04 9B 81 F1|\"; content:\"sock\"; content:\"send\"; reference:bugtraq,5310; reference:bugtraq,5311; reference:cve,2002-0649; reference:nessus,11214; reference:url,vil.nai.com/vil/content/v_99992.htm; classtype:misc-attack; sid:2102004; rev:8; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
