INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (37, 2, 'SMB DCERPC Workstation Service 直接服务绑定尝试', '2006-01-01', 1, 1, 'CVE-2003-0812', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft DCE/RPC服务可以提供网络管理功能，这些功能提供管理用户帐户和网络资源管理的功能。部分网络管理功能在Windows目录下的debug子目录会生成调试日志文件。  Microsoft Workstation服务在处理日志记录时缺少充分的边界缓冲区检查，远程攻击者可以利用这个漏洞提供超长参数触发缓冲区溢出，以SYSTEM权限在系统上执行任意指令。  日志功能中使用vsprintf()在日志文件中生成字符串，日志文件名为NetSetup.LOG，其保存在Windows debug目录中。  这个记录函数有部分处理Workstation服务命令的函数调用，如NetValidateName， NetJoinDomain等，在这NetValidateName()中，computer name作为第二个参数最终记录在日志文件中。  如我们使用NetValidateName() API：\nNetValidateName(L\\\\\\\\192.168.0.100，AAAAAAAA，NULL，NULL，0);  那么我们可以在远程主机中产生如下记录条目：\n08/13 13:01:01 NetpValidateName: checking to see if &#039;&#039; is valid as type 0 name\n08/13 13:01:01 NetpValidateName: &#039;&#039; is not a valid NetBIOS \\\\AAAAAAAA name: 0x57\n如果我们指定超长字符串作为NetValidateName() API的第二个参数，如果调试文件可写就可以在特定主机上发生缓冲区溢出。  一般如果是NTFS文件系统，在Windows目录中的debug目录不允许所有人可写，这表示不能使用NULL会话来生成日志。WsImpersonateClient() API在打开日志文件前调用，如果连接客户端没有有效的权限来写日志文件，那么CreateFile()就会失败，vsprintf()就不会被执行，因此此漏洞在FAT32系统和%SYSTEMROOT%\\debug目录可写的情况下可被利用。  但是部分扩展RPC函数实现在Windows XP上在调用WsImpersonateClient()前打开日志文件，不过这些RPC函数没有提供文档化说明，不过可以观察在WKSSVC.DLL中的函数表观察到。这些扩展命令的RPC号开始于0x1B，如0x1B调用NetpManageComputers()，但在打开日志文件前不调用WsImpersonateClient()。  NetpManageComputers()的使用没有被公开化，但是我们可以在LMJoin.h中找到NetAddAlternateComputerName() API的原型定义，这个API从NETAPI32.DLL导出，这个API也一样没有文档化。我们可以执行这个RPC函数(0x1B)使用如下API产生包：\nNetAddAlternateComputerName(L\\\\\\\\192.168.0.200，long_unicode_string，NULL，NULL，0);  我们不需要特殊权限在远程主机上写第二个产生到日志文件中，如定义超长Unicode字符串作为第二个参数(AlternateName)，在第一个参数定义的远程系统就会由于缓冲区溢出而崩溃。Unicode字符串long_unicode_string会在日志记录函数调用前被转换为ASCII字符串。', 'overflow', 2102315, 'NETBIOS_DCERPC_Workstation_Service_direct_service_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 1024: (msg:\"GPL NETBIOS DCERPC Workstation Service direct service bind attempt\"; flow:to_server,established; content:\"|05 00 0B|\"; depth:3; byte_test:1,&,16,1,relative; content:\"|98 D0 FF|k|12 A1 10|6|98|3F|C3 F8|~4Z\"; within:16; distance:29; reference:bugtraq,9011; reference:cve,2003-0812; reference:url,www.microsoft.com/technet/security/bulletin/MS03-049.mspx; classtype:misc-attack; sid:2102315; rev:7; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (293, 2, 'ZeroAccess 僵尸网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'ZeroAccess，也称为max++和Sirefef，它是一款木马型的恶意软件，它感染微软的Windows操作系统。ZeroAccess主要设计向感染的计算机传递载荷，一旦ZeroAccess感染系统就会开始下载各种不同类型的恶意软件。它的最终目的是为了创造收入，通过广告点击欺诈和比特币挖掘。ZeroAccess是2012年的九大僵尸网络之一，其控制的电脑数量已经超过200万台。', 'botnet', 2015723, 'ZeroAccess_Checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN ZeroAccess Checkin\"; flow:established,to_server; content:\"GET\"; http_method; content:\"/counter.img?theme=\"; fast_pattern:only; http_uri; content:\"&digits=\"; http_uri; content:\"&siteId=\"; http_uri; content:\"Opera/9 (Windows NT\"; http_user_agent; metadata: former_category MALWARE; reference:url,sophos.com/en-us/medialibrary/PDFs/technical%20papers/Sophos_ZeroAccess_Botnet.pdf; reference:url,malwaremustdie.blogspot.co.uk/2013/02/blackhole-of-closest-version-with.html; classtype:trojan-activity; sid:2015723; rev:4; metadata:created_at 2012_09_20, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (805, 2, 'Iconics Genesis SCADA 整数溢出', '2004-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Iconics Genesis。', 'ICONICS是一家专门从事可视化和自动化软件开发的公司。GENESIS32GENESIS32是一个基于Web的HMI和SCADA应用，它完全可扩展OPC、SNMP和BACnet。GenBroker是一个监听38080端口的Windows服务。GENESIS32 9.2在处理opccode 0x1c84型报文时存在整数溢出漏洞，成功的利用该漏洞可导致恶意代码执行。', 'overflow', 1111671, 'Iconics_Genesis_SCADA_Integer_Overflow_0x1c84', 0, 'tcp any any -> any 38080 (msg:\"ETPRO SCADA Iconics Genesis SCADA Integer Overflow 0x1c84\"; flow:to_server,established; content:\"|01 00 00 15 00 00 00 01 00 00 1F F4 01 00 00 00|\"; content:\"|84 1c|\"; distance:4; within:2; isdataat:1024; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111671; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (294, 2, 'Windows 版本 MySQL 服务器远程系统级别 exploit 攻击 (Stuxnet 的技术)', '2006-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, '本签名检测针对Windows版MySQL的远程exploit攻击，该攻击利用了Stuxnet震网的技术。<br>Stuxnet蠕虫病毒（超级工厂病毒）是世界上首个专门针对工业控制系统编写的破坏性病毒，能够利用对windows系统和西门子SIMATIC WinCC系统的7个漏洞进行攻击。特别是针对西门子公司的SIMATIC WinCC监控与数据采集 (SCADA) 系统进行攻击，由于该系统在我国的多个重要行业应用广泛，被用来进行钢铁、电力 、能源、化工等重要行业的人机交互与监控。该病毒主要通过U盘和局域网进行传播。', 'worm', 2015995, 'MySQL_Server_for_Windows_Remote_SYSTEM_Level_Exploit_(Stuxnet_Techique_DUMP_INTO_executable)', 0, 'tcp $EXTERNAL_NET any -> $SQL_SERVERS 3306 (msg:\"ET EXPLOIT MySQL Server for Windows Remote SYSTEM Level Exploit (Stuxnet Techique DUMP INTO executable)\"; flow:to_server,established; content:\"|03|\"; offset:3; depth:4; content:\"SELECT data FROM\"; nocase; distance:0; content:\"INTO DUMPFILE\"; nocase; distance:0; content:\"c|3a|/windows/system32/\"; nocase; fast_pattern; content:\".exe\"; nocase; distance:0; pcre:\"/SELECT data FROM [^\\x20]+?\\x20INTO DUMPFILE [\\x27\\x22]c\\x3a\\/windows\\/system32\\/[a-z0-9_-]+?\\.exe[\\x27\\x22]/i\"; reference:url,seclists.org/fulldisclosure/2012/Dec/att-13/; classtype:attempted-user; sid:2015995; rev:4; metadata:created_at 2012_12_05, updated_at 2012_12_05;)');
