INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (300, 2, 'RealFlex RealWin SCADA On_FC_RFUSER_FCS_LOGIN 缓冲区溢出', '2004-01-01', 1, 1, 'CVE-2011-1563', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问RealFlex RealWin。', 'DATAC RealFlex RealWin 2.1（Build 6.1.10.10）及之前版本中的HMI应用程序中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向910端口发送的多个包中的超长用户名，执行任意代码。这些包有：（1）On_FC_CONNECT_FCS_LOGIN包，特制（2）On_FC_CTAGLIST_FCS_CADDTAG，（3）On_FC_CTAGLIST_FCS_CDELTAG，（4）On_FC_CTAGLIST_FCS_ADDTAGMS，（5）On_FC_RFUSER_FCS_LOGIN，（6）未明On_FC_BINFILE_FCS_*FILE，（7）On_FC_CGETTAG_FCS_GETTELEMETRY，（8）On_FC_CGETTAG_FCS_GETCHANNELTELEMETRY，（9）On_FC_CGETTAG_FCS_SETTELEMETRY，（10）On_FC_CGETTAG_FCS_SETCHANNELTELEMETRY，以及（11）On_FC_SCRIPT_FCS_STARTPROG包。', 'overflow', 1111642, 'RealFlex_RealWin_SCADA_On_FC_RFUSER_FCS_LOGIN_Buffer_Overflow', 0, 'tcp any any -> any 910 (msg:\"ETPRO SCADA RealFlex RealWin SCADA On_FC_RFUSER_FCS_LOGIN Buffer Overflow\"; flow:to_server,established; isdataat:59; content:\"|10 23 54 67|\"; offset:0; byte_test:4,>,44,0,relative,little; content:\"|11 00 01 00|\"; distance:6; within:4; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111642; rev:1;)');
