INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (301, 2, 'SMB Session Setup NTMLSSP unicode asn1 溢出尝试', '2006-01-01', 1, 1, 'CVE-2003-0818', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Internet Explorer是一款流行的WEB浏览程序。  Microsoft Internet Explorer存在多个安全问题，可导致任意代码执行，读取本地系统文件，下载任意文件到用户系统。  具体问题如下：  - 在相关Internet Explorer的跨域安全模型(从共享信息中保持不同域窗口)中存在三个漏洞，这些漏洞可导致在本地计算机区域中执行任意脚本，要利用这些漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。成功利用此漏洞可以从其他WEB站点访问信息，访问用户系统上的文件和在用户上执行任意代码。  - 在Internet Explorer中把区域信息传递给XML对象时存在安全问题。这个漏洞允许攻击者读取用户系统上的本地问。要利用这个漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。在用户浏览恶意站点或查看HTML EMAIL消息后，用户会被提示下载HTML页面，如果用户接收这个HTML文件下载，那么攻击者可以读取已知位置中的本地文件。  - 在Internet Explorer中在动态HTML事件上执行Drag-and-Drop操作时存在安全问题。如果用户点击，这个漏洞允许文件保存在用户系统中的目标指定目录上。没有任何对话框提示会要求用户下载。要利用这个漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。如果用户点击恶意连接，可导致代码保存在用户计算机上。', 'overflow', 2103000, 'NETBIOS_SMB_Session_Setup_NTMLSSP_unicode_asn1_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB Session Setup NTMLSSP unicode asn1 overflow attempt\"; flow:established,to_server; content:\"|00|\"; depth:1; content:\"|FF|SMBs\"; within:5; distance:3; byte_test:1,&,128,6,relative; byte_test:4,&,2147483648,48,relative,little; content:!\"NTLMSSP\"; within:7; distance:54; asn1:double_overflow, bitstring_overflow, relative_offset 54, oversize_length 2048; reference:bugtraq,9633; reference:bugtraq,9635; reference:cve,2003-0818; reference:nessus,12052; reference:nessus,12065; reference:url,www.microsoft.com/technet/security/bulletin/MS04-007.mspx; classtype:protocol-command-decode; sid:2103000; rev:8; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (557, 2, 'OSX/Flashback.K/I User-Agent检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Flashback僵尸网络在2012年感染了近60万台Mac电脑。该木马通过利用Java的漏洞，进入Mac系统，所以安装有Java系统的Mac用户一旦访问了带有Flashback木马的网页就会被感染。Flashback僵尸网络目前的关注点是收集谷歌和Paypal等网站的账户密码，以便网络罪犯可以接管这些账户。<br>', 'botnet', 2014534, 'OSX/Flashback.K/I_User-Agent', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN OSX/Flashback.K/I User-Agent\"; flow:established,to_server; content:\" WOW64|3b| rv|3a|9.0.1|3b| sv|3a|\"; http_header; content:\" id|3a|\"; http_header; within:6; reference:url,f-secure.com/v-descs/trojan-downloader_osx_flashback_k.shtml; reference:url,vms.drweb.com/virus/?i=1816029; reference:url,f-secure.com/v-descs/trojan-downloader_osx_flashback_i.shtml; classtype:trojan-activity; sid:2014534; rev:4; metadata:created_at 2012_04_05, updated_at 2012_04_05;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (302, 2, 'SMB-DS CoGetInstanceFromFile 溢出尝试', '2001-01-01', 1, 1, 'CVE-2003-0995', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Message Queue Manager (MSQM)存在缓冲区溢出漏洞。远程攻击者可以通过队列登记要求来导致服务拒绝（RPC服务崩溃）。', 'overflow', 2103184, 'NETBIOS_SMB-DS_CoGetInstanceFromFile_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS CoGetInstanceFromFile overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.msqueue; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; within:5; distance:3; byte_test:1,!&,128,6,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 01|\"; within:2; distance:19; byte_test:4,>,128,20,relative; reference:cve,2003-0995; reference:url,www.eeye.com/html/Research/Advisories/AD20030910.html; reference:url,www.microsoft.com/technet/security/bulletin/MS03-026.mspx; classtype:attempted-admin; sid:2103184; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (558, 2, 'CODESYS 网关服务器堆缓冲区溢出尝试', '2004-01-01', 1, 1, 'CVE-2012-4706', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问CODESYS Gateway-Server。', 'CoDeSys是Windows平台下，独立于硬件的IEC 61131-3开发系统，用于编程和创建控制器应用。3S CODESYS Gateway-Server 2.3.9.27之前版本中存在整数无符号漏洞。通过特制的数据包触发基于堆的缓冲区溢出，远程攻击者利用该漏洞导致拒绝服务。', 'overflow', 29954, 'CODESYS_Gateway-Server_heap_buffer_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET [1210,1211] ( msg:\"PROTOCOL-SCADA CODESYS Gateway-Server heap buffer overflow attempt\"; flow:to_server,established; content:\"|DD DD|\";depth:2; content:\"|06 00 00 00|\";within:4;distance:16; byte_test:4,>,0x7fffffff,260,relative,little; metadata:policy max-detect-ips drop; reference:cve,2012-4706; classtype:attempted-admin; sid:29954; rev:3; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (814, 2, 'SMB DCERPC Workstation 服务绑定尝试', '2006-01-01', 1, 1, 'CVE-2003-0812', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft DCE/RPC服务可以提供网络管理功能，这些功能提供管理用户帐户和网络资源管理的功能。部分网络管理功能在Windows目录下的debug子目录会生成调试日志文件。  Microsoft Workstation服务在处理日志记录时缺少充分的边界缓冲区检查，远程攻击者可以利用这个漏洞提供超长参数触发缓冲区溢出，以SYSTEM权限在系统上执行任意指令。  日志功能中使用vsprintf()在日志文件中生成字符串，日志文件名为NetSetup.LOG，其保存在Windows debug目录中。  这个记录函数有部分处理Workstation服务命令的函数调用，如NetValidateName， NetJoinDomain等，在这NetValidateName()中，computer name作为第二个参数最终记录在日志文件中。  如我们使用NetValidateName() API：\nNetValidateName(L\\\\\\\\192.168.0.100，AAAAAAAA，NULL，NULL，0);  那么我们可以在远程主机中产生如下记录条目：\n08/13 13:01:01 NetpValidateName: checking to see if &#039;&#039; is valid as type 0 name\n08/13 13:01:01 NetpValidateName: &#039;&#039; is not a valid NetBIOS \\\\AAAAAAAA name: 0x57\n如果我们指定超长字符串作为NetValidateName() API的第二个参数，如果调试文件可写就可以在特定主机上发生缓冲区溢出。  一般如果是NTFS文件系统，在Windows目录中的debug目录不允许所有人可写，这表示不能使用NULL会话来生成日志。WsImpersonateClient() API在打开日志文件前调用，如果连接客户端没有有效的权限来写日志文件，那么CreateFile()就会失败，vsprintf()就不会被执行，因此此漏洞在FAT32系统和%SYSTEMROOT%\\debug目录可写的情况下可被利用。  但是部分扩展RPC函数实现在Windows XP上在调用WsImpersonateClient()前打开日志文件，不过这些RPC函数没有提供文档化说明，不过可以观察在WKSSVC.DLL中的函数表观察到。这些扩展命令的RPC号开始于0x1B，如0x1B调用NetpManageComputers()，但在打开日志文件前不调用WsImpersonateClient()。  NetpManageComputers()的使用没有被公开化，但是我们可以在LMJoin.h中找到NetAddAlternateComputerName() API的原型定义，这个API从NETAPI32.DLL导出，这个API也一样没有文档化。我们可以执行这个RPC函数(0x1B)使用如下API产生包：\nNetAddAlternateComputerName(L\\\\\\\\192.168.0.200，long_unicode_string，NULL，NULL，0);  我们不需要特殊权限在远程主机上写第二个产生到日志文件中，如定义超长Unicode字符串作为第二个参数(AlternateName)，在第一个参数定义的远程系统就会由于缓冲区溢出而崩溃。Unicode字符串long_unicode_string会在日志记录函数调用前被转换为ASCII字符串。', 'overflow', 2102309, 'NETBIOS_SMB_DCERPC_Workstation_Service_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB DCERPC Workstation Service bind attempt\"; flow:to_server,established; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; depth:5; offset:4; nocase; byte_test:2,^,1,5,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C|PIPE|5C 00 05 00 0B|\"; within:10; distance:4; byte_test:1,&,16,1,relative; content:\"|98 D0 FF|k|12 A1 10|6|98|3F|C3 F8|~4Z\"; within:16; distance:29; reference:bugtraq,9011; reference:cve,2003-0812; reference:url,www.microsoft.com/technet/security/bulletin/MS03-049.mspx; classtype:misc-attack; sid:2102309; rev:7; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
