INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (52, 2, 'Win32.Socks.s 蠕虫 HTTP Post 连接尝试', '2005-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Worm.Win32.Socks.s是一种恶意的木马程序，它利用大量的不合法的代码来破坏目标系统。该木马允许各种危险的病毒、间谍软件或广告软件进入系统中。在大多数情况下，这种危险的木马通过移动硬盘、假电子邮件附件、恶意下载等方式来感染系统。', 'worm', 2008020, 'WORM_Win32.Socks.s_HTTP_Post_Checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET WORM Win32.Socks.s HTTP Post Checkin\"; flow:established,to_server; content:\"POST\"; http_method; content:\".php\"; http_uri; content:\"proc=[System Process]|0a|\"; http_client_body; depth:22; reference:url,doc.emergingthreats.net/2008020; classtype:trojan-activity; sid:2008020; rev:5; metadata:created_at 2010_07_30, updated_at 2010_07_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (308, 2, 'Alureon/TDSS 网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Alureon（也称为TDSS）是一个木马和bootkit，它被用来设计窃取数据，通过截获被感染电脑的网络流量获取如下数据，比如银行的用户名、密码、信用卡资料和社会安全号码等。Alureon是2012年的九大僵尸网络之一。<br>', 'botnet', 2019717, 'Alureon_Checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Alureon Checkin\"; flow:established,to_server; content:\"POST\"; http_method; content:\"winver=\"; depth:7; http_client_body; content:\"&ver=\"; distance:0; http_client_body; content:!\"User-Agent|3a|\"; http_header; content:!\"Referer|3a|\"; http_header; pcre:\"/^winver=\\d+&ver=\\d+$/P\";  metadata: former_category MALWARE; reference:md5,2155b7942ddc6d7a82e7d96a8c594501; classtype:trojan-activity; sid:2019717; rev:2; metadata:created_at 2014_11_17, updated_at 2014_11_17;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (564, 2, 'Microsoft Windows Media Encoder 9 msxml.dll 不安全的库加载尝试', '2000-01-01', 1, 1, 'CVE-2010-3965', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows Media Encoder是Microsoft开发的免费的可下载媒体编码器，用于内容开发者转换或捕获实时的和记录的音频、视频和屏幕截图为Windows Media格式以供实时和按需交付。  Microsoft Windows Media Encoder打开处理特定文件时存在加载不可信DLL的漏洞，远程或本地攻击者可能利用此漏洞控制用户系统。  此漏洞源于Media Encoder在打开.prx文件时加载某个DLL程序库，可能导致在打开文件时候加载当前目录下攻击者指定的恶意DLL。', 'code_execution', 2801502, 'Microsoft_Windows_Media_Encoder_PRX_File_msxml.dll_Insecure_Library_Loading', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET $HTTP_PORTS (msg:\"WEB_CLIENT Microsoft Windows Media Encoder PRX File msxml.dll Insecure Library Loading\"; flow:to_server,established; content:\"/msxml.dll\"; http_uri; reference:cve,2010-3965; reference:url,www.microsoft.com/technet/security/Bulletin/MS10-094.mspx; classtype:attempted-user; sid:2801502; rev:3;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (820, 2, 'CONTROL MICROSYSTEMS (事件 10) 锁定PLC尝试', '2005-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Control Microsystems现在归属于施耐德电气公司。本事件报警Control Microsystems中的加锁PLC的行为。', 'misc_activity', 2801009, 'CONTROL_MICROSYSTEMS_(Event_10)_Lock_PLC_Attempt_3', 0, 'tcp any any -> $HOME_NET 20000 (msg:\"SCADA CONTROL MICROSYSTEMS (Event 10) Lock PLC Attempt 3\"; dsize:45; flow:established; content:\"|01460a1401|\"; offset:28; depth:5; classtype:misc-activity; sid:2801009; rev:1;)');
