INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (309, 2, 'ZeroAccess 僵尸网络命令控制行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'ZeroAccess，也称为max++和Sirefef，它是一款木马型的恶意软件，它感染微软的Windows操作系统。ZeroAccess主要设计向感染的计算机传递载荷，一旦ZeroAccess感染系统就会开始下载各种不同类型的恶意软件。它的最终目的是为了创造收入，通过广告点击欺诈和比特币挖掘。ZeroAccess是2012年的九大僵尸网络之一，其控制的电脑数量已经超过200万台。', 'botnet', 2013685, 'ZeroAccess/Max++_Rootkit_C&C_Activity_1', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN ZeroAccess/Max++ Rootkit C&C Activity 1\"; flow:established,to_server; content:\".php?w=\"; http_uri; content:\"&i=\"; http_uri; distance:0; content:\"&a=\"; http_uri; distance:0; pcre:\"/\\.php\\?w=\\d+&i=[0-9a-f]{32}&a=\\d+$/U\"; metadata: former_category MALWARE; reference:url,resources.infosecinstitute.com/step-by-step-tutorial-on-reverse-engineering-malware-the-zeroaccessmaxsmiscer-crimeware-rootkit/; reference:url,www.symantec.com/security_response/writeup.jsp?docid=2011-071314-0410-99&tabid=2; reference:url,www.microsoft.com/security/portal/Threat/Encyclopedia/Entry.aspx?Name=TrojanDropper%3aWin32%2fSirefef.B; classtype:trojan-activity; sid:2013685; rev:3; metadata:created_at 2011_09_21, updated_at 2011_09_21;)');
