INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (55, 2, 'Zeus 僵尸网络请求命令控制服务器', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。<br>', 'botnet', 2014107, 'Zeus_POST_Request_to_CnC_-_cookie_variation', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Zeus POST Request to CnC - cookie variation\"; flow:established,to_server; content:\"POST\"; nocase; http_method; content:\"|20|HTTP/1.\"; content:\"|0d 0a|Accept|3a 20|*/*|0d 0a|Accept-Language|3a 20|en-us|0d 0a|Cookie|3a 20|cid=\"; distance:1; within:51; content:\"User-Agent|3a 20|Mozilla\"; distance:0; content:\"Host|3a 20|\"; distance:0; content:\"Content-Length|3a 20|\"; distance:0; content:!\"0\"; within:1; content:\"Connection|3a| Keep-Alive|0d 0a|\"; distance:0; content:\"|3a| no-cache|0d 0a 0d 0a|\"; distance:0; metadata: former_category MALWARE; reference:url,zeustracker.abuse.ch/monitor.php?search=209.59.216.103; classtype:trojan-activity; sid:2014107; rev:3; metadata:created_at 2012_01_09, updated_at 2012_01_09;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (311, 2, 'Microsoft Address Book msoeres32.dll 不安全的库加载尝试', '2004-01-01', 1, 1, 'CVE-2010-3147', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft Windows XP SP2和SP3，Windows Server 2003 SP2，Windows Vista SP1和SP2，Windows Server 2008 Gold，SP2和R2，以及Windows 7中的Windows Address Book中的wab.exe 6.00.2900.5512中存在不可信搜索路径漏洞。本地用户和远程攻击者可以借助与.wab或.p7c文件在同一文件夹下的wab32res.dll木马执行任意代码和DLL劫持攻击。', 'code_execution', 2801478, 'Microsoft_Address_Book_Insecure_msoeres32.dll_Library_Loading', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET $HTTP_PORTS (msg:\"WEB_CLIENT Microsoft Address Book Insecure msoeres32.dll Library Loading\"; flow:to_server,established; content:\"/msoeres32.dll\"; http_uri; reference:cve,2010-3147; reference:url,www.microsoft.com/technet/security/Bulletin/MS10-096.mspx; classtype:attempted-user; sid:2801478; rev:3;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (567, 2, 'Alureon/TDSS Base64编码后的命令检测', '2008-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Alureon（也称为TDSS）是一个木马和bootkit，它被用来设计窃取数据，通过截获被感染电脑的网络流量获取如下数据，比如银行的用户名、密码、信用卡资料和社会安全号码等。Alureon是2012年的九大僵尸网络之一。', 'botnet', 2012921, 'TDSS_Base64_Encoded_Command_1', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET DELETED Possible TDSS Base64 Encoded Command 1\"; flow:established,to_server; content:\"Q21kRXhl\"; http_uri; classtype:trojan-activity; sid:2012921; rev:3; metadata:created_at 2011_06_02, updated_at 2011_06_02;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (56, 2, 'SpyEye 僵尸网络数据窃取行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'botnet', 2013522, 'Spyeye_Data_Exfiltration_1', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Spyeye Data Exfiltration 1\"; flow:established,to_server; content:\"|40 40 40 40 48 4B 4B 4B 4B 49 49 49 49 47 47 47 47|\"; offset:5; depth:17; classtype:trojan-activity; sid:2013522; rev:4; metadata:created_at 2011_09_01, updated_at 2011_09_01;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (312, 2, 'IGSS SCADA 任意的文件读和覆盖尝试', '2003-01-01', 1, 1, 'CVE-2011-1565', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在目录遍历漏洞。远程攻击者可以借助向TCP端口12401发送的..\\序列，（1）读取（操作码0x3），（2）创建或编写（操作码0x2）任意文件。', 'code_execution', 2802904, '7T_Interactive_Graphical_SCADA_System_Arbitrary_File_Read_And_Overwrite', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 12401 (msg:\"SCADA 7T Interactive Graphical SCADA System Arbitrary File Read And Overwrite\"; flow:established,to_server; content:\"|01 00|\"; offset:2; depth:2; content:\"|0d 00 00 00|\"; offset:6; depth:4; byte_test:4,<,0x00000004,18,little; byte_test:4,>,0x00000001,18,little; content:\"..\"; distance:0; reference:cve,CVE-2011-1565; reference:url,www.igss.com/company/news-and-press-center/11-03-25/IGSS_%E2%80%93_ongoing_focus_on_security.aspx; classtype:attempted-admin; sid:2802904; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (568, 2, 'Zeus P2P网络 dropper User-Agent检测', '2009-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2017726, 'Downloader_(P2P_Zeus_dropper_UA)', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET $HTTP_PORTS (msg:\"ET TROJAN Downloader (P2P Zeus dropper UA)\"; flow:to_server,established; content:\"User-Agent|3a 20|Updates downloader\"; classtype:trojan-activity; sid:2017726; rev:4; metadata:created_at 2013_11_15, updated_at 2013_11_15;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (825, 2, 'Sinapsi eSolar光伏系统管理器SQL注入攻击尝试', '2004-01-01', 1, 1, 'CVE-2012-5861', NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Sinapsi eSolar Light Photovoltaic System Monitor。', 'Sinapsi eSolar Light是太阳能应用内使用的监控系统。Sinapsi eSolar Light Photovoltaic System Monitor (又名Schneider Electric Ezylog photovoltaic SCADA管理服务器)，Sinapsi eSolar，Sinapsi eSolar DUO 固件2.0.2870_2.2.12之前版本中存在多个SQL注入漏洞。远程攻击者利用该漏洞通过(1)primo操作中的‘inverterselect’参数传送到dettagliinverter.php脚本或(2)‘lingua’参数传送到changelanguagesession.php脚本，执行任意SQL命令。', 'sql_injection', 57512, 'Sinapsi_eSolar_Light_Photovoltaic_System_Monitor_SQL_Injection_2', 0, 'http $EXTERNAL_NET any -> $HOME_NET $HTTP_PORTS ( msg:\"SERVER-WEBAPP Sinapsi eSolar Light Photovoltaic System Monitor SQL injection attempt 2\"; flow:to_server,established; content:\"/scripts/dettagliinverter.php\";http_uri; fast_pattern;nocase; content:\"inverterselect=\";http_client_body; nocase; pcre:\"/(^|&)inverterselect=[^&]*?([\\x27\\x22\\x3b\\x23\\x28]|%(25)?(27|22|3b|23|28)|(\\x2f|%(25)?2f)(\\x2a|%(25)?2a)|(\\x2d|%(25)?2d){2})/im\"; metadata:policy max-detect-ips drop,policy security-ips drop; reference:cve,2012-5861; classtype:web-application-attack; sid:57512; rev:1; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (58, 2, 'SandWorm沙虫病毒 INF文件下载尝试 (UNICODE)', '2005-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Sandworm沙虫病毒几乎影响所有微软Windows vista以上装有Office软件的系统主机操作系统，通俗地说，这类攻击是通过发送附件文档的方式发起，受害者只要打开文档就会自动中招，也就是只要电脑中安装了覆盖面广泛的Office软件，都有可能受到该病毒的影响。<br>TrendMicro报告发现Sandworm发起后续攻击的恶意载荷包含了对工控企业的人机界面软件的攻击（主要是GE Cimplicity HMI），目的是进一步控制HMI，并且放置木马。该攻击利用了GE Cimplicity HMI软件的一个漏洞，打开攻击者恶意构造的.cim 或者.bcl文件允许在用户机器上执行任意代码，以及安装木马文件。上面提到的.cim 或者.bcl文件是Cimplicity的工程文件。', 'worm', 2019397, 'CURRENT_EVENTS_Possible_SandWorm_INF_Download_(UNICODE)', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET CURRENT_EVENTS Possible SandWorm INF Download (UNICODE)\"; flow:to_client,established; file_data; content:\"S|00|o|00|f|00|t|00|w|00|a|00|r|00|e|00 5c 00|M|00|i|00|c|00|r|00|o|00|s|00|o|00|f|00|t|00 5c 00|W|00|i|00|n|00|d|00|o|00|w|00|s|00 5c 00|C|00|u|00|r|00|r|00|e|00|n|00|t|00|V|00|e|00|r|00|s|00|i|00|o|00|n|00 5c 00|R|00|u|00|n|00|\"; nocase; content:\"7|00|E|00|B|00|E|00|F|00|B|00|C|00|0|00 2d 00|3|00|2|00|0|00|0|00 2d 00|1|00|1|00|d|00|2|00 2d 00|B|00|4|00|C|00|2|00 2d 00|0|00|0|00|A|00|0|00|C|00|9|00|6|00|9|00|7|00|D|00|1|00|7\"; nocase; content:\"C|00|l|00|a|00|s|00|s|00|G|00|u|00|i|00|d|00|\"; nocase; content:\"D|00|e|00|f|00|a|00|u|00|l|00|t|00|I|00|n|00|s|00|t|00|a|00|l|00|l|00|\"; nocase; classtype:attempted-user; sid:2019397; rev:2; metadata:created_at 2014_10_14, updated_at 2014_10_14;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (314, 2, 'Zeus 僵尸网络请求命令控制服务器', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。<br>', 'botnet', 2014218, 'Zeus_POST_Request_to_CnC_sk1_and_bn1_post_parameters', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Zeus POST Request to CnC sk1 and bn1 post parameters\"; flow:established,to_server; content:\"POST\"; nocase; http_method; content:\"bn1=\"; depth:4; http_client_body; fast_pattern; content:\"&sk1=\"; http_client_body; pcre:\"/^[A-F0-9]{30}/PR\"; metadata: former_category MALWARE; classtype:trojan-activity; sid:2014218; rev:6; metadata:created_at 2012_02_10, updated_at 2012_02_10;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (826, 2, '西门子Tecnomatix FactoryLink vrn.exe opcode 8 任意的文件下载', '2003-01-01', 1, 1, NULL, NULL, 0, NULL, NULL, NULL, NULL, '攻击者必须访问Siemens FactoryLink vrn.exe。', '西门子 Tecnomatix FactoryLink 8.0.1.1473版本存在多个漏洞，包括缓冲区溢出、内存破坏和信息泄露漏洞。vrn.exe是FactoryLink中运行的一个服务器软件，它监听7579端口。攻击者可以利用opcode 8及目录遍历漏洞下周任意文件，导致信息泄露。', 'info_leak', 2801912, 'Siemens_Tecnomatix_FactoryLink_vrn.exe_opcode_8_arbitrary_file_download', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 7579 (msg:\"SCADA Siemens Tecnomatix FactoryLink vrn.exe opcode 8 arbitrary file download 2\"; flow:to_server; content:\"|ff 55 08 00|\"; offset:4; depth:4; content:\"|01 00|\"; distance:8; within:2; content:\"|01 00 01 00|\"; distance:16; within:4; pcre:\"/^..([A-Z]\\x3a[\\x5c\\x2f]|\\x2e\\x2e[\\x5c\\x2f]|[\\x5c\\x2f]{2}(\\x3f[\\x5c\\x2f])?([A-Z]\\x3a[\\x5c\\x2f])?)/Rsi\"; reference:bugtraq,46934; classtype:attempted-recon; sid:2801912; rev:2;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (59, 2, 'HTTP SQL注入攻击尝试', '2005-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本事件检测到针对WEB服务器的SQL注入攻击，攻击者通过发送含有“DELETE FROM”的报文来尝试SQL注入。<br>SQL注入攻击指的是通过构建特殊的输入作为参数传入Web应用程序，而这些输入大都是SQL语法里的一些组合，通过执行SQL语句进而执行攻击者所要的操作，其主要原因是程序没有细致地过滤用户输入的数据，致使非法数据侵入系统。', 'sql_injection', 2006443, 'WEB_SERVER_Possible_SQL_Injection_Attempt_DELETE_FROM', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"ET WEB_SERVER Possible SQL Injection Attempt DELETE FROM\"; flow:established,to_server; content:\"DELETE\"; http_uri; nocase; content:\"FROM\"; http_uri; nocase; pcre:\"/DELETE.+FROM/Ui\"; reference:url,en.wikipedia.org/wiki/SQL_injection; reference:url,doc.emergingthreats.net/2006443; classtype:web-application-attack; sid:2006443; rev:11; metadata:affected_product Web_Server_Applications, attack_target Web_Server, deployment Datacenter, tag SQL_Injection, signature_severity Major, created_at 2010_07_30, updated_at 2019_09_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (315, 2, 'Microsoft Internet Explorer CVE-2015-0042 CGeneratedSvgTreeNode 内存释放后重利用漏洞', '2005-04-01', 1, 1, 'CVE-2015-0042', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Internet Explorer是微软公司推出的一款网页浏览器。Microsoft Internet Explorer 9-11版本存在远程内存破坏漏洞，远程攻击者通过构造的网站，利用此漏洞可执行任意代码或造成拒绝服务。', 'code_execution', 2809736, 'CVE_2015-0042_MS15-009_IE_CGeneratedSvgTreeNode_UAF', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT CVE 2015-0042 MS15-009 IE CGeneratedSvgTreeNode UAF\"; flow:established,from_server; file_data; content:\"altGlyph\"; fast_pattern; nocase; content:\"marker\"; distance:0; nocase; content:\"InsertOrderedList\"; distance:0; nocase; content:\"document.getElementsByTagNameNS(\"; nocase; distance:0; pcre:\"/\\s*marker[^)]*\\)/R\"; classtype:attempted-user; sid:2809736; rev:3;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (571, 2, 'IGSS SCADA RMS Report Template WriteFile 命令行缓冲区溢出', '2003-01-01', 1, 1, 'CVE-2011-1567', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向TCP端口12401传递的多个命令导致拒绝服务，并可能执行任意代码。这些命令包括：0xd操作码中的（1）ListAll，（2）Write File，（3）ReadFile，（4）Delete，（5）RenameFile，及（6）FileInfo命令；RMS报告模板（0x7）操作码中的（7）the Add，（8）ReadFile，（9）Write File，（10）Rename，（11）Delete，（12）Add命令；（13）STDREP请求(0x8)操作码中的0x4命令。', 'overflow', 1111630, 'IGSS_SCADA_RMS_Report_Template_WriteFile_Command_Buffer_Overflow', 0, 'tcp any any -> any 12401 (msg:\"ETPRO SCADA IGSS SCADA RMS Report Template WriteFile Command Buffer Overflow\"; flow:to_server,established; byte_test:2,>,278,0,little; content:\"|01 00 34 12 07|\"; offset:2; depth:5; content:\"|05|\"; distance:11; within:1; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111630; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (827, 2, 'Linux/ShellshockCampaign.DDOSBot 木马的CnC 服务器消息 - 终止进程', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Linux/ShellshockCampaign.DDOSBot是一个利用Bash Shellshock进行传播的木马，本签名检测该木马的CnC 服务器消息 - 终止进程。', 'trojan', 2019304, 'Linux/ShellshockCampaign.DDOSBot_Terminate_Process_CnC_Server_Message', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN Linux/ShellshockCampaign.DDOSBot Terminate Process CnC Server Message\"; flow:established,to_client; dsize:12; content:\"! LOLNOGTFO|0A|\"; depth:12; metadata: former_category MALWARE; reference:url,research.zscaler.com/2014/09/shellshock-attacks-spotted-in-wild.html; reference:cve,2014-6271; classtype:trojan-activity; sid:2019304; rev:2; metadata:created_at 2014_09_29, updated_at 2014_09_29;)');
