INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (68, 2, 'Iconics Genesis SCADA 整数溢出', '2004-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Iconics Genesis。', 'ICONICS是一家专门从事可视化和自动化软件开发的公司。GENESIS32GENESIS32是一个基于Web的HMI和SCADA应用，它完全可扩展OPC、SNMP和BACnet。GenBroker是一个监听38080端口的Windows服务。GENESIS32 9.2在处理opccode 0xfa7型报文时存在整数溢出漏洞，成功的利用该漏洞可导致恶意代码执行。', 'overflow', 1111669, 'Iconics_Genesis_SCADA_Integer_Overflow_0xfa7', 0, 'tcp any any -> any 38080 (msg:\"ETPRO SCADA Iconics Genesis SCADA Integer Overflow 0xfa7\"; flow:to_server,established; content:\"|01 00 00 15 00 00 00 01 00 00 1F F4 01 00 00 00|\"; content:\"|a7 0f|\"; distance:4; within:2; isdataat:1024; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111669; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (324, 2, 'SMB-DS DCERPC Workstation 服务unicode 绑定尝试', '2006-01-01', 1, 1, 'CVE-2003-0812', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft DCE/RPC服务可以提供网络管理功能，这些功能提供管理用户帐户和网络资源管理的功能。部分网络管理功能在Windows目录下的debug子目录会生成调试日志文件。  Microsoft Workstation服务在处理日志记录时缺少充分的边界缓冲区检查，远程攻击者可以利用这个漏洞提供超长参数触发缓冲区溢出，以SYSTEM权限在系统上执行任意指令。  日志功能中使用vsprintf()在日志文件中生成字符串，日志文件名为NetSetup.LOG，其保存在Windows debug目录中。  这个记录函数有部分处理Workstation服务命令的函数调用，如NetValidateName， NetJoinDomain等，在这NetValidateName()中，computer name作为第二个参数最终记录在日志文件中。  如我们使用NetValidateName() API：\nNetValidateName(L\\\\\\\\192.168.0.100，AAAAAAAA，NULL，NULL，0);  那么我们可以在远程主机中产生如下记录条目：\n08/13 13:01:01 NetpValidateName: checking to see if &#039;&#039; is valid as type 0 name\n08/13 13:01:01 NetpValidateName: &#039;&#039; is not a valid NetBIOS \\\\AAAAAAAA name: 0x57\n如果我们指定超长字符串作为NetValidateName() API的第二个参数，如果调试文件可写就可以在特定主机上发生缓冲区溢出。  一般如果是NTFS文件系统，在Windows目录中的debug目录不允许所有人可写，这表示不能使用NULL会话来生成日志。WsImpersonateClient() API在打开日志文件前调用，如果连接客户端没有有效的权限来写日志文件，那么CreateFile()就会失败，vsprintf()就不会被执行，因此此漏洞在FAT32系统和%SYSTEMROOT%\\debug目录可写的情况下可被利用。  但是部分扩展RPC函数实现在Windows XP上在调用WsImpersonateClient()前打开日志文件，不过这些RPC函数没有提供文档化说明，不过可以观察在WKSSVC.DLL中的函数表观察到。这些扩展命令的RPC号开始于0x1B，如0x1B调用NetpManageComputers()，但在打开日志文件前不调用WsImpersonateClient()。  NetpManageComputers()的使用没有被公开化，但是我们可以在LMJoin.h中找到NetAddAlternateComputerName() API的原型定义，这个API从NETAPI32.DLL导出，这个API也一样没有文档化。我们可以执行这个RPC函数(0x1B)使用如下API产生包：\nNetAddAlternateComputerName(L\\\\\\\\192.168.0.200，long_unicode_string，NULL，NULL，0);  我们不需要特殊权限在远程主机上写第二个产生到日志文件中，如定义超长Unicode字符串作为第二个参数(AlternateName)，在第一个参数定义的远程系统就会由于缓冲区溢出而崩溃。Unicode字符串long_unicode_string会在日志记录函数调用前被转换为ASCII字符串。', 'overflow', 2102310, 'NETBIOS_SMB-DS_DCERPC_Workstation_Service_unicode_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS DCERPC Workstation Service unicode bind attempt\"; flow:to_server,established; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; depth:5; offset:4; nocase; byte_test:2,&,1,5,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00 05 00 0B|\"; within:15; distance:4; byte_test:1,&,16,1,relative; content:\"|98 D0 FF|k|12 A1 10|6|98|3F|C3 F8|~4Z\"; within:16; distance:29; reference:bugtraq,9011; reference:cve,2003-0812; reference:url,www.microsoft.com/technet/security/bulletin/MS03-049.mspx; classtype:misc-attack; sid:2102310; rev:9; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (580, 2, 'IGSS SCADA FileInfo 函数缓冲区溢出', '2003-01-01', 1, 1, 'CVE-2011-1567', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向TCP端口12401传递的多个命令导致拒绝服务，并可能执行任意代码。这些命令包括：0xd操作码中的（1）ListAll，（2）Write File，（3）ReadFile，（4）Delete，（5）RenameFile，及（6）FileInfo命令；RMS报告模板（0x7）操作码中的（7）the Add，（8）ReadFile，（9）Write File，（10）Rename，（11）Delete，（12）Add命令；（13）STDREP请求(0x8)操作码中的0x4命令。', 'overflow', 1111627, 'IGSS_SCADA_FileInfo_Function_Buffer_Overflow', 0, 'tcp any any -> any 12401 (msg:\"ETPRO SCADA IGSS SCADA FileInfo Function Buffer Overflow\"; flow:to_server,established; byte_test:2,>,278,0,little; content:\"|01 00 34 12 0D|\"; offset:2; depth:5; content:\"|06|\"; distance:11; within:1; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111627; rev:1;)');
