INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (837, 2, 'Win64.Havex 病毒检测', '2007-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Havex是被编写来感染SCADA和工控系统中使用的工业控制软件，这种木马可能有能力禁用水电大坝、使核电站过载、甚至可以做到按一下键盘就能关闭一个国家的电网。<br>Havex被命名为W32/Havex.A，它是一种通用的远程访问木马，近来被用于从事工业间谍活动，主要攻击对象是欧洲的许多使用和开发工业应用程序和机械设备的公司。Havex存在超过88个变种，期通信用的C&C服务器多达146个，并且有1500多个IP地址向C&C服务器通信。Havex利用OPC标准来收集网络和联网设备的信息。<br>', 'worm', 2020083, 'Win64/Havex_Checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Win64/Havex Checkin\"; flow:established,to_server; content:\".php?id=\"; http_uri; fast_pattern:only; content:!\"Referer|3a|\"; http_header; pcre:\"/\\.php\\?id=\\d{30}\\w{6}-\\d{2}-\\d{3}-\\d{9}$/U\"; metadata: former_category MALWARE; reference:url,blog.trendmicro.com/trendlabs-security-intelligence/64-bit-version-of-havex-spotted/; classtype:trojan-activity; sid:2020083; rev:2; metadata:created_at 2014_12_30, updated_at 2014_12_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (70, 2, '微软终端服务器的用户登录 - 可能为Morto蠕虫的攻击尝试', '2004-01-01', 1, 1, 'CVE-2001-0540', NULL, 1, NULL, NULL, NULL, NULL, NULL, '本签名检测微软终端服务器的用户登录 - 可能为Morto蠕虫的攻击尝试。Morto（Backdoor:W32/Morto.A and Worm:W32/Morto.B）是一个Windows远程桌面蠕虫。一旦某台电脑受到感染，就会扫描本地网络中允许远程桌面连接的机器，并且在RDP端口（port 3389/TCP）发起大量连接，当找到一台远程桌面服务器后，Morto 会尝试不同的密码，并试图以管理员的身份登录。', 'worm', 2013497, 'MS_Terminal_Server_User_A_Login,_possible_Morto_inbound', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 3389 (msg:\"ET DELETED MS Terminal Server User A Login, possible Morto inbound\"; flow:to_server,established; content:\"|03 00 00|\"; depth:3; content:\"|e0 00 00 00 00 00|\"; offset:5; depth:6; content:\"Cookie|3a| mstshash=a|0d 0a|\"; nocase; reference:cve,CAN-2001-0540; classtype:protocol-command-decode; sid:2013497; rev:2; metadata:created_at 2011_08_30, updated_at 2011_08_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (326, 2, 'Muieblackcat 扫描器扫描探测', '2007-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本签名检测Muieblackcat 扫描器的扫描探测行为。Muieblackcat是一个新的扫描器，它被用来扫描PHP应用，如phpMyAdmin。在扫描发起之前，它会发出一个含有/muieblackcat的GET请求报文。', 'scan', 2013115, 'Muieblackcat_scanner', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET WEB_SERVER Muieblackcat scanner\"; flow:established,to_server; content:\"GET /muieblackcat HTTP/1.1\"; depth:26; classtype:attempted-recon; sid:2013115; rev:3; metadata:created_at 2011_06_24, updated_at 2011_06_24;)');
