INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (327, 2, 'Microsoft Internet Explorer CSS内存破坏漏洞(MS10-090)', '2000-01-01', 1, 1, 'CVE-2010-3971', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Internet Explorer是微软Windows操作系统中默认捆绑的WEB浏览器。  在Microsoft Internet Explorer 6至8版本和其他产品中使用的mshtml.dll中的层叠样式表（CSS）解析器的CSharedStyleSheet::Notify函数中存在释放后使用漏洞。远程攻击者可借助样式表中的self-referential @import规则执行任意代码或导致拒绝服务（应用程序崩溃）。', 'code_execution', 2012149, 'MS10-090_IE_CSS_Exploit_Metasploit_POC_Specific_Unicoded', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"ET DELETED MS10-090 IE CSS Exploit Metasploit POC Specific Unicoded\"; flow:to_client,established; content:\"|40 00 69 00 6d 00 70 00 6f 00 72 00 74 00|\"; content:\"|40 00 69 00 6d 00 70 00 6f 00 72 00 74 00|\"; distance:0; content:\"|40 00 69 00 6d 00 70 00 6f 00 72 00 74 00|\"; distance:0; pcre:\"/@\\x00i\\x00m\\x00p\\x00o\\x00r\\x00t\\x00\\x20.{4,20}[^\\x00\\w\\s.]/sG\"; metadata: former_category WEB_CLIENT; reference:cve,CVE-2010-3971; reference:url,breakingpointsystems.com/community/blog/ie-vulnerability/; reference:bid,45246; classtype:attempted-admin; sid:2012149; rev:5; metadata:affected_product Web_Browsers, affected_product Any, affected_product Web_Browser_Plugins, attack_target Client_and_Server, deployment Perimeter, deployment Internet, deployment Internal, deployment Datacenter, tag Web_Client_Attacks, tag Metasploit, signature_severity Critical, created_at 2011_01_05, updated_at 2018_04_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (583, 2, 'Zeus.Downloader 命令控制行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2018384, 'Zeus.Downloader_Campaign_Unknown_Initial_CnC_Beacon_10/4/2014', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Zeus.Downloader Campaign Unknown Initial CnC Beacon 10/4/2014\"; flow:established,to_server; content:\"POST\"; http_method; content:\"/ccc/tab.php\"; http_uri; fast_pattern:only; pcre:\"/Host\\x3A\\x20\\d{1,3}\\x2E\\d{1,3}\\x2E\\d{1,3}\\x2E\\d{1,3}/H\"; metadata: former_category MALWARE; reference:md5,94d5d99b910f9184573a01873fdc42fc; classtype:trojan-activity; sid:2018384; rev:3; metadata:created_at 2014_04_11, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (72, 2, 'Microsoft WINS 溢出尝试', '2008-01-01', 1, 1, 'CVE-2004-1080', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'WINS是Microsoft NetBIOS名字服务，用于解析NetBIOS计算机名到IP地址。  WinNT4.0、Win2000及Win2003中的WINS服务(wins.exe)存在代码执行漏洞。  远程攻击者可利用WINS复制功能，通过构造特殊的WINS复制包发送至TCP 24端口，就可修改任意内存地址，从而可能导致执行任意代码。', 'overflow', 2103017, 'WINS_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 42 (msg:\"GPL DELETED WINS overflow attempt\"; flow:to_server,established; byte_test:1,&,64,6; byte_test:1,&,32,6; byte_test:1,&,16,6; byte_test:1,&,8,6; pcre:!\"/^.{8}(\\x05\\x37(\\x1E[\\x90-\\xFF]|[\\x1F-\\x2F].|\\x30[\\x00-\\x70])|\\x00\\x00\\x00[\\x00-\\x65]|\\x02\\x68\\x05\\xC0)/s\"; reference:bugtraq,11763; reference:cve,2004-1080; reference:url,www.immunitysec.com/downloads/instantanea.pdf; reference:url,www.microsoft.com/technet/security/bulletin/MS04-045.mspx; classtype:misc-attack; sid:2103017; rev:8; metadata:created_at 2010_09_23, updated_at 2016_06_14;)');
