INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (842, 2, 'Ecava IntegraXor SCADA 栈缓冲区溢出', '2001-01-01', 1, 1, 'CVE-2014-0753', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Ecava IntegraXor SCADA Server。', 'Ecava IntegraXor是马来西亚Ecava公司的一套基于Web的用于创建和运行SCADA系统的HMI界面（人机接口）的工具。Ecava IntegraXor 4.1.4380及之前的版本中的SCADA服务器中存在基于栈的缓冲区溢出漏洞。远程攻击者可借助IntegraXor目录下的DLL代码的访问权限利用该漏洞造成拒绝服务（系统崩溃）。', 'overflow', 2807491, 'IntegraXor_Stack_Buffer_Overflow', 0, 'tcp any any -> $HOME_NET 7131 (msg:\"SCADA IntegraXor Stack Buffer Overflow\"; flow:to_server,established; content:\"/res?\"; fast_pattern; content:\".dll/\"; distance:0; isdataat:261,relative; pcre:\"/\\/res\\?[^\\n]+?\\.dll\\/[^\\n]{261}/\"; reference:url,revuln.com/files/Ferrante_Auriemma_Securing_ICS_Without_Vendors.pdf; classtype:attempted-user; sid:2807491; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (75, 2, 'Muieblackcat 扫描器扫描探测', '2007-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本签名检测Muieblackcat 扫描器的扫描探测行为。Muieblackcat是一个新的扫描器，它被用来扫描PHP应用，如phpMyAdmin。在扫描发起之前，它会发出一个含有/muieblackcat的GET请求报文。', 'scan', 2013116, 'Muieblackcat_scanner', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET SCAN Potential muieblackcat scanner double-URI and HTTP library\"; flow:established,to_server; content:\"GET //\"; depth:6; fast_pattern; content:\"HTTP/1.1|0d 0a|Accept|3a| */*|0d 0a|Accept-Language|3a| en-us|0d 0a|Accept-Encoding|3a| gzip, deflate|0d 0a|Host|3a| \"; http_header; content:\"|0d 0a|Connection|3a| Close|0d 0a 0d 0a|\"; http_header; distance:0; metadata: former_category SCAN; classtype:attempted-recon; sid:2013116; rev:5; metadata:created_at 2011_06_24, updated_at 2011_06_24;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (331, 2, 'Microsoft NTLM ASN.1 漏洞扫描尝试', '2006-01-01', 1, 1, 'CVE-2003-0818', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Internet Explorer是一款流行的WEB浏览程序。  Microsoft Internet Explorer存在多个安全问题，可导致任意代码执行，读取本地系统文件，下载任意文件到用户系统。  具体问题如下：  - 在相关Internet Explorer的跨域安全模型(从共享信息中保持不同域窗口)中存在三个漏洞，这些漏洞可导致在本地计算机区域中执行任意脚本，要利用这些漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。成功利用此漏洞可以从其他WEB站点访问信息，访问用户系统上的文件和在用户上执行任意代码。  - 在Internet Explorer中把区域信息传递给XML对象时存在安全问题。这个漏洞允许攻击者读取用户系统上的本地问。要利用这个漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。在用户浏览恶意站点或查看HTML EMAIL消息后，用户会被提示下载HTML页面，如果用户接收这个HTML文件下载，那么攻击者可以读取已知位置中的本地文件。  - 在Internet Explorer中在动态HTML事件上执行Drag-and-Drop操作时存在安全问题。如果用户点击，这个漏洞允许文件保存在用户系统中的目标指定目录上。没有任何对话框提示会要求用户下载。要利用这个漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。如果用户点击恶意连接，可导致代码保存在用户计算机上。', 'code_execution', 2102386, 'NTLM_ASN.1_vulnerability_scan_attempt', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS any (msg:\"GPL EXPLOIT NTLM ASN.1 vulnerability scan attempt\"; flow:to_server,established; content:\"Authorization|3A| Negotiate YIQAAABiBoMAAAYrBgEFBQKgggBTMIFQoA4wDAYKKwYBBAGCNwICCqM\"; http_header; reference:bugtraq,9633; reference:bugtraq,9635; reference:cve,2003-0818; reference:nessus,12052; reference:nessus,12055; reference:nessus,12065; reference:url,www.microsoft.com/technet/security/bulletin/MS04-007.mspx; classtype:attempted-dos; sid:2102386; rev:11; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (587, 2, 'SMB-DS IrotIsRunning 小端字节序andx 尝试', '2007-01-01', 1, 1, 'CVE-2002-1561', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows 2000/XP是微软公司开发的WINDOWS操作系统。  Microsoft Windows 2000/XP的RPC服务存在漏洞，远程攻击者可以利用这个漏洞进行拒绝服务攻击。  漏洞存在于Windows系统的DCE-RPC堆栈实现中，远程攻击者可以连接TCP 135端口，发送畸形数据，可导致关闭RPC服务，关闭RPC服务可以引起系统停止对新的RPC请求进行响应，产生拒绝服务。由于众多服务都依赖于RPC服务， 这可能使系统变得不稳定， 很多正常操作无法进行. 例如， Word中将无法使用拷贝/粘贴功能. 根据系统安装的补丁情况， 可能导致Windows XP系统重新起动.', 'dos', 2103269, 'NETBIOS_SMB-DS_IrotIsRunning_little_endian_andx_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS IrotIsRunning little endian andx attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.irot; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\x73|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,!&,128,6,relative; content:\"%\"; depth:1; offset:39; byte_jump:2,0,little,relative; content:\"&|00|\"; within:2; distance:29; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; isdataat:4,relative; content:\"|05|\"; byte_test:1,&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|02 00|\"; within:2; distance:19; byte_jump:4,8,relative,little,align; byte_test:4,>,1024,0,little; reference:bugtraq,6005; reference:cve,2002-1561; reference:url,www.microsoft.com/technet/security/bulletin/MS03-010.mspx; classtype:protocol-command-decode; sid:2103269; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
