INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (616, 2, 'Zeus.Downloader 命令控制行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2018183, 'Zeus.Downloader_Campaign_Unknown_Initial_CnC_Beacon', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Zeus.Downloader Campaign Unknown Initial CnC Beacon\"; flow:established,to_server; content:\"POST\"; http_method; content:\"/ppp/ta.php\"; http_uri; fast_pattern:only; pcre:\"/Host\\x3A\\x20\\d{1,3}\\x2E\\d{1,3}\\x2E\\d{1,3}\\x2E\\d{1,3}/H\"; metadata: former_category MALWARE; reference:md5,ca15e5e96aee8b18ca6f3c185a690cea; classtype:trojan-activity; sid:2018183; rev:4; metadata:created_at 2014_02_26, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (872, 2, 'SpyEye 僵尸网络数据窃取行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'botnet', 2013527, 'Spyeye_Data_Exfiltration_6', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Spyeye Data Exfiltration 6\"; flow:established,to_server; content:\"|B5 B5 B5 B5 BD BE BE BE BE BC BC BC BC B2 B2 B2 B2|\"; offset:5; depth:17; classtype:trojan-activity; sid:2013527; rev:3; metadata:created_at 2011_09_01, updated_at 2011_09_01;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (105, 2, 'Sinapsi eSolar光伏系统管理器任意的命令行执行攻击尝试', '2004-01-01', 1, 1, 'CVE-2012-5863', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Sinapsi eSolar Light Photovoltaic System Monitor。', 'Sinapsi eSolar Light是太阳能应用内使用的监控系统。Sinapsi eSolar Light Photovoltaic System Monitor (又名Schneider Electric Ezylog photovoltaic SCADA管理服务器)，Sinapsi eSolar，Sinapsi eSolar DUO固件2.0.2870_2.2.12之前的版本中的ping.php脚本中存在漏洞。远程攻击者可通过ip_dominio参数中的shell元字符利用该漏洞执行任意命令。<br>', 'code_execution', 2805585, 'Sinapsi_eSolar_Light_Photovoltaic_System_Monitor_arbitrary_command_execution', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET $HTTP_PORTS (msg:\"SCADA Sinapsi eSolar Light Photovoltaic System Monitor arbitrary command execution\"; flow:established,to_server; content:\"POST\"; http_method; content:\"/ping.php\"; http_uri; content:\"ip_dominio=\"; http_client_body; pcre:\"/ip_dominio\\x3d.+?\\s+?%(26|3b)/Pi\"; reference:url,www.us-cert.gov/control_systems/pdf/ICS-ALERT-12-284-01.pdf; reference:url,exploit-db.com/exploits/21273/; reference:url,nakedsecurity.sophos.com/2012/10/12/hackers-exploit-code-photovoltaic-solar/; reference:url,thehackernews.com/2012/10/scada-hacking-exploit-released-to-hack.html; classtype:web-application-attack; sid:2805585; rev:2;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (618, 2, 'Flashback 僵尸网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Flashback僵尸网络在2012年感染了近60万台Mac电脑。该木马通过利用Java的漏洞，进入Mac系统，所以安装有Java系统的Mac用户一旦访问了带有Flashback木马的网页就会被感染。Flashback僵尸网络目前的关注点是收集谷歌和Paypal等网站的账户密码，以便网络罪犯可以接管这些账户。', 'botnet', 2014597, 'Mac_Flashback_Checkin_1', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Mac Flashback Checkin 1\"; flow:established,to_server; content:\"GET\"; http_method; content:\"/owncheck/\"; http_uri; metadata: former_category MALWARE; classtype:trojan-activity; sid:2014597; rev:2; metadata:created_at 2012_04_16, updated_at 2012_04_16;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (107, 2, 'Microsoft Internet Explorer Word 文档未初始化的内存破坏漏洞 via xlsx', '2003-01-01', 1, 1, 'CVE-2010-3331', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Internet Explorer 是微软发布的非常流行的WEB浏览器。  Microsoft Internet Explorer 6至8版本不能正确处理与Microsoft Word读取Word文档有关的某些状况下的内存中的对象。远程攻击者可以通过访问(1)不能正确初始化或者(2)被删除的对象执行任意代码。', 'code_execution', 2802137, 'Microsoft_Internet_Explorer_Word_Document_Uninitialized_Memory_Corruption_via_xlsx', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT Microsoft Internet Explorer Word Document Uninitialized Memory Corruption via xlsx 2\"; flow:from_server,established; flowbits:isset,http.xlsx.download; file_data; content:\"ScriptBridge.ScriptBridge\"; distance:0; pcre:\"/(ActiveXObject|CreateObject)\\s*\\([\\x22\\x27]ScriptBridge.ScriptBridge/i\"; reference:cve,2010-3331; reference:bugtraq,43707; reference:url,www.microsoft.com/technet/security/bulletin/MS10-071.mspx; classtype:attempted-user; sid:2802137; rev:3;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (875, 2, 'SMB DCERPC msqueue bind 尝试', '2001-01-01', 1, 1, 'CVE-2003-0995', NULL, 0, NULL, NULL, NULL, NULL, NULL, 'Microsoft Message Queue Manager (MSQM)存在缓冲区溢出漏洞。远程攻击者可以通过队列登记要求来导致服务拒绝（RPC服务崩溃）。', 'misc_activity', 2103156, 'NETBIOS_DCERPC_msqueue_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 135 (msg:\"GPL NETBIOS DCERPC msqueue bind attempt\"; flow:to_server,established; content:\"|05|\"; depth:1; byte_test:1,!&,16,3,relative; content:\"|0B|\"; within:1; distance:1; content:\"|B0 01|R|97 CA|Y|D0 11 A8 D5 00 A0 C9 0D 80|Q\"; within:16; distance:29; flowbits:set,smb.tree.bind.msqueue; flowbits:noalert; reference:cve,2003-0995; reference:url,www.eeye.com/html/Research/Advisories/AD20030910.html; reference:url,www.microsoft.com/technet/security/bulletin/MS03-026.mspx; classtype:protocol-command-decode; sid:2103156; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (364, 2, '西门子SIMATIC WinCC flexible runtime 栈缓冲区溢出尝试', '2002-01-01', 1, 1, 'CVE-2011-4875', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Siemens WinCC。', 'Siemens SIMATIC WinCC Flexible是一款集成了SIMATIC STEP 7、SIMATIC WinCC和SINAMICS StartDrive的工程设计软件平台。Siemens SIMATIC WinCC多个版本中的运行加载器中的HmiLoad中存在基于栈的缓冲区溢出漏洞，该漏洞源于Transfer Mode开启时。远程攻击者可利用该漏洞借助与Unicode字符串相关的向量，执行任意代码。这些版本包括：Siemens WinCC flexible 2004版本、2005版本、2007版本、2008版本，WinCC V11 （也称TIA portal），TP、OP、 MP、 Comfort Panels和Mobile Panels SIMATIC HMI面板、WinCC V11 Runtime Advanced以及WinCC flexible Runtime。', 'overflow', 52371, 'Siemens_SIMATIC_WinCC_flexible_runtime_stack_buffer_overflow_attempt_4', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET [2308,50523] ( msg:\"PROTOCOL-SCADA Siemens SIMATIC WinCC flexible runtime stack buffer overflow attempt 7\"; flow:to_server,established; isdataat:1024; content:\"|00 08 03|\";depth:3; byte_test:4,>,0x410,3,little,relative; metadata:policy max-detect-ips drop; reference:cve,2011-4875; reference:url,www.exploit-db.com/exploits/18166/; classtype:attempted-admin; sid:52371; rev:1; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (620, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) 攻击 SIP 代理', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019290, 'CVE-2014-6271_Attempt_Against_SIP_Proxy', 0, 'tcp any any -> $HOME_NET [5060,5061] (msg:\"ET EXPLOIT Possible CVE-2014-6271 Attempt Against SIP Proxy\"; flow:to_server,established; content:\"|28 29 20 7b|\"; fast_pattern:only; reference:url,github.com/zaf/sipshock; classtype:attempted-admin; sid:2019290; rev:2; metadata:created_at 2014_09_26, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (109, 2, 'IGSS SCADA RMS Report Template Rename 命令行缓冲区溢出', '2003-01-01', 1, 1, 'CVE-2011-1567', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向TCP端口12401传递的多个命令导致拒绝服务，并可能执行任意代码。这些命令包括：0xd操作码中的（1）ListAll，（2）Write File，（3）ReadFile，（4）Delete，（5）RenameFile，及（6）FileInfo命令；RMS报告模板（0x7）操作码中的（7）the Add，（8）ReadFile，（9）Write File，（10）Rename，（11）Delete，（12）Add命令；（13）STDREP请求(0x8)操作码中的0x4命令。', 'overflow', 1111632, 'IGSS_SCADA_RMS_Report_Template_Rename_Command_Buffer_Overflow', 0, 'tcp any any -> any 12401 (msg:\"ETPRO SCADA IGSS SCADA RMS Report Template Rename Command Buffer Overflow\"; flow:to_server,established; byte_test:2,>,534,0,little; content:\"|01 00 34 12 07|\"; offset:2; depth:5; content:\"|02|\"; distance:11; within:1; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111632; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (365, 2, 'Microsoft Internet Explorer远程内存破坏漏洞(CVE-2015-0018)(MS15-009)', '2005-04-01', 1, 1, 'CVE-2015-0018', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Internet Explorer是微软公司推出的一款网页浏览器。Microsoft Internet Explorer 11版本存在远程内存破坏漏洞，远程攻击者通过构造的网站，利用此漏洞可执行任意代码或造成拒绝服务。', 'code_execution', 2809719, 'Possible_CVE-2015-0018_MS15-009_IE_UAF', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT Possible CVE-2015-0018 MS15-009 IE UAF\"; flow:established,from_server; file_data; content:\"onload=document.write(|22 22|)\"; nocase; fast_pattern:only; pcre:\"/<script[^\\w]{2,}?\\/script>.*<\\/\\s*head/is\"; pcre:\"/<embed[^\\w]{2,}?\\/\\s*embed>/i\"; classtype:attempted-user; sid:2809719; rev:4;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (621, 2, 'SMB Session Setup NTMLSSP andx asn1 溢出尝试', '2006-01-01', 1, 1, 'CVE-2003-0818', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Internet Explorer是一款流行的WEB浏览程序。  Microsoft Internet Explorer存在多个安全问题，可导致任意代码执行，读取本地系统文件，下载任意文件到用户系统。  具体问题如下：  - 在相关Internet Explorer的跨域安全模型(从共享信息中保持不同域窗口)中存在三个漏洞，这些漏洞可导致在本地计算机区域中执行任意脚本，要利用这些漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。成功利用此漏洞可以从其他WEB站点访问信息，访问用户系统上的文件和在用户上执行任意代码。  - 在Internet Explorer中把区域信息传递给XML对象时存在安全问题。这个漏洞允许攻击者读取用户系统上的本地问。要利用这个漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。在用户浏览恶意站点或查看HTML EMAIL消息后，用户会被提示下载HTML页面，如果用户接收这个HTML文件下载，那么攻击者可以读取已知位置中的本地文件。  - 在Internet Explorer中在动态HTML事件上执行Drag-and-Drop操作时存在安全问题。如果用户点击，这个漏洞允许文件保存在用户系统中的目标指定目录上。没有任何对话框提示会要求用户下载。要利用这个漏洞，攻击者必须构建包含恶意代码的页面，然后诱使用户访问该页。攻击者也可以构建恶意HTML形式EMAIL消息发送给用户打开。如果用户点击恶意连接，可导致代码保存在用户计算机上。', 'overflow', 2103001, 'NETBIOS_SMB_Session_Setup_NTMLSSP_andx_asn1_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB Session Setup NTMLSSP andx asn1 overflow attempt\"; flow:established,to_server; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,!&,128,6,relative; content:\"s\"; depth:1; offset:39; byte_jump:2,0,little,relative; byte_test:4,&,2147483648,21,relative,little; content:!\"NTLMSSP\"; within:7; distance:27; asn1:double_overflow, bitstring_overflow, relative_offset 27, oversize_length 2048; reference:bugtraq,9633; reference:bugtraq,9635; reference:cve,2003-0818; reference:nessus,12052; reference:nessus,12065; reference:url,www.microsoft.com/technet/security/bulletin/MS04-007.mspx; classtype:protocol-command-decode; sid:2103001; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (877, 2, 'Tomcat admin用户-admin密码 扫描探测', '2001-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本签名检测针对Tomcat 服务器的admin用户-admin密码的扫描探测行为。Tomcat 是一个小型的轻量级应用服务器，在中小型系统和并发访问用户不是很多的场合下被普遍使用，是开发和调试JSP 程序的首选。', 'scan', 2009217, 'Tomcat_admin-admin_login_credentials', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET $HTTP_PORTS (msg:\"ET SCAN Tomcat admin-admin login credentials\"; flow:to_server,established; content:\"/manager/html\"; http_uri; nocase; content:\"|0d 0a|Authorization|3a| Basic YWRtaW46YWRtaW4=|0d 0a|\"; flowbits:set,ET.Tomcat.login.attempt; reference:url,tomcat.apache.org; reference:url,doc.emergingthreats.net/2009217; classtype:attempted-admin; sid:2009217; rev:7; metadata:created_at 2010_07_30, updated_at 2020_02_24;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (110, 2, 'Microsoft Report Viewer控件跨站脚本攻击漏洞', '2004-01-01', 1, 1, 'CVE-2011-1976', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Visual Studio 2005 SP1和Report Viewer 2005 SP1版本的Report Viewer控件中存在跨站脚本攻击漏洞。远程攻击者可借助数据源中的参数注入任意web脚本或HTML。', 'xss', 2803595, 'WEB_SERVER_Microsoft_Report_Viewer_control_Cross-Site_Scripting_2', 0, 'tcp $EXTERNAL_NET any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"WEB_SERVER Microsoft Report Viewer control Cross-Site Scripting 2\"; flow:established,to_server; content:\"POST\"; http_method; content:\"ReportViewerWebControl.axd\"; http_uri; content:\"TimerMethod\"; http_client_body; pcre:\"/TimerMethod\\x3d.+(script|onmouse[a-z]+|onkey[a-z]+|onload|onunload|alert|ondragdrop|onblur|onfocus|onclick|ondblclick|onsubmit|onreset|onselect|onchange|style\\x3D)/i\"; reference:cve,2011-1976; reference:url,www.microsoft.com/technet/security/bulletin/MS11-067.mspx; classtype:attempted-user; sid:2803595; rev:2;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (622, 2, '通用电气GE D20 通过TFTP进行密码恢复尝试', '2009-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问General Electric GE D20。', '通用电气D20ME是一种广泛使用PLC，特别是在变电站。本事件报警试图通过TFTP恢复D20ME的默认密码的恶意行为。', 'info_leak', 2804649, 'General_Electric_D20_Password_Recovery_via_TFTP', 0, 'udp any any -> $HOME_NET 69 (msg:\"SCADA General Electric D20 Password Recovery via TFTP\"; content:\"|00 01|\"; depth:2; content:\"NVRAM\"; nocase; distance:0; content:\"D20.zlb\"; within:9;  nocase; reference:url,digitalbond.com/tools/basecamp/general-electric-d20meii/; classtype:attempted-recon; sid:2804649; rev:4;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (878, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) via DHCP', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019238, 'CVE-2014-6271_exploit_attempt_via_malicious_DHCP_ACK', 0, 'udp any 67 -> any 68 (msg:\"ET DELETED Possible CVE-2014-6271 exploit attempt via malicious DHCP ACK - option 67\"; content:\"|02 01|\"; depth:2; content:\"|43|\"; distance:238; content:\"|28 29 20 7b 20|\"; distance:1; within:10; reference:url,access.redhat.com/articles/1200223; reference:cve,2014-6271; classtype:attempted-admin; sid:2019238; rev:2; metadata:created_at 2014_09_25, updated_at 2014_09_25;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (367, 2, 'Zeus P2P网络 OneLouder EXE可执行文件下载', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2019103, 'OneLouder_EXE_download_possibly_installing_Zeus_P2P', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN OneLouder EXE download possibly installing Zeus P2P\"; flow:to_client,established; flowbits:isset,ET.OneLouderHeader; file_data; content:\"MZ\"; within:2; content:\"PE|00 00|\"; fast_pattern:only; flowbits:set,et.exploitkitlanding; classtype:trojan-activity; sid:2019103; rev:4; metadata:created_at 2014_09_02, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (623, 2, 'SMB IrotIsRunning unicode 小端字节序andx 尝试', '2007-01-01', 1, 1, 'CVE-2002-1561', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows 2000/XP是微软公司开发的WINDOWS操作系统。  Microsoft Windows 2000/XP的RPC服务存在漏洞，远程攻击者可以利用这个漏洞进行拒绝服务攻击。  漏洞存在于Windows系统的DCE-RPC堆栈实现中，远程攻击者可以连接TCP 135端口，发送畸形数据，可导致关闭RPC服务，关闭RPC服务可以引起系统停止对新的RPC请求进行响应，产生拒绝服务。由于众多服务都依赖于RPC服务， 这可能使系统变得不稳定， 很多正常操作无法进行. 例如， Word中将无法使用拷贝/粘贴功能. 根据系统安装的补丁情况， 可能导致Windows XP系统重新起动.', 'dos', 2103263, 'NETBIOS_SMB_IrotIsRunning_unicode_little_endian_andx_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB IrotIsRunning unicode little endian andx attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.irot; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\x73|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,&,128,6,relative; content:\"%\"; depth:1; offset:39; byte_jump:2,0,little,relative; content:\"&|00|\"; within:2; distance:29; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00 00 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; isdataat:4,relative; content:\"|05|\"; byte_test:1,&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|02 00|\"; within:2; distance:19; byte_jump:4,8,relative,little,align; byte_test:4,>,1024,0,little; reference:bugtraq,6005; reference:cve,2002-1561; reference:url,www.microsoft.com/technet/security/bulletin/MS03-010.mspx; classtype:protocol-command-decode; sid:2103263; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (112, 2, 'OSX/Flashback.K/I 网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Flashback僵尸网络在2012年感染了近60万台Mac电脑。该木马通过利用Java的漏洞，进入Mac系统，所以安装有Java系统的Mac用户一旦访问了带有Flashback木马的网页就会被感染。Flashback僵尸网络目前的关注点是收集谷歌和Paypal等网站的账户密码，以便网络罪犯可以接管这些账户。<br>', 'botnet', 2014522, 'OSX/Flashback.K/I_reporting_successful_infection', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN OSX/Flashback.K/I reporting successful infection\"; flow:established,to_server; content:\"/stat_d/\"; http_uri; pcre:\"/\\/stat_d\\/$/U\"; reference:url,f-secure.com/v-descs/trojan-downloader_osx_flashback_k.shtml; reference:url,f-secure.com/v-descs/trojan-downloader_osx_flashback_i.shtml; reference:url,vms.drweb.com/virus/?i=1816029; classtype:trojan-activity; sid:2014522; rev:4; metadata:created_at 2012_04_05, updated_at 2012_04_05;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (368, 2, 'CODESYS 网关服务器栈缓冲区溢出尝试', '2004-01-01', 1, 1, 'CVE-2012-4708', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问CoDeSys Gateway Server。', 'CoDeSys是Windows平台下，独立于硬件的IEC 61131-3开发系统，用于编程和创建控制器应用。3S CODESYS Gateway-Server 2.3.9.27之前版本中存在基于栈的缓冲区溢出漏洞。通过特制的数据包，远程攻击者利用该漏洞执行任意代码。', 'overflow', 39391, '3S_CoDeSys_Gateway_Server_stack_buffer_overflow_attempt_2', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET [1210,1211] ( msg:\"PROTOCOL-SCADA 3S CoDeSys Gateway Server stack buffer overflow attempt\"; flow:to_server,established; content:\"|DD DD|\";depth:2; byte_test:4,>,400,12,relative,little; metadata:policy max-detect-ips drop,policy security-ips drop; reference:cve,2012-4708; classtype:attempted-admin; sid:39391; rev:2; )');
