INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (883, 2, 'SMB DCERPC 远程激活绑定尝试', '2005-01-01', 1, 1, 'CVE-2003-0715', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Remote Procedure Call (RPC)是Windows 操作系统使用的一种远程过程调用协议，RPC提供进程间交互通信机制，允许在某台计算机上运行程序的无缝地在远程系统上执行代码。协议本身源自开放软件基金会的RPC协议，Microsoft在其基础上增加了自己的一些扩展。  eEye的研究人员发现，由于Windows RPC DCOM接口对报文的长度域缺乏检查导致发生基于堆的溢出，远程攻击者可以利用这些漏洞以本地系统权限在系统上执行任意指令。  漏洞实质上影响的是使用RPC的DCOM接口，此接口处理由客户端机器发送给服务器的DCOM对象激活请求(如UNC路径)。攻击者通过向目标发送畸形RPC DCOM请求来利用这些漏洞。成功利用此漏洞可以以本地系统权限执行任意指令。攻击者可以在系统上执行任意操作 ，如安装程序、查看或更改、删除数据或创建系统管理员权限的帐户。  攻击者可以通过 135(UDP/TCP)、137/UDP、138/UDP、139/TCP、445(UDP/TCP)、593/TCP端口进行攻击。对于启动了COM Internet服务和RPC over HTTP的用户来说，攻击者还可能通过80/TCP和443/TCP端口进行攻击。\nhttp://www.cert.org/advisories/CA-2003-23.html\nhttp://www.nsfocus.net/index.php?act=advisory&do=view&adv_id=30\nhttp://www.nsfocus.net/index.php?act=alert&do=view&aid=30  *>', 'misc_activity', 2102251, 'NETBIOS_DCERPC_Remote_Activation_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 135 (msg:\"GPL NETBIOS DCERPC Remote Activation bind attempt\"; flow:to_server,established; content:\"|05|\"; content:\"|0B|\"; within:1; distance:1; byte_test:1,&,1,0,relative; content:\"|B8|J|9F|M|1C|}|CF 11 86 1E 00| |AF|n|7C|W\"; within:16; distance:29; tag:session,5,packets; reference:bugtraq,8234; reference:bugtraq,8458; reference:cve,2003-0528; reference:cve,2003-0605; reference:cve,2003-0715; reference:nessus,11798; reference:nessus,11835; reference:url,www.microsoft.com/technet/security/bulletin/MS03-039.mspx; classtype:attempted-admin; sid:2102251; rev:16; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (116, 2, '三维力控 ActiveX 控件不安全的clsid访问', '2003-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Sunway ForceControl。', '三维力控6.1 SP3及之前版本的ActiveX控件存在多个安全漏洞。攻击者可以构造包含三维力控ActiveX控件的恶意网页，并诱导用户访问。成功的攻击者可以利用这些漏洞，在应用程序上下文执行任意的代码，并导致拒绝服务。', 'code_execution', 2013736, 'Sunway_ForceControl_Activex_Control_Insecure_clsid_access_2', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET SCADA Sunway ForceControl Activex Control Remote Code Execution Vulnerability 2\"; flow:to_client,established; content:\"<OBJECT \"; nocase; content:\"classid\"; nocase; distance:0; content:\"CLSID\"; nocase; distance:0; content:\"3310FA24-A027-47B3-8C49-1091077317E9\"; nocase; distance:0; pcre:\"/<OBJECT\\s+[^>]*classid\\s*=\\s*[\\x22\\x27]?\\s*clsid\\s*\\x3a\\s*\\x7B?\\s*3310FA24-A027-47B3-8C49-1091077317E9/si\"; reference:bugtraq,49747; classtype:attempted-user; sid:2013736; rev:4; metadata:affected_product Windows_XP_Vista_7_8_10_Server_32_64_Bit, attack_target Client_Endpoint, deployment Perimeter, tag ActiveX, signature_severity Major, created_at 2011_10_04, updated_at 2016_07_01;)');
