INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (628, 2, 'SMB llsrconnect 小端字节序andx 溢出尝试', '2001-01-01', 1, 1, 'CVE-2005-0050', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'License Logging Service是一款设计用于帮助客户管理Microsoft服务产品的证书的工具。Microsoft License Logging Service在处理畸形请求时存在安全漏洞，远程攻击者可以利用这个漏洞以系统权限在系统上执行任意指令。License Logging Service在处理某个带有超长的参数畸形请求时存在缓冲区溢出漏洞，远程攻击者可能利用此漏洞在主机上执行任意指令，从而完全控制系统。', 'overflow', 2103119, 'NETBIOS_SMB_llsrconnect_little_endian_andx_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB llsrconnect little endian andx overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.llsrpc; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\x73|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,!&,128,6,relative; content:\"%\"; depth:1; offset:39; byte_jump:2,0,little,relative; content:\"&|00|\"; within:2; distance:29; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 00|\"; within:2; distance:19; byte_test:4,>,52,0,relative; reference:url,www.microsoft.com/technet/security/bulletin/ms05-010.mspx; classtype:attempted-admin; sid:2103119; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (884, 2, 'Microsoft SQL Server 2000注册表表项权限设置不正确漏洞(MS02-034)', '2008-01-01', 1, 1, 'CVE-2002-0642', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft SQL Server 2000是微软公司开发和维护的一种商业SQL数据库系统。  SQL Server 2000的默认注册表表项权限设置存在漏洞，远程可以执行查询操作的用户可能利用此问题修改下次启动SQL服务进程的权限。  在默认情况下，指定SQL服务进程所运行帐号的注册表项的权限设置不正确，能在服务器上执行SQL查询的远程攻击者可以无需管理员权限通过SQL查询修改此注册表表项，使SQL服务在下次启动时以较高的权限执行。', 'code_execution', 2100686, 'NETBIOS_xp_reg*_-_registry_access', 0, 'tcp $EXTERNAL_NET any -> $SQL_SERVERS 1433 (msg:\"GPL NETBIOS xp_reg* - registry access\"; flow:to_server,established; content:\"x|00|p|00|_|00|r|00|e|00|g|00|\"; nocase; reference:bugtraq,5205; reference:cve,2002-0642; reference:nessus,10642; reference:url,www.microsoft.com/technet/security/bulletin/MS02-034; classtype:attempted-user; sid:2100686; rev:11; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
