INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (118, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) via OpenVPN', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019322, 'OpenVPN_CVE-2014-6271_attempt', 0, 'udp any any -> $HOME_NET 1194 (msg:\"ET EXPLOIT Possible OpenVPN CVE-2014-6271 attempt\"; flow:to_server; content:\"|20|\"; depth:1; content:\"|28 29 20 7b|\"; fast_pattern:only; reference:url,news.ycombinator.com/item?id=8385332; classtype:attempted-admin; sid:2019322; rev:2; metadata:created_at 2014_09_30, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (886, 2, 'Worm.Win32.Rimecud 蠕虫检测', '2002-03-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Win32.Worm.Rimecud是一个可以通过可移动驱动器、MSN、P2P网络和网络共享传播的蠕虫病毒。<br>Win32.Worm.Rimecud感染后，受影响的电脑可能会变得不稳定，还允许远程攻击者能够访问存储在被感染计算机上的数据并在无形中损害数据。', 'worm', 2012739, 'WORM_Rimecud_Worm_checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET WORM Rimecud Worm checkin\"; flow:established,to_server; content:\"GET\"; http_method; content:\"User-Agent|3a| Mozilla/3.0 (compatible|3b| Indy Library)\"; http_header; content:\"/taskx.txt\"; http_uri; fast_pattern; reference:url,www.threatexpert.com/report.aspx?md5=9623efa133415d19c941ef92a4f921fc; classtype:trojan-activity; sid:2012739; rev:2; metadata:created_at 2011_04_29, updated_at 2019_10_11;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (119, 2, 'SandWorm沙虫病毒 INF文件下载尝试 (SMB)', '2005-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Sandworm沙虫病毒几乎影响所有微软Windows vista以上装有Office软件的系统主机操作系统，通俗地说，这类攻击是通过发送附件文档的方式发起，受害者只要打开文档就会自动中招，也就是只要电脑中安装了覆盖面广泛的Office软件，都有可能受到该病毒的影响。<br>TrendMicro报告发现Sandworm发起后续攻击的恶意载荷包含了对工控企业的人机界面软件的攻击（主要是GE Cimplicity HMI），目的是进一步控制HMI，并且放置木马。该攻击利用了GE Cimplicity HMI软件的一个漏洞，打开攻击者恶意构造的.cim 或者.bcl文件允许在用户机器上执行任意代码，以及安装木马文件。上面提到的.cim 或者.bcl文件是Cimplicity的工程文件。', 'worm', 2019398, 'CURRENT_EVENTS_Possible_SandWorm_INF_Download_(SMB)', 0, 'tcp $EXTERNAL_NET [445,139] -> $HOME_NET any (msg:\"ET CURRENT_EVENTS Possible SandWorm INF Download (SMB)\"; flow:to_client,established; content:\"Software|5c|Microsoft|5c|Windows|5c|CurrentVersion|5c|Run\"; nocase; content:\"7EBEFBC0-3200-11d2-B4C2-00A0C9697D17\"; fast_pattern; nocase; content:\"ClassGuid\"; nocase; content:\"DefaultInstall\"; nocase; classtype:attempted-user; sid:2019398; rev:2; metadata:created_at 2014_10_14, updated_at 2014_10_14;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (375, 2, 'SMB-DS DCERPC PnP QueryResConfList exploit攻击尝试', '2004-01-01', 1, 1, 'CVE-2005-1983', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows即插即用(PnP)功能允许操作系统在安装新硬件时能够检测到这些设备。 Microsoft Windows即插即用功能中存在缓冲区溢出漏洞，成功利用这个漏洞的攻击者可以完全控制受影响的系统。起因是PnP服务处理包含有过多数据的畸形消息的方式。在Windows 2000上，匿名用户可以通过发送特制消息来利用这个漏洞；在Windows XP Service Pack 1上，只有通过认证的用户才能发送恶意消息；在Windows XP Service Pack 2和Windows Server 2003上，攻击者必需本地登陆到系统然后运行特制的应用程序才能利用这个漏洞。目前已经出现了利用此漏洞进行传播的网络蠕虫。', 'overflow', 2002201, 'NETBIOS_SMB-DS_DCERPC_PnP_QueryResConfList_exploit_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"ET NETBIOS SMB-DS DCERPC PnP QueryResConfList exploit attempt\"; flow:to_server,established; content:\"|FF|SMB%\"; depth:5; offset:4; nocase; content:\"&|00|\"; within:2; distance:56; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00|\"; within:12; distance:5; nocase; content:\"|36 00|\"; within:2; distance:26; pcre:\"/(\\x00\\\\\\x00.*?){2}\\x00{2}\\xFF{2}.{128,}[\\x04-\\xFF][\\x00-\\xFF]{3}\\x00{4}$/Rs\"; flowbits:isset,netbios.pnp.bind.attempt; reference:cve,CAN-2005-1983; reference:url,www.microsoft.com/technet/security/Bulletin/MS05-039.mspx; reference:url,doc.emergingthreats.net/bin/view/Main/2002201; classtype:attempted-admin; sid:2002201; rev:4; metadata:created_at 2010_07_30, updated_at 2010_07_30;)');
