INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (887, 2, '施耐德电气IGSS 整数下溢尝试', '2002-01-01', 1, 1, 'CVE-2013-0657', NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'Schneider Electric Interactive Graphical SCADA System (IGSS) 10和较早版本中存在基于栈的缓冲区溢出漏洞。远程攻击者利用该漏洞执行任意代码。', 'overflow', 25849, 'Schneider_Electric_IGSS_integer_underflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET [12397,12399] ( msg:\"PROTOCOL-SCADA Schneider Electric IGSS integer underflow attempt\"; flow:to_server,established; content:\"|00 00 00|\";depth:3;offset:9; byte_test:1,<,0x20,8; metadata:policy max-detect-ips drop,policy security-ips drop; reference:cve,2013-0657; classtype:attempted-user; sid:25849; rev:10; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (120, 2, 'HTTP SQL注入攻击尝试', '2005-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本事件检测到针对WEB服务器的SQL注入攻击，攻击者通过发送含有“CREATE ”的报文来尝试SQL注入。<br>SQL注入攻击指的是通过构建特殊的输入作为参数传入Web应用程序，而这些输入大都是SQL语法里的一些组合，通过执行SQL语句进而执行攻击者所要的操作，其主要原因是程序没有细致地过滤用户输入的数据，致使非法数据侵入系统。', 'sql_injection', 2010086, 'WEB_SERVER_Possible_CREATE_SQL_Injection_Attempt', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"ET WEB_SERVER Possible CREATE SQL Injection Attempt in URI\"; flow:to_server,established; content:\"CREATE\"; http_uri; nocase; pcre:\"/CREATE\\ +(database|procedure|table|column|directory)/Ui\"; reference:url,www.owasp.org/index.php/SQL_Injection; reference:url,www.w3schools.com/Sql/sql_create_db.asp; reference:url,doc.emergingthreats.net/2010086; classtype:web-application-attack; sid:2010086; rev:6; metadata:affected_product Web_Server_Applications, attack_target Web_Server, deployment Datacenter, tag SQL_Injection, signature_severity Major, created_at 2010_07_30, updated_at 2019_09_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (376, 2, 'Microsoft Internet Explorer 惠普照片创意 ActiveX 组件安全漏洞', '2000-01-01', 1, 1, 'CVE-2011-3397', NULL, 0, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft Windows XP SP2和SP3版本，以及Server 2003 SP2版本的Microsoft Time组件中存在漏洞。远程攻击者可借助Internet Explorer上的未明binary behavior的特制网站执行任意代码，也称为Microsoft Time Remote Code Execution Vulnerability。', 'code_execution', 2804152, 'Microsoft_Internet_Explorer_HP_Photo_Creative_ActiveX_-_CLSID_4', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"ACTIVEX Microsoft Internet Explorer HP Photo Creative ActiveX - CLSID 4\"; flow:established,from_server; content:\"7a7b986c-31e9-4286-88ca-b9dc481ca989\"; nocase; content:\"CLSID\"; nocase; pcre:\"/CLSID[\\s\\x3a\\x7b]*7a7b986c-31e9-4286-88ca-b9dc481ca989/i\"; reference:cve,2011-3397; reference:url,technet.microsoft.com/en-us/security/bulletin/ms11-090; classtype:attempted-user; sid:2804152; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (632, 2, 'Microsoft Windows Media Player ehtrace.dll 不安全的库加载尝试', '2002-01-01', 1, 1, 'CVE-2011-0038', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Internet Explorer是微软Windows操作系统中默认捆绑的WEB浏览器。  Microsoft Internet Explorer 8版本中存在不可信搜索路径漏洞。本地用户可借助当前工作目录中的Trojan木马IEShims.dll文件获得特权。', 'code_execution', 2801454, 'Windows_Media_Player_ehtrace.dll_Insecure_Library_Loading', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET $HTTP_PORTS (msg:\"WEB_CLIENT Windows Media Player ehtrace.dll Insecure Library Loading\"; flow:to_server,established; content:\"/ehtrace.dll\"; http_uri; nocase; content:\"GET\"; http_method; reference:cve,2011-0038; reference:url,www.microsoft.com/technet/security/Bulletin/MS11-015.mspx; classtype:attempted-user; sid:2801454; rev:5;)');
