INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (897, 2, 'NetBiter webScada 用户信息泄漏', '2002-01-01', 1, 1, NULL, NULL, 0, NULL, NULL, NULL, NULL, '攻击者必须访问Intellicom Netbiter webSCADA。', 'Intellicom Netbiter webSCADA WS100和WS200产品中存在多个远程安全漏洞，包括一个目录遍历漏洞，信息泄露漏洞，和任意文件上传漏洞。远程攻击者可以利用这些漏洞上传并执行任意脚本代码，查看任意的本地文件，获取敏感信息。', 'info_leak', 2800917, 'NetBiter_webScada_User_Information_Disclosure', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET $HTTP_PORTS (msg:\"SCADA SCADA NetBiter webScada User Information Disclosure\"; flow:to_server,established; content:\"/cgi-bin/read.cgi?file=/home/config/users.cfg\"; http_uri; reference:url,www.securityfocus.com/archive/1/514104; classtype:web-application-attack; sid:2800917; rev:2;)');
