INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (899, 2, 'Zeus P2P网络 OneLouder 下载器的网络流量检测 - SET', '2009-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2018982, 'Probable_OneLouder_downloader_(Zeus_P2P)', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN Probable OneLouder downloader (Zeus P2P) exe download\"; flow:established,to_client; flowbits:isset,ET.Onelouder.bin; content:\"MZ\"; byte_jump:4,58,relative,little; content:\"PE|00 00|\"; distance:-64; within:4; classtype:trojan-activity; sid:2018982; rev:2; metadata:created_at 2014_08_21, updated_at 2014_08_21;)');
