INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (388, 2, 'Zeus GameOver 僵尸网络 Upatre 下载器的网络流量检测', '2006-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Upatre下载器被Gameover Zeus使用来分发它的恶意软件。Gameover Zeus僵尸网络，被美国FBI称为“史上最复杂、最具破坏性的网络僵尸病毒”。而Gameover Zeus的强大之处在于，它不仅会利用Zeus木马感染计算机，窃取在线电子邮件账户、社交网络和网银与其它在线金融服务。Gameover Zeus会在入侵的电脑中安装加密勒索程序CryptoLocker，将使用者的所有资料进行加密，付钱才能赎回资料。', 'botnet', 2018453, 'Upatre_Downloader_2p_(Zeus)_May_07_2014', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Upatre Downloader 2p (Zeus) May 07 2014\"; flow:to_server,established; content:\"2p/\"; http_uri; fast_pattern:only; content:!\"Accept-Language\"; http_header; content:!\"Referer|3A|\"; http_header; pcre:\"/\\/p?2p\\/[a-z]{3}$/U\"; classtype:trojan-activity; sid:2018453; rev:5; metadata:created_at 2014_05_07, updated_at 2019_10_07;)');
