INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (141, 2, 'HTTP SQL注入攻击尝试', '2005-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本事件检测到针对WEB服务器的SQL注入攻击，攻击者通过发送含有“UNION SELECT”的报文来尝试SQL注入。<br>SQL注入攻击指的是通过构建特殊的输入作为参数传入Web应用程序，而这些输入大都是SQL语法里的一些组合，通过执行SQL语句进而执行攻击者所要的操作，其主要原因是程序没有细致地过滤用户输入的数据，致使非法数据侵入系统。', 'sql_injection', 2006446, 'WEB_SERVER_Possible_SQL_Injection_Attempt_UNION_SELECT', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS any (msg:\"ET WEB_SERVER Possible SQL Injection Attempt UNION SELECT\"; flow:established,to_server; content:\"UNION\"; http_uri; nocase; content:\"SELECT\"; http_uri; nocase; pcre:\"/UNION.+SELECT/Ui\"; reference:url,en.wikipedia.org/wiki/SQL_injection; reference:url,doc.emergingthreats.net/2006446; classtype:web-application-attack; sid:2006446; rev:13; metadata:affected_product Web_Server_Applications, attack_target Web_Server, deployment Datacenter, tag SQL_Injection, signature_severity Major, created_at 2010_07_30, updated_at 2019_08_22;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (397, 2, 'SMB DCERPC LSASS 绑定尝试', '2000-09-01', 1, 1, 'CVE-2003-0533', NULL, 0, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows LSA是本地安全授权服务(LSASRV.DLL)。  LSASS DCE/RPC末端导出的Microsoft活动目录服务存在一个缓冲区溢出，远程攻击者可以利用这个漏洞以SYSTEM权限在系统上执行任意指令。  一些活动目录服务在Windows目录中在debug子目录里生成调试日志文件，在LSASRV.DLL实现的记录函数写信息条目到日志文件中，这里的vsprintf()函数用于建立日志条目。由于对提供给这个函数的字符串参数缺少正确的边界缓冲区检查，超长字符串可导致缓冲区溢出。部分RPC函数接收此字符串作为参数并尝试写调试日志文件，利用这些RPC函数，可能以SYSTEM权限在系统上执行任意指令。', 'misc_activity', 2102507, 'NETBIOS_DCERPC_LSASS_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 135 (msg:\"GPL NETBIOS DCERPC LSASS bind attempt\"; flow:to_server,established; content:\"|05|\"; depth:1; content:\"|00|\"; within:1; distance:1; byte_test:1,&,1,0,relative; content:\"j|28 19|9|0C B1 D0 11 9B A8 00 C0|O|D9|.|F5|\"; within:16; distance:29; flowbits:set,netbios.lsass.bind.attempt; flowbits:noalert; reference:bugtraq,10108; reference:cve,2003-0533; reference:url,www.microsoft.com/technet/security/bulletin/MS04-011.mspx; classtype:protocol-command-decode; sid:2102507; rev:8; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
