INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (143, 2, 'Microsoft Internet Explorer CVE-2015-0046 类型混淆漏洞', '2005-04-01', 1, 1, 'CVE-2015-0046', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Internet Explorer是微软公司推出的一款网页浏览器。Microsoft Internet Explorer 9-11版本存在远程内存破坏漏洞，远程攻击者通过构造的网站，利用此漏洞可执行任意代码或造成拒绝服务。', 'code_execution', 2809739, 'CVE_2015-0046_MS15-009_IE_Type_Confusion', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT CVE 2015-0046 MS15-009 IE Type Confusion\"; flow:established,from_server; file_data; content:\".setAttribute(\"; nocase; distance:0; content:\"884E2049-217D-11DA-B2A4-000E7BBB2B09\"; nocase; fast_pattern:6,20; distance:0; content:\"document.body.swapNode(\"; nocase; distance:0; pcre:\"/[\\s]*(?P<objname>\\w+?)\\s*\\).*?(?P=objname)\\[\\s*\'\\s*0\\s*\'/Rsi\"; classtype:attempted-user; sid:2809739; rev:8;)');
