INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (677, 2, 'Microsoft Internet Explorer 潜在的 Use-After-Free 堆溢出攻击尝试', '2003-01-01', 1, 1, 'CVE-2010-3328', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Internet Explorer是微软Windows操作系统中默认捆绑的WEB浏览器。  Microsoft Internet Explorer 6至8版本的mshtml.dll中的CAttrArray::PrivateFind函数中存在释放后使用漏洞。远程攻击者可通过设置样式表对象中的未明属性执行任意代码。', 'overflow', 2802122, 'Microsoft_Internet_Explorer_Potential_Use-After-Free_Heap_Overflow_attempt_(Exploit_Specific)', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT Microsoft Internet Explorer Potential Use-After-Free Heap Overflow attempt (Exploit Specific)\"; flow:from_server,established; file_data; content:\"heapLib.ie|28|\"; distance:0; content:\"|2e|push|28|document|2e|createElement|28 27|div|27 29 29|\"; distance:0; content:\"unescape|28|\"; distance:0; reference:cve,2010-3328; reference:url,www.microsoft.com/technet/security/bulletin/MS10-071.mspx; reference:url,www.vreugdenhilresearch.nl/pocs/ms10-071.txt; classtype:attempted-user; sid:2802122; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (166, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) via DNS', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019402, 'CVE-2014-6271_malicious_DNS_response', 0, 'udp $EXTERNAL_NET 53 -> $HOME_NET any (msg:\"ET EXPLOIT Possible CVE-2014-6271 malicious DNS response\"; byte_test:1,&,128,2; content:\"|28 29 20 7b|\"; fast_pattern:only; reference:cve,2014-6271; reference:url,packetstormsecurity.com/files/128650; classtype:attempted-admin; sid:2019402; rev:1; metadata:created_at 2014_10_15, updated_at 2019_10_07;)');
