INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (428, 2, 'Zeus 或 ZeroAccess 僵尸网络请求命令控制服务器', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。<br>ZeroAccess，也称为max++和Sirefef，它是一款木马型的恶意软件，它感染微软的Windows操作系统。ZeroAccess主要设计向感染的计算机传递载荷，一旦ZeroAccess感染系统就会开始下载各种不同类型的恶意软件。它的最终目的是为了创造收入，通过广告点击欺诈和比特币挖掘。ZeroAccess是2012年的九大僵尸网络之一，其控制的电脑数量已经超过200万台。', 'botnet', 2013911, 'P2P_Zeus_or_ZeroAccess_Request_To_CnC', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN P2P Zeus or ZeroAccess Request To CnC\"; flow:established,to_server; dsize:20; content:\"|E5 AA C0 31|\"; depth:4; content:\"|5B 74 08 4D 9B 39 C1|\"; distance:5; within:7; metadata: former_category MALWARE; reference:url,www.abuse.ch/?p=3499; reference:url,www.kindsight.net/sites/default/files/Kindsight_Malware_Analysis-ZeroAcess-Botnet-final.pdf; classtype:trojan-activity; sid:2013911; rev:9; metadata:created_at 2011_11_11, updated_at 2011_11_11;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (684, 2, 'IGSS SCADA RMS Report Add 命令行缓冲区溢出', '2003-01-01', 1, 1, 'CVE-2011-1567', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向TCP端口12401传递的多个命令导致拒绝服务，并可能执行任意代码。这些命令包括：0xd操作码中的（1）ListAll，（2）Write File，（3）ReadFile，（4）Delete，（5）RenameFile，及（6）FileInfo命令；RMS报告模板（0x7）操作码中的（7）the Add，（8）ReadFile，（9）Write File，（10）Rename，（11）Delete，（12）Add命令；（13）STDREP请求(0x8)操作码中的0x4命令。', 'overflow', 1111628, 'IGSS_SCADA_RMS_Report_Add_Command_Buffer_Overflow', 0, 'tcp any any -> any 12401 (msg:\"ETPRO SCADA IGSS SCADA RMS Report Add Command Buffer Overflow\"; flow:to_server,established; byte_test:2,>,278,0,little; content:\"|01 00 34 12 07|\"; offset:2; depth:5; content:\"|04|\"; distance:11; within:1; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111628; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (940, 2, 'Microsoft Internet Explorer远程内存破坏漏洞(CVE-2015-1623)(MS15-018)', '2003-07-01', 1, 1, 'CVE-2015-1623', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Internet Explorer是微软公司推出的一款网页浏览器。Internet Explorer 没有正确访问内存对象，在实现上存在远程代码执行漏洞，成功利用后可破坏内存，在当前用户权限下执行任意代码。', 'code_execution', 2810028, 'MS15-018_Internet_Explorer_Elevation_of_Privilege_Vulnerability_CVE-2015-1623', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"WEB_CLIENT MS15-018 Internet Explorer Elevation of Privilege Vulnerability CVE-2015-1623\"; flow:from_server,established; file_data; content:\"MutationObserver(\"; fast_pattern:only; content:\"childList\"; content:\"characterData\"; content:\"characterDataOldValue\"; content:\"location.reload()\"; content:\"text/javascript\"; pcre:\"/[^/]*var\\s+(?P<mo_name>[^ =]+)\\s*=\\s*new\\s*MutationObserver\\(\\s*function\\(\\s*\\).*(?P=mo_name).observe\\(.*\\.insertAdjacentText\\((?:\\x22|\\x27)\\s*afterEnd.*]\\.replaceNode\\(.*document.getSelection\\(\\).*\\.adoptNode\\(/Rsi\"; reference:cve,2015-1623; classtype:attempted-user; sid:2810028; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (173, 2, 'NETBIOS DCERPC Workstation Service 直接服务访问尝试', '2006-01-01', 1, 1, 'CVE-2003-0812', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft DCE/RPC服务可以提供网络管理功能，这些功能提供管理用户帐户和网络资源管理的功能。部分网络管理功能在Windows目录下的debug子目录会生成调试日志文件。  Microsoft Workstation服务在处理日志记录时缺少充分的边界缓冲区检查，远程攻击者可以利用这个漏洞提供超长参数触发缓冲区溢出，以SYSTEM权限在系统上执行任意指令。  日志功能中使用vsprintf()在日志文件中生成字符串，日志文件名为NetSetup.LOG，其保存在Windows debug目录中。  这个记录函数有部分处理Workstation服务命令的函数调用，如NetValidateName， NetJoinDomain等，在这NetValidateName()中，computer name作为第二个参数最终记录在日志文件中。  如我们使用NetValidateName() API：\nNetValidateName(L\\\\\\\\192.168.0.100，AAAAAAAA，NULL，NULL，0);  那么我们可以在远程主机中产生如下记录条目：\n08/13 13:01:01 NetpValidateName: checking to see if &#039;&#039; is valid as type 0 name\n08/13 13:01:01 NetpValidateName: &#039;&#039; is not a valid NetBIOS \\\\AAAAAAAA name: 0x57\n如果我们指定超长字符串作为NetValidateName() API的第二个参数，如果调试文件可写就可以在特定主机上发生缓冲区溢出。  一般如果是NTFS文件系统，在Windows目录中的debug目录不允许所有人可写，这表示不能使用NULL会话来生成日志。WsImpersonateClient() API在打开日志文件前调用，如果连接客户端没有有效的权限来写日志文件，那么CreateFile()就会失败，vsprintf()就不会被执行，因此此漏洞在FAT32系统和%SYSTEMROOT%\\debug目录可写的情况下可被利用。  但是部分扩展RPC函数实现在Windows XP上在调用WsImpersonateClient()前打开日志文件，不过这些RPC函数没有提供文档化说明，不过可以观察在WKSSVC.DLL中的函数表观察到。这些扩展命令的RPC号开始于0x1B，如0x1B调用NetpManageComputers()，但在打开日志文件前不调用WsImpersonateClient()。  NetpManageComputers()的使用没有被公开化，但是我们可以在LMJoin.h中找到NetAddAlternateComputerName() API的原型定义，这个API从NETAPI32.DLL导出，这个API也一样没有文档化。我们可以执行这个RPC函数(0x1B)使用如下API产生包：\nNetAddAlternateComputerName(L\\\\\\\\192.168.0.200，long_unicode_string，NULL，NULL，0);  我们不需要特殊权限在远程主机上写第二个产生到日志文件中，如定义超长Unicode字符串作为第二个参数(AlternateName)，在第一个参数定义的远程系统就会由于缓冲区溢出而崩溃。Unicode字符串long_unicode_string会在日志记录函数调用前被转换为ASCII字符串。', 'misc_activity', 2102316, 'NETBIOS_DCERPC_Workstation_Service_direct_service_access_attempt', 0, 'udp $EXTERNAL_NET any -> $HOME_NET 1024: (msg:\"GPL NETBIOS DCERPC Workstation Service direct service access attempt\"; content:\"|04 00|\"; depth:2; byte_test:1,&,16,2,relative; content:\"|98 D0 FF|k|12 A1 10|6|98|3F|C3 F8|~4Z\"; within:16; distance:22; reference:bugtraq,9011; reference:cve,2003-0812; reference:url,www.microsoft.com/technet/security/bulletin/MS03-049.mspx; classtype:misc-attack; sid:2102316; rev:7; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
