INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (688, 2, 'SMB IrotIsRunning 小端字节序andx 尝试', '2007-01-01', 1, 1, 'CVE-2002-1561', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows 2000/XP是微软公司开发的WINDOWS操作系统。  Microsoft Windows 2000/XP的RPC服务存在漏洞，远程攻击者可以利用这个漏洞进行拒绝服务攻击。  漏洞存在于Windows系统的DCE-RPC堆栈实现中，远程攻击者可以连接TCP 135端口，发送畸形数据，可导致关闭RPC服务，关闭RPC服务可以引起系统停止对新的RPC请求进行响应，产生拒绝服务。由于众多服务都依赖于RPC服务， 这可能使系统变得不稳定， 很多正常操作无法进行. 例如， Word中将无法使用拷贝/粘贴功能. 根据系统安装的补丁情况， 可能导致Windows XP系统重新起动.', 'dos', 2103261, 'NETBIOS_SMB_IrotIsRunning_little_endian_andx_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB IrotIsRunning little endian andx attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.irot; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\x73|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,!&,128,6,relative; content:\"%\"; depth:1; offset:39; byte_jump:2,0,little,relative; content:\"&|00|\"; within:2; distance:29; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; isdataat:4,relative; content:\"|05|\"; byte_test:1,&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|02 00|\"; within:2; distance:19; byte_jump:4,8,relative,little,align; byte_test:4,>,1024,0,little; reference:bugtraq,6005; reference:cve,2002-1561; reference:url,www.microsoft.com/technet/security/bulletin/MS03-010.mspx; classtype:protocol-command-decode; sid:2103261; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (944, 2, 'PRG木马wnspoem 或 Zeus 信息窃取器的木马配置下载', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'PrgTrojan，又称为wnspoem，它是2006年10月发现的一个信息窃取型的木马。<br>Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'trojan', 2008100, 'PRG/wnspoem/Zeus_InfoStealer_Trojan_Config_Download', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN PRG/wnspoem/Zeus InfoStealer Trojan Config Download\"; flow:established;  content:\"GET\"; http_method; content:\"/cfg.bin\"; nocase; http_uri; fast_pattern; isdataat:!1,relative; content:\"no-cache|0d 0a|\"; http_header; nocase; reference:url,doc.emergingthreats.net/2008100; classtype:trojan-activity; sid:2008100; rev:12; metadata:created_at 2010_07_30, updated_at 2019_09_28;)');
