INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (689, 2, 'SMB-DS llsrconnect unicode 溢出尝试', '2001-01-01', 1, 1, 'CVE-2005-0050', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'License Logging Service是一款设计用于帮助客户管理Microsoft服务产品的证书的工具。Microsoft License Logging Service在处理畸形请求时存在安全漏洞，远程攻击者可以利用这个漏洞以系统权限在系统上执行任意指令。License Logging Service在处理某个带有超长的参数畸形请求时存在缓冲区溢出漏洞，远程攻击者可能利用此漏洞在主机上执行任意指令，从而完全控制系统。', 'overflow', 2103124, 'NETBIOS_SMB-DS_llsrconnect_unicode_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS llsrconnect unicode overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.llsrpc; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; within:5; distance:3; byte_test:1,&,128,6,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00 00 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 00|\"; within:2; distance:19; byte_test:4,>,104,0,relative; reference:url,www.microsoft.com/technet/security/bulletin/ms05-010.mspx; classtype:attempted-admin; sid:2103124; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
