INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (179, 2, 'Microsoft Windows远程桌面协议代码执行漏洞', '2001-01-01', 1, 1, 'CVE-2012-0002', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft Windows XP SP2与SP3， Windows Server 2003 SP2， Windows Vista SP2， Windows Server 2008 SP2， R2及R2 SP1，与Windows 7 Gold与SP1版本中的远程桌面协议(RDP)实现中存在漏洞，该漏洞源于没有正确处理内存中的数据包。远程攻击者可通过发送特制RDP数据包触发访问（1）没有正确初始化或者（2）已被删除的对象，执行任意代码。也称&lsquo;Remote Desktop Protocol Vulnerability&rsquo;。  该漏洞已有修复补丁，如果未能及时安装补丁可采取以下临时修复措施：  方法一：禁用终端服务、远程桌面、远程协助和 Windows Small Business Server 2003 远程工作网站功能；  方法二：在企业周边防火墙中屏蔽TCP端口3389；  方法三：在运行 Windows Vista、Windows 7、Windows Server 2008 和 Windows Server 2008 R2 的受支持版本的系统上启用网络级别身份验证。', 'code_execution', 2014663, 'DOS_Microsoft_Remote_Desktop_Protocol_(RDP)_maxChannelIds_Negative_Integer_indef_DoS_Attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 3389 (msg:\"ET DOS Microsoft Remote Desktop Protocol (RDP) maxChannelIds Negative Integer indef DoS Attempt\"; flow:to_server,established; content:\"|03 00|\"; depth:2; content:\"|e0|\"; distance:3; within:1; content:\"|03 00|\"; distance:0; content:\"|f0|\"; distance:3; within:1; content:\"|7f 65|\"; distance:1; within:2; content:\"|04 01 01 04 01 01 01 01 ff 30|\"; distance:3; within:10; content:\"|02|\"; distance:1; within:1; byte_test:1,>,0x80,0,relative; byte_test:1,<,0xFF,0,relative; byte_jump:1,0,relative, post_offset -128; byte_jump:1,-1,relative; byte_test:1,&,0x80,-1,relative,big; reference:url, www.msdn.microsoft.com/en-us/library/cc240836.aspx; reference:cve,2012-0002; reference:url,technet.microsoft.com/en-us/security/bulletin/ms12-020; reference:url,stratsec.blogspot.com.au/2012/03/ms12-020 vulnerability-for-breakfast.html; reference:url,aluigi.org/adv/termdd_1-adv.txt; reference:url,blog.binaryninjas.org/?p=58; reference:url,luca.ntop.org/Teaching/Appunti/asn1.html; classtype:attempted-dos; sid:2014663; rev:1; metadata:created_at 2012_05_02, updated_at 2012_05_02;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (435, 2, 'SpyEye 僵尸网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'botnet', 2012686, 'SpyEye_Checkin_version_1.3.25_or_later', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN SpyEye Checkin version 1.3.25 or later\"; flow:established,to_server; content:\"POST\"; nocase; http_method; content:\"data=vK6yv+\"; http_client_body; metadata: former_category MALWARE; classtype:trojan-activity; sid:2012686; rev:4; metadata:created_at 2011_04_13, updated_at 2011_04_13;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (691, 2, 'Microsoft Windows 通用控件库堆缓冲区溢出', '2004-01-01', 1, 1, 'CVE-2010-2746', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Comctl32.dll是Windows应用程序公用GUI图形用户界面模块。Microsoft Windows XP SP2和SP3版本，Windows Server 2003 SP2版本，Windows Vista SP1和SP2版本，Windows Server 2008 Gold，SP2和R2版本，以及Windows 7中的Comctl32.dll(又名通用控制库)文件中存在基于堆的缓冲区溢出漏洞。当第三方SVG查看器启用时，远程攻击者可以借助特制HTML文档（该文档能够触发查看器中的相关信息）执行任意代码。', 'overflow', 2012174, 'Microsoft_Windows_Common_Control_Library_Heap_Buffer_Overflow', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET EXPLOIT Microsoft Windows Common Control Library Heap Buffer Overflow\"; flow:established,from_server; content:\"Content-Type|3a| image/svg|2b|xml\"; nocase; file_data; content:\"|3c|svg xmlns=\"; nocase; distance:0; content:\"style|3d 22|fill|3a 20 23|ffffff|22|\"; nocase; distance:0; content:\"transform\"; nocase; distance:0; pcre:\"/^=\\s*\\x22\\s*[^\\s\\x22\\x28]{1000}/iR\"; reference:bugtraq,43717; reference:url,www.microsoft.com/technet/security/bulletin/MS10-081.mspx; classtype:attempted-admin; sid:2012174; rev:9; metadata:created_at 2011_01_12, updated_at 2011_01_12;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (436, 2, 'Sinapsi eSolar光伏系统管理器SQL注入攻击尝试', '2004-01-01', 1, 1, 'CVE-2012-5861', NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Ezylog photovoltaic management server。', 'Sinapsi eSolar Light是太阳能应用内使用的监控系统。Sinapsi eSolar Light Photovoltaic System Monitor (又名Schneider Electric Ezylog photovoltaic SCADA管理服务器)，Sinapsi eSolar，Sinapsi eSolar DUO 固件2.0.2870_2.2.12之前版本中存在多个SQL注入漏洞。远程攻击者利用该漏洞通过(1)primo操作中的‘inverterselect’参数传送到dettagliinverter.php脚本或(2)‘lingua’参数传送到changelanguagesession.php脚本，执行任意SQL命令。', 'sql_injection', 57515, 'Sinapsi_eSolar_Light_Photovoltaic_System_Monitor_SQL_Injection_2', 0, 'http $EXTERNAL_NET any -> $HOME_NET $HTTP_PORTS ( msg:\"SERVER-WEBAPP Sinapsi eSolar Light Photovoltaic System Monitor SQL injection attempt 5\"; flow:to_server,established; content:\"/scripts/changelanguagesession.php\";http_uri; fast_pattern;nocase; content:\"lingua=\";http_client_body; nocase; pcre:\"/(^|&)lingua=[^&]*?([\\x27\\x22\\x3b\\x23\\x28]|%(25)?(27|22|3b|23|28)|(\\x2f|%(25)?2f)(\\x2a|%(25)?2a)|(\\x2d|%(25)?2d){2})/im\"; metadata:policy max-detect-ips drop,policy security-ips drop; reference:cve,2012-5861; classtype:web-application-attack; sid:57515; rev:1; )');
