INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (437, 2, '西门子Tecnomatix FactoryLink vrn.exe opcode 9 缓冲区溢出', '2003-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Siemens FactoryLink vrn.exe。', '西门子 Tecnomatix FactoryLink 8.0.1.1473版本存在多个漏洞，包括缓冲区溢出、内存破坏和信息泄露漏洞。vrn.exe是FactoryLink中运行的一个服务器软件，它监听7579端口。vrn.exe在解析opccode 9的text域时使用了一个特殊的函数。这个函数拷贝栈缓冲区中以分号或空格分隔的字符串，它存在一个经典的栈缓冲区溢出漏洞。', 'overflow', 2801910, 'Siemens_Tecnomatix_FactoryLink_vrn.exe_opcode_9_buffer_overflow', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 7579 (msg:\"SCADA Siemens Tecnomatix FactoryLink vrn.exe opcode 9 buffer overflow 2\"; flow:to_server; content:\"|ff 55 09 00|\"; offset:4; depth:4; content:\"|01 00|\"; distance:8; within:2; content:\"|01 00 01 00|\"; distance:16; within:4; pcre:\"/^..[^\\s\\x3b]{512}/Rs\"; reference:bugtraq,46934; classtype:attempted-user; sid:2801910; rev:2;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (693, 2, 'BMP含bfOffBits的畸形位图文件', '2002-01-01', 1, 1, 'CVE-2006-0006', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows Media Player是一款非常流行的媒体播放器。  Microsoft Windows Media Player在处理畸形的位图文件时存在漏洞，攻击者可能利用此漏洞在用户机器上执行任意指令。Windows Media Player可以播放位图格式文件(如.bmp文件)并解码bmp文件，但没有正确地处理声明大小为0的bmp文件。在这种情况下，WMP会分配大小为0的堆，但实际上会以实际文件长度拷贝数据到这个堆，因此声明大小为0的bmp文件会导致溢出。攻击者可以通过诱骗用户使用Windows Media Player打开特制的位图文件导致执行任意指令。', 'overflow', 2002803, 'BMP_with_invalid_bfOffBits', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET EXPLOIT BMP with invalid bfOffBits\"; flow:established,to_client; content:\"|0d 0a 0d 0a|BM\"; fast_pattern; byte_test:4,>,14,0,relative; content:\"|0000000000000000|\"; distance:4; within:8; reference:url,www.microsoft.com/technet/security/Bulletin/ms06-005.mspx; reference:cve,2006-0006; reference:bugtraq,16633; reference:url,doc.emergingthreats.net/bin/view/Main/2002803; classtype:attempted-user; sid:2002803; rev:10; metadata:created_at 2010_07_30, updated_at 2010_07_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (182, 2, 'SMB DCERPC CoGetInstanceFromFile 溢出尝试', '2001-01-01', 1, 1, 'CVE-2003-0995', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Message Queue Manager (MSQM)存在缓冲区溢出漏洞。远程攻击者可以通过队列登记要求来导致服务拒绝（RPC服务崩溃）。', 'overflow', 2103159, 'NETBIOS_DCERPC_CoGetInstanceFromFile_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 135 (msg:\"GPL NETBIOS DCERPC CoGetInstanceFromFile overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.msqueue; content:\"|05|\"; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|01 00|\"; within:2; distance:19; byte_test:4,>,128,20,relative; reference:cve,2003-0995; reference:url,www.eeye.com/html/Research/Advisories/AD20030910.html; reference:url,www.microsoft.com/technet/security/bulletin/MS03-026.mspx; classtype:attempted-admin; sid:2103159; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (950, 2, 'Microsoft Windows远程桌面协议代码执行漏洞', '2001-01-01', 1, 1, 'CVE-2012-0002', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft Windows XP SP2与SP3， Windows Server 2003 SP2， Windows Vista SP2， Windows Server 2008 SP2， R2及R2 SP1，与Windows 7 Gold与SP1版本中的远程桌面协议(RDP)实现中存在漏洞，该漏洞源于没有正确处理内存中的数据包。远程攻击者可通过发送特制RDP数据包触发访问（1）没有正确初始化或者（2）已被删除的对象，执行任意代码。也称&lsquo;Remote Desktop Protocol Vulnerability&rsquo;。  该漏洞已有修复补丁，如果未能及时安装补丁可采取以下临时修复措施：  方法一：禁用终端服务、远程桌面、远程协助和 Windows Small Business Server 2003 远程工作网站功能；  方法二：在企业周边防火墙中屏蔽TCP端口3389；  方法三：在运行 Windows Vista、Windows 7、Windows Server 2008 和 Windows Server 2008 R2 的受支持版本的系统上启用网络级别身份验证。', 'code_execution', 2014662, 'DOS_Microsoft_Remote_Desktop_Protocol_(RDP)_maxChannelIds_Integer_indef_DoS_Attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 3389 (msg:\"ET DOS Microsoft Remote Desktop Protocol (RDP) maxChannelIds Integer indef DoS Attempt\"; flow:to_server,established; content:\"|03 00|\"; depth:2; content:\"|e0|\"; distance:3; within:1; content:\"|03 00|\"; distance:0; content:\"|f0|\"; distance:3; within:1; content:\"|7f 65|\"; distance:1; within:2; content:\"|04 01 01 04 01 01 01 01 ff 30|\"; distance:3; within:10; content:\"|02|\"; distance:1; within:1; byte_test:1,>,0x80,0,relative; byte_test:1,<,0xFF,0,relative; byte_jump:1,0,relative, post_offset -128; byte_jump:1,-1,relative; byte_test:1,<,0x06,-1,relative,big; reference:url,www.msdn.microsoft.com/en-us/library/cc240836.aspx; reference:cve,2012-0002; reference:url,technet.microsoft.com/en-us/security/bulletin/ms12-020; reference:url,stratsec.blogspot.com.au/2012/03/ms12-020 vulnerability-for-breakfast.html; reference:url,aluigi.org/adv/termdd_1-adv.txt; reference:url,blog.binaryninjas.org/?p=58; reference:url,luca.ntop.org/Teaching/Appunti/asn1.html; classtype:attempted-dos; sid:2014662; rev:1; metadata:created_at 2012_05_02, updated_at 2012_05_02;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (183, 2, '罗克韦尔自动化ControlLogix EtherNET/IPInitialize 设备更新固件尝试', '2007-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Rockwell ControlLogix。', 'ControlLogix是罗克韦尔自动化公司发布的PLC产品。ControlLogix系统是基于机架的系统，可以提供对使用顺序控制、过程控制、运动控制和驱动控制的控制系统进行组态的选项，还可提供通信I/O 功能。本事件检测针对ControlLogix的固件更改攻击，成功的攻击可导致固件中被植入木马。', 'code_execution', 2804492, 'Rockwell_Automation_ControlLogix_EtherNET/IPInitialize_the_device_to_update_the_firmware', 0, 'tcp any any -> any 44818 (msg:\"SCADA Rockwell Automation ControlLogix EtherNET/IPInitialize the device to update the firmware\"; flow:established,to_server; content:\"|6f 00|\"; depth:2; content:\"|00 00 00 00 05 00 02 00 00 00 00 00 b2 00 16 00 4b 02 20 a1 24 01 05 99 07 00 4f 02 20 37 24 c8 00 00 01 00 01 00|\"; distance:0; reference:url,threatpost.com/en_us/blogs/dhs-thinks-some-scada-problems-are-too-big-call-bug-092611; reference:url,reversemode.com/index.php?option=com_content&task=view&id=81&Itemid=2; reference:url,rockwellautomation.com/enabled/pdf/eipexp1_2.pdf; classtype:attempted-admin; sid:2804492; rev:4;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (439, 2, 'Linux/ShellshockCampaign.DDOSBot 木马的CnC 服务器消息 - 获取僵尸网络主机IP', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Linux/ShellshockCampaign.DDOSBot是一个利用Bash Shellshock进行传播的木马，本签名检测该木马的CnC 服务器消息 - 获取僵尸网络主机IP。', 'trojan', 2019295, 'Linux/ShellshockCampaign.DDOSBot_Get_Bot_IP_CnC_Server_Message', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN Linux/ShellshockCampaign.DDOSBot Get Bot IP CnC Server Message\"; flow:established,to_client; dsize:13; content:\"! GETLOCALIP|0A|\"; depth:13; metadata: former_category MALWARE; reference:url,research.zscaler.com/2014/09/shellshock-attacks-spotted-in-wild.html; reference:cve,2014-6271; classtype:trojan-activity; sid:2019295; rev:2; metadata:created_at 2014_09_29, updated_at 2014_09_29;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (695, 2, 'Zeus 僵尸网络恶意的SSL证书检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2018715, 'ABUSE.CH_SSL_Fingerprint_Blacklist_Malicious_SSL_certificate_detected_(ZeuS_C2)', 0, 'tls $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET DELETED ABUSE.CH SSL Fingerprint Blacklist Malicious SSL certificate detected (ZeuS C2)\"; flow:established,from_server; content:\"|06|lzx.su\"; fast_pattern:only; tls.fingerprint:\"79:67:bb:dd:e9:c1:17:46:8d:26:cd:de:db:20:e2:1c:46:63:bd:d7\"; reference:url,sslbl.abuse.ch; classtype:trojan-activity; sid:2018715; rev:3; metadata:attack_target Client_Endpoint, deployment Perimeter, tag SSL_Malicious_Cert, signature_severity Major, created_at 2014_07_17, updated_at 2016_07_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (440, 2, 'HTTP SQL注入攻击尝试', '2005-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本事件检测到针对WEB服务器的SQL注入攻击，攻击者通过发送含有“SHOW TABLES”的报文来尝试SQL注入。<br>SQL注入攻击指的是通过构建特殊的输入作为参数传入Web应用程序，而这些输入大都是SQL语法里的一些组合，通过执行SQL语句进而执行攻击者所要的操作，其主要原因是程序没有细致地过滤用户输入的数据，致使非法数据侵入系统。', 'sql_injection', 2010967, 'WEB_SERVER_SHOW_TABLES_SQL_Injection_Attempt', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"ET WEB_SERVER SHOW TABLES SQL Injection Attempt in URI\"; flow:established,to_server; content:\"SHOW\"; http_uri; nocase; content:\"TABLES\"; http_uri; nocase; pcre:\"/SHOW.+TABLES/Ui\"; reference:url,en.wikipedia.org/wiki/SQL_injection; reference:url,dev.mysql.com/doc/refman/4.1/en/show-tables.html; reference:url,doc.emergingthreats.net/2010967; classtype:web-application-attack; sid:2010967; rev:4; metadata:affected_product Web_Server_Applications, attack_target Web_Server, deployment Datacenter, tag SQL_Injection, signature_severity Major, created_at 2010_07_30, updated_at 2019_09_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (696, 2, 'Zeus 僵尸网络恶意的SSL证书检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Zeus，曾被称为“DIY僵尸之神”，它可以让网络罪犯窃取银行信息和其他敏感数据。它包括一个控制平面和一个可生成执行程序感染肉鸡的生成器。该恶意软件主要通过邮件传播，或感染驱动。2012年初，Zeus的一个新版本出现，可利用P2P协议与命令控制网站建立联系。Zeus是2012年的九大僵尸网络之一，截至2012年10月，Zeus僵尸网络的命令控制(C&C)服务器估计已有944台。', 'botnet', 2022946, '', 0, 'tls $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN ABUSE.CH SSL Fingerprint Blacklist Malicious SSL Certificate Detected (Zeus C2)\"; flow:established,from_server; content:\"|55 04 03|\"; content:\"|1e|7yh0mdze6ztr7erew835im3w8.info\"; fast_pattern; distance:1; within:31; metadata: former_category MALWARE; reference:url,sslbl.abuse.ch; classtype:trojan-activity; sid:2022946; rev:2; metadata:attack_target Client_Endpoint, deployment Perimeter, tag SSL_Malicious_Cert, signature_severity Major, created_at 2016_07_05, performance_impact Low, updated_at 2016_07_05;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (185, 2, 'BlackEnergy 恶意软件 SSL 证书检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'BlackEnergy是一款受欢迎的犯罪软件（即一种使犯罪活动自动化的恶意软件），流通在俄罗斯的地下网络，最早能够追溯到2007年。 刚开始，它被设计为一个在DDos攻击中创建僵尸网络的工具。随着时间的推移，这款恶意软件已经演变为支持各种插件，插件能够基于攻击的意图进行组合以提供必要的功能。鉴于该工具的特点，Blackenergy已被不同团伙用于不同的用途。有些团伙利用它发送垃圾邮件；另一些用来盗取银行凭证。最恶名昭彰的案例是在2008年俄格冲突期间，该工具被用来对格鲁吉亚实施网络攻击。', 'trojan', 2019504, 'BlackEnergy_SSL_Cert', 0, 'tls $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN BlackEnergy SSL Cert\"; flow:from_server,established; content:\"|16|\"; content:\"|0b|\"; within:8; content:\"|09 00 9a ba cb 13 6d 76 5b 79|\"; distance:0; fast_pattern; content:\"|55 04 06|\"; distance:0; content:\"|02|XX\"; distance:1; within:3; content:\"|55 04 07|\"; distance:0; content:\"|0c|Default City\"; distance:1; within:13; content:\"|55 04 0a|\"; distance:0; content:\"|13|Default Company Ltd\"; distance:1; within:20; reference:url,blog.trendmicro.com/trendlabs-security-intelligence/sandworm-to-blacken-the-scada-connection; classtype:trojan-activity; sid:2019504; rev:2; metadata:attack_target Client_Endpoint, deployment Perimeter, tag SSL_Malicious_Cert, signature_severity Major, created_at 2014_10_24, updated_at 2016_07_01;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (441, 2, 'Microsoft Windows远程桌面协议代码执行漏洞', '2001-01-01', 1, 1, 'CVE-2012-0002', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft Windows XP SP2与SP3， Windows Server 2003 SP2， Windows Vista SP2， Windows Server 2008 SP2， R2及R2 SP1，与Windows 7 Gold与SP1版本中的远程桌面协议(RDP)实现中存在漏洞，该漏洞源于没有正确处理内存中的数据包。远程攻击者可通过发送特制RDP数据包触发访问（1）没有正确初始化或者（2）已被删除的对象，执行任意代码。也称&lsquo;Remote Desktop Protocol Vulnerability&rsquo;。  该漏洞已有修复补丁，如果未能及时安装补丁可采取以下临时修复措施：  方法一：禁用终端服务、远程桌面、远程协助和 Windows Small Business Server 2003 远程工作网站功能；  方法二：在企业周边防火墙中屏蔽TCP端口3389；  方法三：在运行 Windows Vista、Windows 7、Windows Server 2008 和 Windows Server 2008 R2 的受支持版本的系统上启用网络级别身份验证。', 'code_execution', 2014430, 'DOS_Microsoft_Remote_Desktop_Protocol_(RDP)_maxChannelIds_DoS_Attempt_Negative_INT', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 3389 (msg:\"ET DOS Microsoft Remote Desktop Protocol (RDP) maxChannelIds DoS Attempt Negative INT\"; flow:to_server,established; content:\"|03 00|\"; depth:2; content:\"|e0|\"; distance:3; within:1; content:\"|03 00|\"; distance:0; content:\"|f0|\"; distance:3; within:1; content:\"|7f 65|\"; distance:1; within:2; content:\"|04 01 01 04 01 01 01 01 ff 30|\"; distance:3; within:10; content:\"|02|\"; distance:1; within:1; byte_test:1,<,0x80,0,relative,big; byte_test:1,&,0x80,1,relative,big; reference:url,www.msdn.microsoft.com/en-us/library/cc240836.aspx; reference:cve,2012-0002; reference:url,technet.microsoft.com/en-us/security/bulletin/ms12-020; reference:url,stratsec.blogspot.com.au/2012/03/ms12-020-vulnerability-for-breakfast.html; reference:url,aluigi.org/adv/termdd_1-adv.txt; reference:url,blog.binaryninjas.org/?p=58; reference:url,luca.ntop.org/Teaching/Appunti/asn1.html; classtype:attempted-dos; sid:2014430; rev:13; metadata:created_at 2012_03_20, updated_at 2012_03_20;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (697, 2, 'Conficker/MS08-067 蠕虫外连流量', '2002-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Conficker，也被称作Downup，Downadup或Kido，Conficker蠕虫最早于2008年11月20日被发现的以微软的windows操作系统为攻击目标的计算机蠕虫病毒。迄今已出现了A、B、C、E四个版本，目前全球已有超过1500万台电脑受到感染。Conficker主要利用Windows操作系统MS08-067漏洞来传播，同时也能借助任何有USB接口的硬件设备来感染。', 'worm', 2008739, 'TROJAN_Conficker/MS08-067_Worm_Traffic_Outbound', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Conficker/MS08-067 Worm Traffic Outbound\"; flowbits:isset,ET.ms08067_header; flow:established,to_server; content:\"If-None-Match|3A| |22|60794|2D|12b3|2D|e4169440|22|\"; nocase; reference:url,doc.emergingthreats.net/bin/view/Main/2008739; classtype:trojan-activity; sid:2008739; rev:8; metadata:created_at 2010_07_30, updated_at 2010_07_30;)');
