INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (442, 2, 'SMB CoGetInstanceFromFile andx 溢出尝试', '2001-01-01', 1, 1, 'CVE-2003-0995', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Message Queue Manager (MSQM)存在缓冲区溢出漏洞。远程攻击者可以通过队列登记要求来导致服务拒绝（RPC服务崩溃）。', 'overflow', 2103180, 'NETBIOS_SMB_CoGetInstanceFromFile_andx_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB CoGetInstanceFromFile andx overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.msqueue; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\x73|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,!&,128,6,relative; content:\"%\"; depth:1; offset:39; byte_jump:2,0,little,relative; content:\"&|00|\"; within:2; distance:29; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 01|\"; within:2; distance:19; byte_test:4,>,128,20,relative; reference:cve,2003-0995; reference:url,www.eeye.com/html/Research/Advisories/AD20030910.html; reference:url,www.microsoft.com/technet/security/bulletin/MS03-026.mspx; classtype:attempted-admin; sid:2103180; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (954, 2, 'SMB IrotIsRunning unicode 尝试', '2007-01-01', 1, 1, 'CVE-2002-1561', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows 2000/XP是微软公司开发的WINDOWS操作系统。  Microsoft Windows 2000/XP的RPC服务存在漏洞，远程攻击者可以利用这个漏洞进行拒绝服务攻击。  漏洞存在于Windows系统的DCE-RPC堆栈实现中，远程攻击者可以连接TCP 135端口，发送畸形数据，可导致关闭RPC服务，关闭RPC服务可以引起系统停止对新的RPC请求进行响应，产生拒绝服务。由于众多服务都依赖于RPC服务， 这可能使系统变得不稳定， 很多正常操作无法进行. 例如， Word中将无法使用拷贝/粘贴功能. 根据系统安装的补丁情况， 可能导致Windows XP系统重新起动.', 'dos', 2103258, 'NETBIOS_SMB_IrotIsRunning_unicode_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB IrotIsRunning unicode attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.irot; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; within:5; distance:3; byte_test:1,&,128,6,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00 00 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; isdataat:4,relative; content:\"|05|\"; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 02|\"; within:2; distance:19; byte_jump:4,8,relative,little,align; byte_test:4,>,1024,0,little; reference:bugtraq,6005; reference:cve,2002-1561; reference:url,www.microsoft.com/technet/security/bulletin/MS03-010.mspx; classtype:protocol-command-decode; sid:2103258; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
