INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (443, 2, 'CODESYS 网关服务器无效的内存访问尝试', '2004-01-01', 1, 1, 'CVE-2012-4704', NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问CODESYS Gateway-Server。', 'CoDeSys是Windows平台下，独立于硬件的IEC 61131-3开发系统，用于编程和创建控制器应用。3S CODESYS Gateway-Server 2.3.9.27之前版本中存在数组索引漏洞。通过特制的数据包，远程攻击者利用该漏洞执行任意代码。', 'dos', 44151, 'CODESYS_Gateway-Server_invalid_memory_access_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET [1210,1211] ( msg:\"PROTOCOL-SCADA CODESYS Gateway-Server invalid memory access attempt\"; flow:to_server,established; content:\"|DD DD|AaBbCcDdEeFf\";depth:14; metadata:policy max-detect-ips drop,policy security-ips drop; reference:bugtraq,58032; reference:cve,2012-4704; reference:url,ics-cert.us-cert.gov/pdf/ICSA-13-050-01-a.pdf; classtype:attempted-admin; sid:44151; rev:3; )');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (188, 2, 'Havex病毒 CnC 服务器响应的HTML Tag 检测', '2007-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Havex是被编写来感染SCADA和工控系统中使用的工业控制软件，这种木马可能有能力禁用水电大坝、使核电站过载、甚至可以做到按一下键盘就能关闭一个国家的电网。<br>Havex被命名为W32/Havex.A，它是一种通用的远程访问木马，近来被用于从事工业间谍活动，主要攻击对象是欧洲的许多使用和开发工业应用程序和机械设备的公司。Havex存在超过88个变种，期通信用的C&C服务器多达146个，并且有1500多个IP地址向C&C服务器通信。Havex利用OPC标准来收集网络和联网设备的信息。<br>', 'worm', 2018244, 'Havex_RAT_CnC_Server_Response_HTML_Tag', 0, 'http $EXTERNAL_NET any -> $HOME_NET any (msg:\"ET TROJAN Havex RAT CnC Server Response HTML Tag\"; flow:established,from_server; file_data; content:\"|3c|mega http|2d|equiv|3d|\"; metadata: former_category MALWARE; reference:md5,6557d6518c3f6bcb8b1b2de77165c962; classtype:trojan-activity; sid:2018244; rev:2; metadata:created_at 2014_03_11, updated_at 2014_03_11;)');
