INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (956, 2, 'Sinapsi eSolar光伏系统管理器使用硬编码的账户登录', '2004-01-01', 1, 1, 'CVE-2012-5862', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Sinapsi eSolar Light Photovoltaic System Monitor。', 'Sinapsi eSolar Light是太阳能应用内使用的监控系统。基于Sinapsi eSolar Light Photovoltaic System Monitor (又名Schneider Electric Ezylog photovoltaic SCADA 管理服务器)，Sinapsi eSolar，Sinapsi eSolar DUO 固件2.0.2870_2.2.12之前版本中的login.php脚本中存在漏洞，该漏洞源于建立了多个硬编码账户。远程攻击者利用脚本中包含的(1)明文密码或(2)密码哈希值（如密码astridservice 或36e44c9b64），获得管理员身份访问权限。', 'info_leak', 2805584, 'Sinapsi_eSolar_Light_Photovoltaic_System_Monitor_Login_with_hard_coded_account', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET $HTTP_PORTS (msg:\"SCADA Sinapsi eSolar Light Photovoltaic System Monitor Login with hard coded account\"; flow:established,to_server; content:\"POST\"; http_method; content:\"/login.php\"; http_uri; content:\"password=36e44c9b64\"; http_client_body; reference:url,www.us-cert.gov/control_systems/pdf/ICS-ALERT-12-284-01.pdf; reference:url,exploit-db.com/exploits/21273/; reference:url,nakedsecurity.sophos.com/2012/10/12/hackers-exploit-code-photovoltaic-solar/; reference:url,thehackernews.com/2012/10/scada-hacking-exploit-released-to-hack.html; classtype:web-application-attack; sid:2805584; rev:2;)');
