INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (959, 2, 'SMB IrotIsRunning 尝试', '2007-01-01', 1, 1, 'CVE-2002-1561', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows 2000/XP是微软公司开发的WINDOWS操作系统。  Microsoft Windows 2000/XP的RPC服务存在漏洞，远程攻击者可以利用这个漏洞进行拒绝服务攻击。  漏洞存在于Windows系统的DCE-RPC堆栈实现中，远程攻击者可以连接TCP 135端口，发送畸形数据，可导致关闭RPC服务，关闭RPC服务可以引起系统停止对新的RPC请求进行响应，产生拒绝服务。由于众多服务都依赖于RPC服务， 这可能使系统变得不稳定， 很多正常操作无法进行. 例如， Word中将无法使用拷贝/粘贴功能. 根据系统安装的补丁情况， 可能导致Windows XP系统重新起动.', 'dos', 2103256, 'NETBIOS_SMB_IrotIsRunning_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB IrotIsRunning attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.irot; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; within:5; distance:3; byte_test:1,!&,128,6,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; isdataat:4,relative; content:\"|05|\"; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 02|\"; within:2; distance:19; byte_jump:4,8,relative,little,align; byte_test:4,>,1024,0,little; reference:bugtraq,6005; reference:cve,2002-1561; reference:url,www.microsoft.com/technet/security/bulletin/MS03-010.mspx; classtype:protocol-command-decode; sid:2103256; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (192, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) via DNS', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019403, 'CVE-2014-6271_exploit_attempt_via_malicious_DNS', 0, 'tcp $EXTERNAL_NET 53 -> $HOME_NET any (msg:\"ET EXPLOIT Possible CVE-2014-6271 exploit attempt via malicious DNS\"; byte_test:1,&,128,4; content:\"|28 29 20 7b|\"; fast_pattern:only; reference:cve,2014-6271; reference:url,packetstormsecurity.com/files/128650; classtype:attempted-admin; sid:2019403; rev:1; metadata:created_at 2014_10_15, updated_at 2019_10_07;)');
