INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (453, 2, 'HTTP SQL注入攻击尝试', '2005-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本事件检测到针对WEB服务器的SQL注入攻击，攻击者通过发送含有“UPDATE SET”的报文来尝试SQL注入。<br>SQL注入攻击指的是通过构建特殊的输入作为参数传入Web应用程序，而这些输入大都是SQL语法里的一些组合，通过执行SQL语句进而执行攻击者所要的操作，其主要原因是程序没有细致地过滤用户输入的数据，致使非法数据侵入系统。', 'sql_injection', 2006447, 'WEB_SERVER_Possible_SQL_Injection_Attempt_UPDATE_SET', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"ET WEB_SERVER Possible SQL Injection Attempt UPDATE SET\"; flow:established,to_server; content:\"UPDATE\"; nocase; http_uri; content:\"SET\"; nocase; distance:0; http_uri; pcre:\"/\\WUPDATE\\s+[A-Za-z0-9$_].*?\\WSET\\s+[A-Za-z0-9$_].*?\\x3d/Ui\"; reference:url,en.wikipedia.org/wiki/SQL_injection; reference:url,doc.emergingthreats.net/2006447; classtype:web-application-attack; sid:2006447; rev:13; metadata:affected_product Web_Server_Applications, attack_target Web_Server, deployment Datacenter, tag SQL_Injection, signature_severity Major, created_at 2010_07_30, updated_at 2019_09_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (965, 2, 'RealFlex RealWin SCADA On_FC_SCRIPT_FCS_STARTPROG 缓冲区溢出', '2004-01-01', 1, 1, 'CVE-2011-1563', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问RealFlex RealWin。', 'DATAC RealFlex RealWin 2.1（Build 6.1.10.10）及之前版本中的HMI应用程序中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向910端口发送的多个包中的超长用户名，执行任意代码。这些包有：（1）On_FC_CONNECT_FCS_LOGIN包，特制（2）On_FC_CTAGLIST_FCS_CADDTAG，（3）On_FC_CTAGLIST_FCS_CDELTAG，（4）On_FC_CTAGLIST_FCS_ADDTAGMS，（5）On_FC_RFUSER_FCS_LOGIN，（6）未明On_FC_BINFILE_FCS_*FILE，（7）On_FC_CGETTAG_FCS_GETTELEMETRY，（8）On_FC_CGETTAG_FCS_GETCHANNELTELEMETRY，（9）On_FC_CGETTAG_FCS_SETTELEMETRY，（10）On_FC_CGETTAG_FCS_SETCHANNELTELEMETRY，以及（11）On_FC_SCRIPT_FCS_STARTPROG包。', 'overflow', 1111655, 'RealFlex_RealWin_SCADA_On_FC_SCRIPT_FCS_STARTPROG_Buffer_Overflow', 0, 'tcp any any -> any 910 (msg:\"ETPRO SCADA RealFlex RealWin SCADA On_FC_SCRIPT_FCS_STARTPROG Buffer Overflow\"; flow:to_server,established; isdataat:1000; content:\"|10 23 54 67|\"; offset:0; byte_test:4,>,1000,0,relative,little; content:\"|09 00 12 00|\"; distance:6; within:4; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111655; rev:1;)');
