INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (966, 2, 'SMB DCERPC LSASS 直接绑定尝试', '2000-09-01', 1, 1, 'CVE-2003-0533', NULL, 0, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows LSA是本地安全授权服务(LSASRV.DLL)。  LSASS DCE/RPC末端导出的Microsoft活动目录服务存在一个缓冲区溢出，远程攻击者可以利用这个漏洞以SYSTEM权限在系统上执行任意指令。  一些活动目录服务在Windows目录中在debug子目录里生成调试日志文件，在LSASRV.DLL实现的记录函数写信息条目到日志文件中，这里的vsprintf()函数用于建立日志条目。由于对提供给这个函数的字符串参数缺少正确的边界缓冲区检查，超长字符串可导致缓冲区溢出。部分RPC函数接收此字符串作为参数并尝试写调试日志文件，利用这些RPC函数，可能以SYSTEM权限在系统上执行任意指令。', 'misc_activity', 2102524, 'NETBIOS_DCERPC_LSASS_direct_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 135 (msg:\"GPL NETBIOS DCERPC LSASS direct bind attempt\"; flow:to_server,established; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; depth:4; offset:4; nocase; content:\"|05|\"; content:\"|0B|\"; within:1; distance:1; content:\"j|28 19|9|0C B1 D0 11 9B A8 00 C0|O|D9|.|F5|\"; within:16; distance:29; flowbits:set,netbios.lsass.bind.attempt; flowbits:noalert; reference:bugtraq,10108; reference:cve,2003-0533; reference:url,www.microsoft.com/technet/security/bulletin/MS04-011.mspx; classtype:protocol-command-decode; sid:2102524; rev:8; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
