INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (457, 2, 'SMB CoGetInstanceFromFile 小端字节序andx 溢出尝试', '2001-01-01', 1, 1, 'CVE-2003-0995', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Message Queue Manager (MSQM)存在缓冲区溢出漏洞。远程攻击者可以通过队列登记要求来导致服务拒绝（RPC服务崩溃）。', 'overflow', 2103181, 'NETBIOS_SMB_CoGetInstanceFromFile_little_endian_andx_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB CoGetInstanceFromFile little endian andx overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.msqueue; content:\"|00|\"; depth:1; content:\"|FF|SMB\"; within:4; distance:3; pcre:\"/^(\\x75|\\x2d|\\x2f|\\x73|\\xa2|\\x2e|\\x24|\\x74)/sR\"; byte_test:1,!&,128,6,relative; content:\"%\"; depth:1; offset:39; byte_jump:2,0,little,relative; content:\"&|00|\"; within:2; distance:29; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|01 00|\"; within:2; distance:19; byte_test:4,>,128,20,relative; reference:cve,2003-0995; reference:url,www.eeye.com/html/Research/Advisories/AD20030910.html; reference:url,www.microsoft.com/technet/security/bulletin/MS03-026.mspx; classtype:attempted-admin; sid:2103181; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (713, 2, 'SMB-DS DCERPC ISystemActivator unicode 绑定尝试', '2004-01-01', 1, 1, 'CVE-2003-0813', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Remote Procedure Call (RPC)是Windows 操作系统使用的一种远程过程调用协议，RPC提供进程间交互通信机制，允许在某台计算机上运行程序的无缝地在远程系统上执行代码。协议本身源自开放软件基金会的RPC协议，Microsoft在其基础上增加了自己的一些扩展。  RPCSS模块中的激活类函数在处理部分激活消息请求时存在问题，远程攻击者可以利用这个漏洞以SYSTEM进程权限在系统上执行任意指令。  RPCSS模块包含的激活类函数设计用于处理进入的DCOM激活请求，因此实例可提交给请求代理端。通过初始化两个同步的激活请求，然后快速关闭连接，Window RPC实时库处理这特殊的消息时可触发竞争条件问题，这样svchost rpc服务进程堆中会引起小部分破坏，而导致崩溃，进行构建提交请求可能以进程权限在系统上执行任意指令。', 'misc_activity', 2102491, 'NETBIOS_SMB-DS_DCERPC_ISystemActivator_unicode_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS DCERPC ISystemActivator unicode bind attempt\"; flow:to_server,established; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; depth:5; offset:4; nocase; byte_test:2,&,1,5,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00 05 00 0B|\"; within:15; distance:4; byte_test:1,&,16,1,relative; content:\"|A0 01 00 00 00 00 00 00 C0 00 00 00 00 00 00|F\"; within:16; distance:29; flowbits:set,dce.isystemactivator.bind.call.attempt; reference:bugtraq,8811; reference:cve,2003-0813; reference:nessus,12206; reference:url,www.microsoft.com/technet/security/bulletin/MS04-011.mspx; classtype:protocol-command-decode; sid:2102491; rev:8; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (202, 2, 'Iconics Genesis SCADA 整数溢出', '2004-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Iconics Genesis。', 'ICONICS是一家专门从事可视化和自动化软件开发的公司。GENESIS32GENESIS32是一个基于Web的HMI和SCADA应用，它完全可扩展OPC、SNMP和BACnet。GenBroker是一个监听38080端口的Windows服务。GENESIS32 9.2在处理opccode 0x04b5型报文时存在整数溢出漏洞，成功的利用该漏洞可导致恶意代码执行。', 'overflow', 1111665, 'Iconics_Genesis_SCADA_Integer_Overflow_0x04b5', 0, 'tcp any any -> any 38080 (msg:\"ETPRO SCADA Iconics Genesis SCADA Integer Overflow 0x04b5\"; flow:to_server,established; content:\"|01 00 00 15 00 00 00 01 00 00 1F F4 01 00 00 00|\"; content:\"|B5 04|\"; distance:4; within:2; isdataat:1024; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111665; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (458, 2, '罗克韦尔RSLogix 拒绝服务尝试', '2002-01-01', 1, 1, 'CVE-2011-3489', NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Rockwell RSLogix。', 'Rockwell RSLogix 19及之前版本的RsvcHost.exe 2.30.0.23版本中的RnaUtility.dll中存在缓冲区溢出漏洞。远程攻击者可借助带有向TCP端口4446发送的超长字符串的特制rna数据包导致拒绝服务（崩溃），该数据包可触发一个memset零溢出或越界读取。', 'dos', 2803693, 'Rockwell_RSLogix_Denial_of_Service_Trigger_1', 0, 'tcp any any -> $HOME_NET 4446 (msg:\"SCADA Rockwell RSLogix Denial of Service Trigger 1\"; flow:to_server,established; content:\"rna|F2 FF FF FF FF|\"; reference:url,aluigi.altervista.org/adv/rslogix_1-adv.txt; classtype:denial-of-service; sid:2803693; rev:1;)');
