INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (203, 2, 'SpyEye 僵尸网络命令控制行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'botnet', 2011857, 'SpyEye_C&C_Check-in_URI', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN SpyEye C&C Check-in URI\"; flow:established,to_server; content:\"guid=\"; http_uri; content:\"ver=\"; http_uri; content:\"stat=\"; http_uri; fast_pattern; content:\"ie=\"; http_uri; content:\"os=\"; http_uri; pcre:\"/(\\?|&)guid=[^!&]+?\\!/U\"; metadata: former_category MALWARE; reference:url,www.symantec.com/connect/blogs/spyeye-bot-versus-zeus-bot; reference:url,krebsonsecurity.com/2010/10/spyeye-v-zeus-rivalry-ends-in-quiet-merger/; classtype:trojan-activity; sid:2011857; rev:6; metadata:created_at 2010_10_27, updated_at 2010_10_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (459, 2, 'Cutwail 僵尸网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Cutwail僵尸网络在2007年被发现，它被用来发送垃圾邮件和执行DoS攻击。2010年，Cutwail所感染的计算机被用来发送针对数百家网站的DoS攻击，这些网站包括美国中情局和联邦调查局。2012年初，经Trustwave确认，由恶意HTML附件发起的大规模垃圾邮件活动应归咎于Cutwail。', 'botnet', 2008152, 'Pakes/Cutwail/Kobcka_Checkin_URL', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Pakes/Cutwail/Kobcka Checkin URL\"; flow:established,to_server; content:\"/firstrun.php?product=\"; nocase; http_uri; content:\"&aff=\"; nocase; http_uri; content:\"&update=\"; nocase; http_uri; content:\"User-Agent|3a| Mozilla|0d 0a|\"; http_header; metadata: former_category MALWARE; reference:url,doc.emergingthreats.net/2008152; classtype:trojan-activity; sid:2008152; rev:6; metadata:created_at 2010_07_30, updated_at 2019_10_11;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (204, 2, 'SMB llsrconnect 溢出尝试', '2001-01-01', 1, 1, 'CVE-2005-0050', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'License Logging Service是一款设计用于帮助客户管理Microsoft服务产品的证书的工具。Microsoft License Logging Service在处理畸形请求时存在安全漏洞，远程攻击者可以利用这个漏洞以系统权限在系统上执行任意指令。License Logging Service在处理某个带有超长的参数畸形请求时存在缓冲区溢出漏洞，远程攻击者可能利用此漏洞在主机上执行任意指令，从而完全控制系统。', 'overflow', 2103114, 'NETBIOS_SMB_llsrconnect_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB llsrconnect overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.llsrpc; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; within:5; distance:3; byte_test:1,!&,128,6,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,!&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 00|\"; within:2; distance:19; byte_test:4,>,52,0,relative; reference:url,www.microsoft.com/technet/security/bulletin/ms05-010.mspx; classtype:attempted-admin; sid:2103114; rev:5; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (716, 2, 'ZeroAccess 僵尸网络udp流量检测', '2009-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'ZeroAccess，也称为max++和Sirefef，它是一款木马型的恶意软件，它感染微软的Windows操作系统。ZeroAccess主要设计向感染的计算机传递载荷，一旦ZeroAccess感染系统就会开始下载各种不同类型的恶意软件。它的最终目的是为了创造收入，通过广告点击欺诈和比特币挖掘。ZeroAccess是2012年的九大僵尸网络之一，其控制的电脑数量已经超过200万台。', 'botnet', 2015474, 'ZeroAccess_udp_traffic_detected', 0, 'udp $HOME_NET any -> $EXTERNAL_NET 53 (msg:\"ET TROJAN ZeroAccess udp traffic detected\"; content:\"|9e 98|\"; offset:6; depth:2; dsize:20; classtype:trojan-activity; sid:2015474; rev:2; metadata:created_at 2012_07_13, updated_at 2012_07_13;)');
