INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (206, 2, 'IGSS 8 ODBC服务器多个远程未初始化的指针释放导致的拒绝服务', '2004-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS 8 ODBC Server。', 'IGSS是用于监控工业流程的SCADA系统。7T Interactive Graphical SCADA System在处理畸形报文时存在远程内存破坏漏洞，远程攻击者可利用此漏洞以管理员权限执行执行任意代码，完全控制受影响系统，造成拒绝服务。', 'dos', 2803965, 'IGSS_8_ODBC_Server_Multiple_Remote_Uninitialized_Pointer_Free_DoS', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 20222 (msg:\"SCADA IGSS 8 ODBC Server Multiple Remote Uninitialized Pointer Free DoS\"; flow:established,to_server; flowbits:isset,8miles.IGSS.DoS; content:\"|16|\"; offset:5; depth:1; byte_jump:4,0; byte_test:4,>,0x7ffdefff,1,relative; reference:url,www.securiteam.com/exploits/5VP3H153PA.html; classtype:attempted-user; sid:2803965; rev:1;)');
