INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (974, 2, 'IGSS SCADA 系统目录遍历和下载', '2003-01-01', 1, 1, 'CVE-2011-1565', NULL, 0, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在目录遍历漏洞。远程攻击者可以借助向TCP端口12401发送的..\\序列，（1）读取（操作码0x3），（2）创建或编写（操作码0x2）任意文件。', 'info_leak', 1111620, 'IGSS_SCADA_System_Directory_Traversal_and_Download', 0, 'tcp any any -> any 12401 (msg:\"ETPRO SCADA IGSS SCADA System Directory Traversal and Download\"; flow:to_server,established; content:\"|01 00 34 12 0D|\"; offset:2; depth:5; content:\"|03|\"; distance:11; within:1; content:\"|2E 2E 5C 2E 2E 5C 2E 2E 5C 2E 2E 5C|\"; distance:0; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111620; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (207, 2, 'Iconics Genesis SCADA 整数溢出', '2004-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Iconics Genesis。', 'ICONICS是一家专门从事可视化和自动化软件开发的公司。GENESIS32GENESIS32是一个基于Web的HMI和SCADA应用，它完全可扩展OPC、SNMP和BACnet。GenBroker是一个监听38080端口的Windows服务。GENESIS32 9.2在处理opccode 0xfa4型报文时存在整数溢出漏洞，成功的利用该漏洞可导致恶意代码执行。', 'overflow', 1111668, 'Iconics_Genesis_SCADA_Integer_Overflow_0xfa4', 0, 'tcp any any -> any 38080 (msg:\"ETPRO SCADA Iconics Genesis SCADA Integer Overflow 0xfa4\"; flow:to_server,established; content:\"|01 00 00 15 00 00 00 01 00 00 1F F4 01 00 00 00|\"; content:\"|a4 0f|\"; distance:4; within:2; isdataat:1024; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111668; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (975, 2, 'Worm.Win32.Welchia 蠕虫的 PING 尝试', '2002-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Welchia蠕虫，全名W32.Welchia.Worm。它会利用DCOM RPC 的漏洞MS03-026、MS03-039 来攻击目标机的TCP 135端口；并利用WebDav的漏洞MS03-007 来攻击TCP 80端口。Welchia蠕虫试图清除冲击波蠕虫，它会试图下载并安装来自微软升级网站的补丁程序并重启计算机，这会导致网络速度减慢甚至瘫痪。<br>该病毒利用特殊的ICMP 的PING 来发现局域网中的其他存活主机，并尝试传播感染。', 'worm', 2100483, 'PING_CyberKit_2.2_Windows', 0, 'icmp $EXTERNAL_NET any -> $HOME_NET any (msg:\"GPL SCAN PING CyberKit 2.2 Windows\"; itype:8; content:\"|AA AA AA AA AA AA AA AA AA AA AA AA AA AA AA AA|\"; depth:32; reference:arachnids,154; classtype:misc-activity; sid:2100483; rev:6; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (208, 2, 'Microsoft Windows Data Access Components bidlab.dll 不安全的库加载尝试', '2004-01-01', 1, 1, 'CVE-2011-1975', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。  Microsoft Windows 7 Gold和SP1版本以及Windows Server 2008 R2和R2 SP1版本的Windows Data Access Components (Windows DAC) 6.0版本中的Data Access Tracing组件中存在不可信任搜索路径漏洞。本地用户可借助当前工作目录中的Trojan木马DLL获得特权。', 'code_execution', 2803412, 'Microsoft_Windows_Data_Access_Components_Insecure_Library_Loading_Attempt', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET $HTTP_PORTS (msg:\"WEB_CLIENT Microsoft Windows Data Access Components Insecure Library Loading Attempt\"; flow:to_server,established; content:\"GET\"; http_method; content:\"BIDLAB.DLL\"; nocase; http_uri; reference:cve,2011-1975; reference:url,www.microsoft.com/technet/security/Bulletin/MS11-059.mspx; classtype:attempted-user; sid:2803412; rev:6;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (720, 2, 'ZeroAccess 僵尸网络命令控制行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'ZeroAccess，也称为max++和Sirefef，它是一款木马型的恶意软件，它感染微软的Windows操作系统。ZeroAccess主要设计向感染的计算机传递载荷，一旦ZeroAccess感染系统就会开始下载各种不同类型的恶意软件。它的最终目的是为了创造收入，通过广告点击欺诈和比特币挖掘。ZeroAccess是2012年的九大僵尸网络之一，其控制的电脑数量已经超过200万台。', 'botnet', 2013686, 'ZeroAccess/Max++_Rootkit_C&C_Activity_2', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN ZeroAccess/Max++ Rootkit C&C Activity 2\"; flow:established,to_server; content:\".php?w=\"; http_uri; content:\"&fail=\"; http_uri; content:\"&i=\"; http_uri; pcre:\"/\\.php\\?w=\\d+&fail=\\d+&i=[0-9a-f]{32}$/U\"; metadata: former_category MALWARE; reference:url,resources.infosecinstitute.com/step-by-step-tutorial-on-reverse-engineering-malware-the-zeroaccessmaxsmiscer-crimeware-rootkit/; reference:url,www.symantec.com/security_response/writeup.jsp?docid=2011-071314-0410-99&tabid=2; reference:url,www.microsoft.com/security/portal/Threat/Encyclopedia/Entry.aspx?Name=TrojanDropper%3aWin32%2fSirefef.B; classtype:trojan-activity; sid:2013686; rev:2; metadata:created_at 2011_09_21, updated_at 2011_09_21;)');
