INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (209, 2, 'Microsoft Windows Media Player ‘winmm.dll’ MIDI文件解析远程代码执行漏洞', '2008-01-01', 1, 1, 'CVE-2012-0003', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows是微软发布的非常流行的操作系统。Windows Media Player是系统的多媒体播放组件。  WMP在处理畸形结构的MIDI数据时存在内存破坏漏洞。远程攻击者可利用该漏洞通过诱使用户访问恶意网页控制用户系统。', 'code_execution', 2014207, 'Likely_MS12-004_midiOutPlayNextPolyEvent_Heap_Overflow_Midi_Filename_Requested_baby.mid', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET WEB_CLIENT Likely MS12-004 midiOutPlayNextPolyEvent Heap Overflow Midi Filename Requested baby.mid\"; flow:established,to_server; content:\"/baby.mid\"; http_uri; reference:cve,2012-0003; classtype:trojan-activity; sid:2014207; rev:3; metadata:affected_product Web_Browsers, affected_product Web_Browser_Plugins, attack_target Client_Endpoint, deployment Perimeter, tag Web_Client_Attacks, signature_severity Major, created_at 2012_02_07, updated_at 2016_07_01;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (465, 2, 'TheMoon.linksys.router 蠕虫检测', '2005-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'TheMoon.linksys.router是一种蠕虫病毒，它利用Linksys的家庭和小型企业路由器的身份验证绕过漏洞进行攻击。该蠕虫影响E系列路由器，它影响的路由器的固件版本如下：E4200、E3200、E3000、E2500、E2100L、E2000、E1550、E1500、E1200、E1000、E900。', 'worm', 2018132, 'WORM_TheMoon.linksys.router_2', 0, 'http any any -> $HOME_NET 8080 (msg:\"ET WORM TheMoon.linksys.router 2\"; flow:to_server,established; content:\"POST\"; http_method; content:\"/tmUnblock.cgi\"; http_uri; reference:url,isc.sans.edu/forums/diary/Linksys+Worm+Captured/17630; reference:url,devttys0.com/2014/02/wrt120n-fprintf-stack-overflow/; classtype:trojan-activity; sid:2018132; rev:4; metadata:created_at 2014_02_13, updated_at 2014_02_13;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (210, 2, 'Microsoft Office PNG Exploit 插件检测脚本访问尝试', '2006-01-01', 1, 1, 'CVE-2013-1331', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须构建恶意文件，诱使用户解析。', 'Microsoft Office是美国微软（Microsoft）公司开发的办公软件套件。常用组件有Word、Excel、Access、Powerpoint、FrontPage等。Microsoft Office分析特制Office文件的方式中存在一个远程执行代码漏洞。成功利用此漏洞的攻击者可以完全控制受影响的系统。攻击者可随后安装程序；查看、更改或删除数据；或者创建拥有完全用户权限的新帐户。那些帐户被配置为拥有较少系统用户权限的用户比具有管理用户权限的用户受到的影响要小。基于Mac系统下的Microsoft Office 2003 SP3和Office 2011版本中存在漏洞。', 'code_execution', 2017007, 'CVE-2013-1331_Microsoft_Office_PNG_Exploit_plugin-detect_script_access', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET EXPLOIT CVE-2013-1331 Microsoft Office PNG Exploit plugin-detect script access\"; flow:established,from_client; content:\"/vw.php?i=\"; http_uri; fast_pattern:only; pcre:\"/\\/vw\\.php\\?i=[a-fA-F0-9]+?\\-[a-fA-F0-9]+?$/U\"; reference:url,blogs.technet.com/b/srd/archive/2013/06/11/ms13-051-get-out-of-my-office.aspx; classtype:attempted-user; sid:2017007; rev:6; metadata:created_at 2013_06_11, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (466, 2, 'SMB-DS DCERPC LSASS 绑定尝试', '2000-09-01', 1, 1, 'CVE-2003-0533', NULL, 0, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows LSA是本地安全授权服务(LSASRV.DLL)。  LSASS DCE/RPC末端导出的Microsoft活动目录服务存在一个缓冲区溢出，远程攻击者可以利用这个漏洞以SYSTEM权限在系统上执行任意指令。  一些活动目录服务在Windows目录中在debug子目录里生成调试日志文件，在LSASRV.DLL实现的记录函数写信息条目到日志文件中，这里的vsprintf()函数用于建立日志条目。由于对提供给这个函数的字符串参数缺少正确的边界缓冲区检查，超长字符串可导致缓冲区溢出。部分RPC函数接收此字符串作为参数并尝试写调试日志文件，利用这些RPC函数，可能以SYSTEM权限在系统上执行任意指令。', 'misc_activity', 2102512, 'NETBIOS_SMB-DS_DCERPC_LSASS_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS DCERPC LSASS bind attempt\"; flow:to_server,established; content:\"|FF|SMB%\"; depth:5; offset:4; nocase; content:\"&|00|\"; within:2; distance:56; content:\"|5C 00|P|00|I|00|P|00|E|00 5C 00|\"; within:12; distance:5; nocase; content:\"|05|\"; within:1; distance:2; content:\"|0B|\"; within:1; distance:1; byte_test:1,&,1,0,relative; content:\"j|28 19|9|0C B1 D0 11 9B A8 00 C0|O|D9|.|F5|\"; within:16; distance:29; flowbits:set,netbios.lsass.bind.attempt; flowbits:noalert; reference:bugtraq,10108; reference:cve,2003-0533; reference:url,www.microsoft.com/technet/security/bulletin/MS04-011.mspx; classtype:protocol-command-decode; sid:2102512; rev:8; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (722, 2, 'GNU Bash 环境变量远程命令执行漏洞(CVE-2014-6271) 攻击 SIP 代理', '2006-01-01', 1, 1, 'CVE-2014-6271', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'GNU Bash（Bourne again shell）是一个为GNU计划编写的Unix shell。<br>GNU Bash 4.3及之前版本在处理某些构造的环境变量时存在安全漏洞，向环境变量值内的函数定义后添加多余的字符串会触发此漏洞，攻击者可利用此漏洞改变或绕过环境限制，以执行shell命令。某些服务和应用允许未经身份验证的远程攻击者提供环境变量以利用此漏洞。此漏洞源于在调用bash shell之前可以用构造的值创建环境变量。这些变量可以包含代码，在shell被调用后会被立即执行。<br>此漏洞可能会影响到使用ForceCommand功能的OpenSSH sshd、使用mod_cgi或mod_cgid的Apache服务器、DHCP客户端、其他使用bash作为解释器的应用等。<br>目前认为使用mod_php/mod_python/mod_perl的Apache httpd不受此问题影响。', 'code_execution', 2019289, 'CVE-2014-6271_Attempt_Against_SIP_Proxy', 0, 'udp any any -> $HOME_NET [5060,5061] (msg:\"ET EXPLOIT Possible CVE-2014-6271 Attempt Against SIP Proxy\"; flow:to_server; content:\"|28 29 20 7b|\"; fast_pattern:only; reference:url,github.com/zaf/sipshock; classtype:attempted-admin; sid:2019289; rev:3; metadata:created_at 2014_09_26, updated_at 2019_10_07;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (211, 2, 'Microsoft Windows ANI文件解析远程缓冲区溢出漏洞(MS05-002)', '2003-01-01', 1, 1, 'CVE-2004-1049', NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Windows是Microsoft公司开发的视窗操作系统。<br>\nWindows处理动画光标文件时存在问题，远程攻击者可以利用这个漏洞构建恶意ANI文件，诱使用户处理，可能以进程权限执行任意指令。<br>\n问题存在于USER32.dll处理.ani文件时，ANI部分文件格式如下：<br>\nRIFF {(DWORD)Length_of_file}<br>\nACON<br>\nLIST {(DWORD)Length_of_list}<br>\nINFO<br>\nINAM {(DWORD)Length_of_title} {szTitle}<br>\nIART {(DWORD)Length_of_author} {szAuthor}<br>\nanih {(DWORD)Length_of_AnimationHeader} {AnimationHeaderBlock}<br>\n一般来说，AnimationHeaderBlock长度是36字节(0x00000024)，此漏洞就是在处理Length_of_AnimationHeader字段上，为了拷贝AnimationHeaderBlock的内容，这个值会作为长度参数传递给memcpy()，但是这个值没有正确的进行检查，超长的参数可导致覆盖返回地址，并以进程权限执行任意指令。<br>\n由于动画光标文件可提供给Internet Explorer使用，所以攻击者可以构建恶意ANI文件，诱使用户处理，可能以进程权限执行任意指令。', 'overflow', 2001668, 'Exploit_MS05-002_Malformed_.ANI_stack_overflow_attack', 0, 'tcp $EXTERNAL_NET $HTTP_PORTS -> $HOME_NET any (msg:\"ET EXPLOIT Exploit MS05-002 Malformed .ANI stack overflow attack\"; flow: to_client,established; content:\"RIFF\"; content:\"ACON\"; distance: 8; content:\"anih\"; distance: 160; byte_test:4,>,36,0,relative,little; reference:url,doc.emergingthreats.net/bin/view/Main/2001668; classtype:misc-attack; sid:2001668; rev:6; metadata:created_at 2010_07_30, updated_at 2010_07_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (723, 2, 'SMB DCERPC LSASS DsRolerUpgradeDownlevelServer 攻击尝试', '2000-09-01', 1, 1, 'CVE-2003-0533', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft Windows LSA是本地安全授权服务(LSASRV.DLL)。  LSASS DCE/RPC末端导出的Microsoft活动目录服务存在一个缓冲区溢出，远程攻击者可以利用这个漏洞以SYSTEM权限在系统上执行任意指令。  一些活动目录服务在Windows目录中在debug子目录里生成调试日志文件，在LSASRV.DLL实现的记录函数写信息条目到日志文件中，这里的vsprintf()函数用于建立日志条目。由于对提供给这个函数的字符串参数缺少正确的边界缓冲区检查，超长字符串可导致缓冲区溢出。部分RPC函数接收此字符串作为参数并尝试写调试日志文件，利用这些RPC函数，可能以SYSTEM权限在系统上执行任意指令。', 'overflow', 2102511, 'NETBIOS_SMB_DCERPC_LSASS_DsRolerUpgradeDownlevelServer_exploit_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 139 (msg:\"GPL NETBIOS SMB DCERPC LSASS DsRolerUpgradeDownlevelServer exploit attempt\"; flow:to_server,established; flowbits:isset,netbios.lsass.bind.attempt; content:\"|FF|SMB\"; depth:4; offset:4; nocase; content:\"|05|\"; distance:59; content:\"|00|\"; within:1; distance:1; content:\"|09 00|\"; within:2; distance:19; reference:bugtraq,10108; reference:cve,2003-0533; reference:url,www.microsoft.com/technet/security/bulletin/MS04-011.mspx; classtype:attempted-admin; sid:2102511; rev:10; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (979, 2, 'HTTP SQL注入攻击尝试', '2005-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '本事件检测到针对WEB服务器的SQL注入攻击，攻击者通过发送含有“INSERT INTO”的报文来尝试SQL注入。<br>SQL注入攻击指的是通过构建特殊的输入作为参数传入Web应用程序，而这些输入大都是SQL语法里的一些组合，通过执行SQL语句进而执行攻击者所要的操作，其主要原因是程序没有细致地过滤用户输入的数据，致使非法数据侵入系统。', 'sql_injection', 2006444, 'WEB_SERVER_Possible_SQL_Injection_Attempt_INSERT_INTO', 0, 'http $EXTERNAL_NET any -> $HTTP_SERVERS $HTTP_PORTS (msg:\"ET WEB_SERVER Possible SQL Injection Attempt INSERT INTO\"; flow:established,to_server; content:\"INSERT\"; http_uri; nocase; content:\"INTO\"; http_uri; nocase; pcre:\"/INSERT.+INTO/Ui\"; reference:url,en.wikipedia.org/wiki/SQL_injection; reference:url,doc.emergingthreats.net/2006444; classtype:web-application-attack; sid:2006444; rev:11; metadata:affected_product Web_Server_Applications, attack_target Web_Server, deployment Datacenter, tag SQL_Injection, signature_severity Major, created_at 2010_07_30, updated_at 2019_09_27;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (212, 2, 'Cutwail 僵尸网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Cutwail僵尸网络在2007年被发现，它被用来发送垃圾邮件和执行DoS攻击。2010年，Cutwail所感染的计算机被用来发送针对数百家网站的DoS攻击，这些网站包括美国中情局和联邦调查局。2012年初，经Trustwave确认，由恶意HTML附件发起的大规模垃圾邮件活动应归咎于Cutwail。', 'botnet', 2008358, 'Pakes/Cutwail/Kobcka_Checkin_Detected_High_Ports', 0, 'http $HOME_NET any -> $EXTERNAL_NET 1024: (msg:\"ET TROJAN Pakes/Cutwail/Kobcka Checkin Detected High Ports\"; flow:established,to_server; content:\"/?bot_id=0&mode=1\"; http_uri; fast_pattern:only; metadata: former_category MALWARE; reference:url,doc.emergingthreats.net/2008358; classtype:trojan-activity; sid:2008358; rev:7; metadata:created_at 2010_07_30, updated_at 2010_07_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (468, 2, 'CONTROL MICROSYSTEMS (事件 41) 配置文件更改', '2005-01-01', 1, 1, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 'Control Microsystems现在归属于施耐德电气公司。本事件报警Control Microsystems中的配置文件更改的行为。', 'misc_activity', 2801022, 'CONTROL_MICROSYSTEMS_(Event_41)_Config_File_Change', 0, 'tcp any any -> $HOME_NET 20000 (msg:\"SCADA CONTROL MICROSYSTEMS (Event 41)Config File Change\"; flow:established; content:\"|0146|\"; offset:28; depth:2; content:\"|14|\"; distance:31; within:1; classtype:misc-activity; sid:2801022; rev:2;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (724, 2, 'SMB-DS llsrconnect 小端字节序溢出尝试', '2001-01-01', 1, 1, 'CVE-2005-0050', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'License Logging Service是一款设计用于帮助客户管理Microsoft服务产品的证书的工具。Microsoft License Logging Service在处理畸形请求时存在安全漏洞，远程攻击者可以利用这个漏洞以系统权限在系统上执行任意指令。License Logging Service在处理某个带有超长的参数畸形请求时存在缓冲区溢出漏洞，远程攻击者可能利用此漏洞在主机上执行任意指令，从而完全控制系统。', 'overflow', 2103123, 'NETBIOS_SMB-DS_llsrconnect_little_endian_overflow_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS llsrconnect little endian overflow attempt\"; flow:established,to_server; flowbits:isset,smb.tree.bind.llsrpc; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; within:5; distance:3; byte_test:1,!&,128,6,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C|PIPE|5C 00|\"; distance:4; nocase; byte_jump:2,-17,relative,from_beginning,little; content:\"|05|\"; distance:4; within:1; byte_test:1,&,16,3,relative; content:\"|00|\"; within:1; distance:1; content:\"|00 00|\"; within:2; distance:19; byte_test:4,>,52,0,relative; reference:url,www.microsoft.com/technet/security/bulletin/ms05-010.mspx; classtype:attempted-admin; sid:2103123; rev:4; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (980, 2, 'Microsoft SQL Server 2000注册表表项权限设置不正确漏洞(MS02-034)', '2008-01-01', 1, 1, 'CVE-2002-0642', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft SQL Server 2000是微软公司开发和维护的一种商业SQL数据库系统。  SQL Server 2000的默认注册表表项权限设置存在漏洞，远程可以执行查询操作的用户可能利用此问题修改下次启动SQL服务进程的权限。  在默认情况下，指定SQL服务进程所运行帐号的注册表项的权限设置不正确，能在服务器上执行SQL查询的远程攻击者可以无需管理员权限通过SQL查询修改此注册表表项，使SQL服务在下次启动时以较高的权限执行。', 'code_execution', 2100689, 'NETBIOS_xp_reg*_registry_access', 0, 'tcp $EXTERNAL_NET any -> $SQL_SERVERS 139 (msg:\"GPL NETBIOS xp_reg* registry access\"; flow:to_server,established; content:\"x|00|p|00|_|00|r|00|e|00|g|00|\"; depth:32; offset:32; nocase; reference:bugtraq,5205; reference:cve,2002-0642; reference:nessus,10642; reference:url,www.microsoft.com/technet/security/bulletin/MS02-034; classtype:attempted-user; sid:2100689; rev:12; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (469, 2, 'Alureon/TDSS MBR Rootkit 网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Alureon（也称为TDSS）是一个木马和bootkit，它被用来设计窃取数据，通过截获被感染电脑的网络流量获取如下数据，比如银行的用户名、密码、信用卡资料和社会安全号码等。Alureon是2012年的九大僵尸网络之一。', 'botnet', 2011894, 'TDSS/TDL/Alureon_MBR_rootkit_Checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN TDSS/TDL/Alureon MBR rootkit Checkin\"; flow:established,to_server; urilen:16<>402; content:\"GET\"; nocase; http_method; content:\"Accept-Language|3a| \"; http_header; depth:17; content:!\"Accept|3a| \"; http_header; content:\"User-Agent|3a| Mozilla/4.0 |28|compatible|3b| MSIE\"; fast_pattern:23,18; http_header; content:\"Host|3a| \"; distance:0; http_header; content:\"|3a| no-cache\"; distance:0; http_header; pcre:\"/^\\/[a-z0-9+\\/=]{16,400}$/Ui\"; metadata: former_category MALWARE; classtype:trojan-activity; sid:2011894; rev:18; metadata:created_at 2010_11_05, updated_at 2010_11_05;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (725, 2, '西门子Tecnomatix FactoryLink vrn.exe opcode 10 缓冲区溢出', '2003-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问Siemens FactoryLink vrn.exe。', '西门子 Tecnomatix FactoryLink 8.0.1.1473版本存在多个漏洞，包括缓冲区溢出、内存破坏和信息泄露漏洞。vrn.exe是FactoryLink中运行的一个服务器软件，它监听7579端口。vrn.exe在解析opccode 10的text域时使用了一个特殊的函数。这个函数拷贝栈缓冲区中以分号或空格分隔的字符串，它存在一个经典的栈缓冲区溢出漏洞。', 'overflow', 2801908, 'Siemens_Tecnomatix_FactoryLink_vrn.exe_opcode_10_buffer_overflow', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 7579 (msg:\"SCADA Siemens Tecnomatix FactoryLink vrn.exe opcode 10 buffer overflow 2\"; flow:to_server,established; content:\"|ff 55 0a 00|\"; offset:4; depth:4; content:\"|01 00|\"; distance:8; within:2; content:\"|01 00 01 00|\"; distance:16; within:4; pcre:\"/^..[^\\s\\x3b]{512}/Rs\"; reference:bugtraq,46934; classtype:attempted-user; sid:2801908; rev:2;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (214, 2, 'IGSS SCADA RMS Report Template Add 命令行缓冲区溢出', '2003-01-01', 1, 1, 'CVE-2011-1567', NULL, 2, NULL, NULL, NULL, NULL, '攻击者必须访问IGSS。', 'IGSS是用于监控工业流程的SCADA系统。7-Technologies Interactive Graphical SCADA System（IGSS）的IGSSdataServer.exe 9.00.00.11063及之前版本中存在多个基于栈的缓冲区溢出漏洞。远程攻击者可以借助向TCP端口12401传递的多个命令导致拒绝服务，并可能执行任意代码。这些命令包括：0xd操作码中的（1）ListAll，（2）Write File，（3）ReadFile，（4）Delete，（5）RenameFile，及（6）FileInfo命令；RMS报告模板（0x7）操作码中的（7）the Add，（8）ReadFile，（9）Write File，（10）Rename，（11）Delete，（12）Add命令；（13）STDREP请求(0x8)操作码中的0x4命令。', 'overflow', 1111631, 'IGSS_SCADA_RMS_Report_Template_Add_Command_Buffer_Overflow', 0, 'tcp any any -> any 12401 (msg:\"ETPRO SCADA IGSS SCADA RMS Report Template Add Command Buffer Overflow\"; flow:to_server,established; byte_test:2,>,534,0,little; content:\"|01 00 34 12 07|\"; offset:2; depth:5; content:\"|04|\"; distance:11; within:1; classtype:attempted-user; reference:url,digitalbond.com/tools/quickdraw/vulnerability-rules; sid:1111631; rev:1;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (470, 2, 'OSX/Flashback.K 网络通信检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Flashback僵尸网络在2012年感染了近60万台Mac电脑。该木马通过利用Java的漏洞，进入Mac系统，所以安装有Java系统的Mac用户一旦访问了带有Flashback木马的网页就会被感染。Flashback僵尸网络目前的关注点是收集谷歌和Paypal等网站的账户密码，以便网络罪犯可以接管这些账户。<br>', 'botnet', 2014525, 'OSX/Flashback.K_first_execution_checkin', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN OSX/Flashback.K first execution checkin\"; flow:established,to_server; content:\"/stat_svc/\"; http_uri; pcre:\"/\\/stat_svc\\/$/U\"; metadata: former_category MALWARE; reference:url,f-secure.com/v-descs/trojan-downloader_osx_flashback_k.shtml; reference:url,vms.drweb.com/virus/?i=1816029; classtype:trojan-activity; sid:2014525; rev:4; metadata:created_at 2012_04_05, updated_at 2012_04_05;)');
