INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (750, 2, 'Alureon/TDSS User-Agent 检测', '2008-01-01', 1, 1, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Alureon（也称为TDSS）是一个木马和bootkit，它被用来设计窃取数据，通过截获被感染电脑的网络流量获取如下数据，比如银行的用户名、密码、信用卡资料和社会安全号码等。Alureon是2012年的九大僵尸网络之一。', 'botnet', 2012322, 'TDSS_User-Agent_CMD', 0, 'http $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Possible TDSS User-Agent CMD\"; flow:established,to_server; content:\" (compatible|3b| MSIE 1.0|3b| Windows NT|3b| \"; http_user_agent; fast_pattern:16,20; metadata: former_category USER_AGENTS; reference:url,www.kernelmode.info/forum/viewtopic.php?f=16&t=19; reference:url,www.securelist.com/en/analysis/204792180/TDL4_Top_Bot; classtype:trojan-activity; sid:2012322; rev:8; metadata:created_at 2011_02_21, updated_at 2017_10_30;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1006, 2, 'SMB-DS DCERPC Workstation 服务绑定尝试', '2006-01-01', 1, 1, 'CVE-2003-0812', NULL, 2, NULL, NULL, NULL, NULL, NULL, 'Microsoft DCE/RPC服务可以提供网络管理功能，这些功能提供管理用户帐户和网络资源管理的功能。部分网络管理功能在Windows目录下的debug子目录会生成调试日志文件。  Microsoft Workstation服务在处理日志记录时缺少充分的边界缓冲区检查，远程攻击者可以利用这个漏洞提供超长参数触发缓冲区溢出，以SYSTEM权限在系统上执行任意指令。  日志功能中使用vsprintf()在日志文件中生成字符串，日志文件名为NetSetup.LOG，其保存在Windows debug目录中。  这个记录函数有部分处理Workstation服务命令的函数调用，如NetValidateName， NetJoinDomain等，在这NetValidateName()中，computer name作为第二个参数最终记录在日志文件中。  如我们使用NetValidateName() API：\nNetValidateName(L\\\\\\\\192.168.0.100，AAAAAAAA，NULL，NULL，0);  那么我们可以在远程主机中产生如下记录条目：\n08/13 13:01:01 NetpValidateName: checking to see if &#039;&#039; is valid as type 0 name\n08/13 13:01:01 NetpValidateName: &#039;&#039; is not a valid NetBIOS \\\\AAAAAAAA name: 0x57\n如果我们指定超长字符串作为NetValidateName() API的第二个参数，如果调试文件可写就可以在特定主机上发生缓冲区溢出。  一般如果是NTFS文件系统，在Windows目录中的debug目录不允许所有人可写，这表示不能使用NULL会话来生成日志。WsImpersonateClient() API在打开日志文件前调用，如果连接客户端没有有效的权限来写日志文件，那么CreateFile()就会失败，vsprintf()就不会被执行，因此此漏洞在FAT32系统和%SYSTEMROOT%\\debug目录可写的情况下可被利用。  但是部分扩展RPC函数实现在Windows XP上在调用WsImpersonateClient()前打开日志文件，不过这些RPC函数没有提供文档化说明，不过可以观察在WKSSVC.DLL中的函数表观察到。这些扩展命令的RPC号开始于0x1B，如0x1B调用NetpManageComputers()，但在打开日志文件前不调用WsImpersonateClient()。  NetpManageComputers()的使用没有被公开化，但是我们可以在LMJoin.h中找到NetAddAlternateComputerName() API的原型定义，这个API从NETAPI32.DLL导出，这个API也一样没有文档化。我们可以执行这个RPC函数(0x1B)使用如下API产生包：\nNetAddAlternateComputerName(L\\\\\\\\192.168.0.200，long_unicode_string，NULL，NULL，0);  我们不需要特殊权限在远程主机上写第二个产生到日志文件中，如定义超长Unicode字符串作为第二个参数(AlternateName)，在第一个参数定义的远程系统就会由于缓冲区溢出而崩溃。Unicode字符串long_unicode_string会在日志记录函数调用前被转换为ASCII字符串。', 'overflow', 2102311, 'NETBIOS_SMB-DS_DCERPC_Workstation_Service_bind_attempt', 0, 'tcp $EXTERNAL_NET any -> $HOME_NET 445 (msg:\"GPL NETBIOS SMB-DS DCERPC Workstation Service bind attempt\"; flow:to_server,established; content:\"|00|\"; depth:1; content:\"|FF|SMB%\"; depth:5; offset:4; nocase; byte_test:2,^,1,5,relative; content:\"&|00|\"; within:2; distance:56; content:\"|5C|PIPE|5C 00 05 00 0B|\"; within:10; distance:4; byte_test:1,&,16,1,relative; content:\"|98 D0 FF|k|12 A1 10|6|98|3F|C3 F8|~4Z\"; within:16; distance:29; reference:bugtraq,9011; reference:cve,2003-0812; reference:url,www.microsoft.com/technet/security/bulletin/MS03-049.mspx; classtype:misc-attack; sid:2102311; rev:8; metadata:created_at 2010_09_23, updated_at 2010_09_23;)');
INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (239, 2, 'SpyEye 僵尸网络数据窃取行为检测', '2009-01-01', 1, 1, NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 'SpyEye僵尸网络的主要目的是盗取消费者的银行信息。它尤其卑鄙地是从肉鸡电脑上偷了钱，还能让消费者自以为钱还在他们的账户上。作为一款特洛伊木马，它可获取用户的在线账号登录证书，然后悄悄进行转账交易。截至2012年10月初，SpyEye的C&C服务器已有278台。', 'botnet', 2013525, 'Spyeye_Data_Exfiltration_4', 0, 'tcp $HOME_NET any -> $EXTERNAL_NET any (msg:\"ET TROJAN Spyeye Data Exfiltration 4\"; flow:established,to_server; content:\"|DD DD DD DD D5 D6 D6 D6 D6 D4 D4 D4 D4 DA DA DA DA|\"; offset:5; depth:17; classtype:trojan-activity; sid:2013525; rev:3; metadata:created_at 2011_09_01, updated_at 2011_09_01;)');
