INSERT INTO `firewall`.`security_blacklist_vulnerability`(`id`, `risk_level`, `name`, `publish_date`, `status`, `action`, `cve`, `vulnerability_source`, `severity`, `trigger_device`, `rule_source`, `event_handle`, `affected_firm`, `attack_requirement`, `description`, `type`, `sid`, `sign_name`, `priority`, `message`) VALUES (1021, 2, 'Sinapsi eSolar光伏系统管理器SQL注入攻击尝试', '2004-01-01', 1, 1, 'CVE-2012-5861', NULL, 1, NULL, NULL, NULL, NULL, '攻击者必须访问Ezylog photovoltaic management server。', 'Sinapsi eSolar Light是太阳能应用内使用的监控系统。Sinapsi eSolar Light Photovoltaic System Monitor (又名Schneider Electric Ezylog photovoltaic SCADA管理服务器)，Sinapsi eSolar，Sinapsi eSolar DUO 固件2.0.2870_2.2.12之前版本中存在多个SQL注入漏洞。远程攻击者利用该漏洞通过(1)primo操作中的‘inverterselect’参数传送到dettagliinverter.php脚本或(2)‘lingua’参数传送到changelanguagesession.php脚本，执行任意SQL命令。', 'sql_injection', 57511, 'Sinapsi_eSolar_Light_Photovoltaic_System_Monitor_SQL_Injection_1', 0, 'http $EXTERNAL_NET any -> $HOME_NET $HTTP_PORTS ( msg:\"SERVER-WEBAPP Sinapsi eSolar Light Photovoltaic System Monitor SQL injection attempt 1\"; flow:to_server,established; content:\"/scripts/dettagliinverter.php\";http_uri; fast_pattern;nocase; content:\"inverterselect=\";nocase; pcre:\"/[?&]inverterselect=[^&]*?([\\x27\\x22\\x3b\\x23\\x28]|\\x2f\\x2a|\\x2d\\x2d)/i\"; metadata:policy max-detect-ips drop,policy security-ips drop; reference:cve,2012-5861; classtype:web-application-attack; sid:57511; rev:1; )');
